style.css:
@-moz-document url-prefix("https://typetest.io/") {
body {
    font-size: 1.15rem;
    font-family: Consolas,Source Code Pro,monospace;
    color: #403d3d;
    background-color: #e3ddcc;
    margin: auto;
    width: 80%;
    max-width: 64rem;
    line-height: 1.5em;
    padding-bottom: 12rem;
}

:root {
    --blue-dark: #3187e9;
    --blue-main: #5ba7ff;
    --blue-light: #89c0ff;
    --grey-0: #121212;
    --grey-1: #1d1d1d;
    --grey-2: #2f2f2f;
    --grey-3: #707070;
    --grey-4: #424242;
    --danger: #f06565;
}

#title-main {
    display: flex;
    align-items: center;
    justify-content: center;
    margin: 1rem 0;
    font-size: 1.2em;
    font-style: italic;
    font-weight: 700;
    background-color: #e3ddcc;
    padding: .75rem 1.25rem;
    border-radius: 2px;
}

#text-container {
    user-select: none;
    background-color: #fdf6e3;
    line-height: 2.4rem;
    padding: 1.25rem 2rem;
    margin: 0;
}

#text-input {
    flex-grow: 1;
    margin-top: 1rem;
}

#wpm-container {
    padding: .5rem 0;
    justify-content: center;
    display: flex;
    align-items: center;
    margin-left: 1rem;
    text-align: center;
    width: 7rem;
    user-select: none;
    cursor: pointer;
}

.widget {
    margin: 0;
    margin-top: 1rem;
    background-color: #fdf6e3;
    padding: .5rem 1.25rem;
    font: inherit;
    color: #1a1a1a;
    border-radius: 2px;
}

#results {
    background-color: #cad8e861;
    padding: 1.5rem 0;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    border-radius: 2px;
    width: 18rem;
    margin: 3rem auto;
}

#results-list li:nth-child(odd) {
    background-color: #d1dfef6b;
}

#results-list li {
    margin: 0;
    padding: .5rem 1.25rem;
    display: flex;
    justify-content: space-between;
}

li {
    padding: 1rem 1.5rem;
    width: auto;
    margin: .75rem;
    border-radius: 2px;
    background-color: #fdf6e36b;
}

.test-word.current {
    overflow: visible;
    background-color: #cad8e8;
}

input {
    padding: .75rem 1rem;
    font: inherit;
    font-size: inherit;
    color: inherit;
    background-color: #fdf6e3;
    border: none;
    border-radius: 2px;
}

.correct {
    color: #4da537;
}




}
