dl {
	display: block;
	margin-block-start: 0em;
	margin-block-end: 0em;
}
.scon-box {
	margin-bottom: 10px;
	position: relative;
	background: none;
	padding-bottom: 10px;
	border-radius: 10px;
	-moz-box-shadow: 0 3px 3px 0 rgba(0, 0, 0, 0.2);
	-webkit-box-shadow: 0 3px 3px 0 rgba(0, 0, 0, 0.2);
	box-shadow: 0 3px 3px 0 rgba(0, 0, 0, 0.2);
}
.scon-box dl.box {
	padding-bottom: 10px;
	position: relative;
	background: none;
}
.scon-box dl.box dt {
	text-align: left;
	margin-bottom: 8px;
	padding-top: 10px;
	font-size: medium;
	font-weight: bold;
	position: relative;
	background: var(--main-bg-color);
	border-radius: 10px 10px 0 0;
}
.scon-box dl.box dt span {
	display: block;
	margin: 0;
	padding: 0 0 13px 8px;
	vertical-align: text-top;
	position: relative;
	background: none;
}
