	width: 260px;
}
.GridTitle {
	display: none;
}
.main-box {
	margin-bottom: 0px;
	position: relative;
	background: none;
	border-radius: 10px;
	-moz-box-shadow: 0 3px 3px 0 rgba(0, 0, 0, 0.2);
	-webkit-box-shadow: 0 3px 3px 0 rgba(0, 0, 0, 0.2);
	box-shadow: 0 3px 3px 0 rgba(0, 0, 0, 0.2);
	padding-bottom: 12px;
}
.main-box dl.box {
	padding-bottom: 10px;
	position: relative;
	background: none;
}
.main-box dl.box dt {
	text-align: left;
	margin-bottom: 7px;
	padding-top: 10px;
	font-size: medium;
	font-weight: bold;
	position: relative;
	background: var(--main-bg-color);
	border-radius: 10px 10px 0 0;
}
.main-box dl.box dt span {
	display: block;
