}
input {
	color: #555555;
	vertical-align: middle;
	background-color: #ffffff;
	border: 1px solid #cccccc;
	border-radius: 6px;
	-webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075);
	box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075);
	-webkit-transition: .3s;
	transition: .3s;
}
input:hover {
	border-color: var(--main-bg-color);
	outline: 0;
	-webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075), 0 0 10px var(--main-bg-color);
	box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075), 0 0 10px var(--main-bg-color);
	transform: scale(1.1, 1.1);
}
input:focus {
	border-color: var(--main-bg-color);
	outline: 0;
	-webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075), 0 0 10px var(--main-bg-color);
	box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075), 0 0 10px var(--main-bg-color);
	transform: scale(1, 1);
}
input:active {
	transition: .1s;
	transform: scale(1, 1);
	background-color: var(--main-bg-color);
}
