@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain("murobbs.muropaketti.com") {
  
/************************************************/
/*      XF Light theme for MuroBBS (2015)       */
/*      for Firefox & Chrome with Stylish       */
/* -------------------------------------------  */
/*     created by Lifeless - rmj@3rdw.net       */
/* -------------------------------------------  */
/*        version: 1.16 - 2014-12-27            */
/* -------------------------------------------  */
/*            No Lilies Were Harmed             */
/*       During the Making of This Style        */
/* -------------------------------------------  */
/*                nos univers                   */
/*              A Contre-Courant                */
/*              tout à l'envers                 */
/*             pas pour longtemps               */
/************************************************/
  
/************************************************/
/*DEFAULTS (DO NOT TOUCH, USE THE OPTIONS BELOW)*/  
html { background:#dde!important; }
.pageWidth { margin:0 0 0 0 !important; }
/*DEFAULTS (DO NOT TOUCH, USE THE OPTIONS BELOW)*/  
/************************************************/
  
  
/************************************************/
/*                                              */
/*                  OPTIONS                     */
/*                                              */
/************************************************/

/* --- TAUSTAVARI --------------------------------------------------- */
/* - Varit ilmoitetaan muodossa:                                      */
/*        #rgb (r=red g=green b=blue, hex arvot valilta 0-f)          */
/*        #rrggbb (kuten ylempi, mutta arvot valilta 00-ff)           */
/*        color (esim: white, brown, blue, gold, silver, tan)         */
/*        ESIM (puna-oranssi):   background:#ff9900                   */
/*        ESIM (musta):          background:black                     */
html { background:/*[[bgcolor]]*/!important; }

/* --- YLIMAARAISET TYHJAT REUNUKSET FOORUMIN YMPARILLE ------------- */
/* - numerot tarkoittaa reunoja jarjestyksessa: TOP RIGHT BOTTOM LEFT */
/* - asetus voi olla '%' tai 'em'.                                    */
/*        Sivuille % toimii paremmin                                  */ 
/*        Alas toimii paremmin 'em' (1em on fontin korkeus            */
/*        ESIM:       margin:0 10% 0em 10%                            */
/*        ESIM:       margin:0 5% 2% 15%                              */
html { margin:0 /*[[sidemargin]]*/% 0 /*[[sidemargin]]*/% !important; }
    
/************************************************/
/*                                              */
/*               OPTIONS ENDS                   */
/*                                              */
/************************************************/  
  
/* BASIC STUFF FOR BACKGROUND */  
body {
  background:transparent!important;
}
#content .pageContent {
  border:none!important;
  background:transparent!important;
}  
.pageWidth { margin:0!important; }
/* BASIC STUFF FOR BACKGROUND ENDS */  

  /*
.forum_view li.unread .main .title a:before {
  content:'* ';
  padding-left:2em!important;
}
.forum_view li:not(.unread) a, .forum_view li.unread a.username {
  margin-left:4em!important;
}*/
  
  
/* REMOVE SCROLLBAR */  
.off-canvas-wrapper {
  overflow-x:inherit!important;
  overflow-y:inherit!important;
}  

  

/* REMOVE SIDE BAR */
/*[[sidebar]]*/
/* REMOVE SIDE BAR ENDS */

  
  
/* FORUM NAVIGATION */  
/*[[forumnav]]*/
/* FORUM NAVIGATION ENDS */  
  

  
/* LOGO */
#header, #logoBlock {
  margin-top:0px!important;
  position:relative;
  top:-10px!important;
}
IMG[src="http://otavafiles.fi/plaza/uploads/images/murobbs_logo.png"] {
  opacity:0;
  width:240px!important;
  padding-left:20px!important;
}
#logo {
  background:url('http://www.3rdw.net/themes/MuroBBS/xf/light/logo.png') 25% 50% no-repeat;
}
/* LOGO ENDS */


#content {
  position:absolute;
  top:73px;
  background:transparent!important;
  width:100%!important;
}

#Notices {
  width:100px!important;
  height:27px;
  position:absolute;
  top:-80px;
  left:50%!important;
  opacity:0.8;
  overflow:hidden;
  border-radius:0 0 5px 5px;
/*  display:none!important; */
}
#Notices:hover {
  width:400px!important;
  height:100%;
}
.baseHtml.noticeContent {
  font-size:8pt!important;
}
.baseHtml.noticeContent:before {
  content:'ilmoitukset: \013';
}
  
  
  
/***********************/  
/*     MAIN MENU       */
/***********************/  
/*-- home link --*/  
.navTabs [href*="http://murobbs"] {
   display:none;
}
/*-- main links --*/  
.navTabs {
  width:100%!important;
  position:absolute;
  top:-32px;
  left:-15px;
  font-size: 10pt;
  background:transparent;
  border:none;
  z-index:50;
}  
.navTabs * {
  border:none!important;
  margin:0!important;
  padding:0!important;
}
.navTab {
  margin:5px!important;
}
.navTab:hover {
  background:rgba(255,255,255,0.5)!important;
  margin:0px!important;
  padding:5px!important;
}
.navTabs .navLink {
  height:10px!important;
  line-height:10px!important;
  padding-left:5px!important;
  padding-right:5px!important;
}
.navTabs .navLink:not([href*="http://murobbs"]) {
  border-left:0px solid #fff!important;
}
/*-- user name --*/  
.visitorTabs {
  position:absolute;
  top:-10px;
  right:0px;
  background:rgba(255,255,255,0.3)!important;
  height:20px;
  line-height:30px!important;
  border-radius:3px 3px;
  padding:3px!important;
}
.visitorTabs li.PopupContainerControl a {
  line-height:30px!important;
}
/*-- main menu murobbs link --*/  
.navLink.NoPopupGadget[rel="Menu"][href="#"] {
  display: none!important;
  content: "\f015" !important;  
}
/*-- quick links --*/  
.navTab.selected .tabLinks {
  width:100%!important;
  position:absolute!important;
  top:18px!important;
  z-index:10!important;
  border:0px solid #ccc!important;
  background:transparent!important;
}
.tabLinks.forumsTabLinks li {
  float:right!important;
  margin-left:6px!important;
  padding-left:6px!important;
}
.tabLinks.forumsTabLinks LI:first-child {
  border-left:none!important;
}
.tabLinks.forumsTabLinks li * {
  font-size:7pt!important;
}
.tabLinks {
  height:27px!important;
}
.tabLinks * {
  height:27px!important;
  line-height:27px!important;
  font-weight:normal!important;
}
/***********************/  
/*   MAIN MENU ENDS    */
/***********************/  
  
  
/* FORUM TITLE */
.titleBar {
  position:absolute!important;
  top:73px;
  padding-left:1em!important;
}
.titleBar h1 {
  font-size:10pt!important;
}
.titleBar #pageDescription {
  font-size:7pt!important;
  /* display:none; */
  width:100%!important;
  height:16px!important;
  overflow:hidden;
}
/* FORUM TITLE ENDS */
  
  
  
/* PAGE NAV */
.PageNav {
  margin-top:2em!important;
  position:relative;
  z-index:50;
}
/* PAGE NAV ENDS */

  

/* THREAD VIEW */
.listBlock.ikoni {
  border:none!important;
  background:#fff!important;
}
.discussionList.section.sectionMain {
  border:none!important;
}
li.discussionListItem {
  background:#fff!important;
}
.discussionList.section.sectionMain li {
  border-bottom:1px solid #ddd!important;
} 
div.listBlock.stats.pairsJustified {
  border:none!important;
  background:#f9f9f9!important;
}
/* sticky threads */
li.sticky {
  background:#fcfcd9!important;
}
li.sticky .ikoni {
  background:#fcfcd9!important;
}
li.sticky div.listBlock.stats.pairsJustified {
  border:none!important;
  background:#f9f9d3!important;
}
li.sticky div.stats .major, li.sticky div.stats .minor {
  margin:0!important;
}
/* topic title & poster info */
.titleText {
  padding-left:3px!important;
}
.titleText .secondRow {
  font-size:8pt;
  padding-left:0px;
}  
.sectionHeaders dd.stats {
}
.discussionListItem .stats {
  width:140px!important;
}
.discussionListItem .stats * {
  color:#000;
  font-size:8pt;
}
.discussionListItem .stats .minor dt {
  width:40px!important;
  height:1.5em;
  overflow:hidden;
}
.discussionListItem .stats .minor dt:before {
  content:'Luettu: ';
}
/* Move first unread button */
.main .titleText {
  overflow:visible!important;
}
a.unreadLink {
  position:absolute!important;
  left:-36px!important;
  top:3px!important;
  width:30px!important;
  height:30px!important;
  opacity:0;
}
/* THREAD VIEW ENDS */
  
  
  
/* MESSAGE VIEW */  
li .messageMeta.otsikko {
  background:#333!important;
  border:none!important;
  height: 40px!important;
  padding:0px!important;
}
li .messageMeta.otsikko * {
  padding:0px!important;
  margin:0px!important;
  border:none!important;
}
li .messageMeta.otsikko .postNumber {
  padding:5px!important;
  position:relative;
  z-index:60;
}
li .messageMeta.otsikko .DateTime {
  padding-top:5px!important;
  padding-left:25px!important;
  display:block;
  width:73px!important;
  height:40px!important;
  border:none!important;
  background:#333;
  position:relative;
  top:0px;
  z-index:10;
}
.messageUserInfo {
  position:relative;
  top:-40px;
  left:0px;
  height:40px!important;
  width:100%!important;
}
.quickReply  {
  background:transparent!important;
  margin-bottom:1em!important;
}
.quickReply #QuickReply {
  margin-left:0px!important;
  padding:10px!important;
}
.quickReply .messageUserInfo {
  display:none!important;
}
.quickReply .messageUserBlock  {
 display:none!important;
}
.messageUserInfo * {
  padding:0px!important;
  margin-left:49px;
}
.messageUserBlock {
  height:40px!important; 
}
.messageUserBlock .userText {
  position:relative;
  background:#285E78!important;
  height:40px!important;
  z-index:50!important;
  padding-right:5px!important;
}
.messageUserBlock .userText a {
  padding-left:5px!important;
  padding-top:3px!important;
  font-size:14pt!important;
  overflow:visible!important;
  color:#fff;
  z-index:50!important;
  margin-left:0px!important;
}
.messageUserBlock .userText a.username  {
  display:inline-block;
}
.messageUserBlock .userText a.username .style3 {
  margin:0px!important;
  color:#fff;
  font-weight:normal!important;
}
.messageUserBlock .userTitle {
  display:inline-block;
  margin:0px!important;
  color:#ff9;
}
.messageUserBlock .extraUserInfo {
  position:relative!important;
  top:-20px!important;
  height:20px!important;
  font-size:7pt!important;
  color:#fff!important;
  background:transparent!important;
  padding-top:1px!important;
  padding-left:1px!important;
  z-index:50;
}
.messageUserBlock .extraUserInfo dl {
  display:inline-block;
  padding-top:0px!important;
  padding-left:5px!important;
}
.messageUserBlock .extraUserInfo dl dt {
/*display:none!important;*/
  display:inline-block;
  color:#fff!important;
}
.messageUserBlock .extraUserInfo dl:first-child dt {
  display:none!important;
}
.messageUserBlock .extraUserInfo dl:first-child dd {
  margin-left:0px!important;
}
.messageUserBlock .extraUserInfo dl:first-child dd:before {
  content:'Rek. ';
}
.messageUserBlock .extraUserInfo dl dd {
  display:inline-block;
}
.messageUserBlock .extraUserInfo dl:last-child {
  position:relative;
  left:-20px!important;
}
.messageUserBlock .extraUserInfo dl:last-child dd, .messageUserBlock .extraUserInfo dl:last-child dd a {
  margin-left:0px!important;
}
li.message .item.edit{
  position:absolute;
  right:170px!important;
}
li.sectionMain {
  border:1px solid #285E78!important;
  background:#ddd!important;
  margin-bottom:5px!important;
}
li.message .newIndicator {
/*  display:none!important;*/
  font-size:8pt;
  background:none!important;
  padding:0px!important;
  margin:7px!important;
  position:absolute;
  left:0px!important;
  top:-42px!important;
  z-index:99!important;
}
.messageList .message {
  background:transparent!important;
}
li .primaryContent {
  border-left:none!important;
  margin-left:0px!important;
  margin-top:0px;
  position:relative;
  z-index:10;
}
li .primaryContent .messageContent {
  min-height:0px!important;
  position:relative;
  margin-top:0px!important;
  padding-top:0px!important;
  top:-15px!important;
}
.messageInfo.primaryContent {
  padding-left:0px!important;
  padding-top:1px!important;
  margin-top:0px!important;
}
.signature.messageText {
  opacity:1;
}
.signature.messageText * {
  font-size:7pt!important;
  font-weight:normal!important;
  color: #aaa!important;
}
.signature.messageText a {
  text-decoration:underline!important;
}
.bbCodeQuote, .bbCodeQuote * {
  background:transparent!important;
}
.quoteContainer, .quoteContainer .quoteContainer .quoteContainer, .quoteContainer .quoteContainer .quoteContainer .quoteContainer .quoteContainer {
  border:none!important;
  background:rgba(128,128,128,0.1)!important;
}
.quoteContainer .quoteContainer, .quoteContainer .quoteContainer .quoteContainer .quoteContainer, .quoteContainer .quoteContainer .quoteContainer .quoteContainer .quoteContainer .quoteContainer {
  border:none!important;
  background:rgba(128,128,128,0.2)!important;
}
.bbCodeBlock.bbCodeQuote .attribution.type {
  width:60px!important;
  background:rgba(128,128,128,0.1)!important;
  border:none!important;
  padding-left:5px;
}
.messageMeta.ToggleTriggerAnchor {
  background:#ddd!important;
  border-top:1px solid #ccc;
}
/* Images in messages */
.bbCodeImage.LbImage {
  qborder:1px dashed #999 ;
  box-shadow:0 0 3px;
}
/* fix bottom bar */
.messageMeta.ToggleTriggerAnchor {
  min-height:30px!important;
}
/* MESSAGE VIEW ENDS */  

  
  
/* QUICK JUMP ENDS */
.xenOverlay {
  left:30%!important;
}
.jumpMenuColumn:nth-of-type(1) {
}
.jumpMenuColumn .primaryContent {
  display:none!important;
}
.jumpMenuColumn .secondaryContent li a {
  padding:2px!important;
  font-size:7pt!important;
}    
/* REMOVE kaupallinen alue */
.jumpMenuColumn li.d0:nth-of-type(8), .jumpMenuColumn li.d0:nth-of-type(8) + li, .jumpMenuColumn li.d0:nth-of-type(8) + li + li, 
.jumpMenuColumn li.d0:nth-of-type(8) + li + li + li, .jumpMenuColumn li.d0:nth-of-type(8) + li + li + li + li, 
.jumpMenuColumn li.d0:nth-of-type(8) + li + li + li + li + li, .jumpMenuColumn li.d0:nth-of-type(8) + li + li + li + li + li + li {
  display:none;
}
.jumpMenuColumn li.d0 {
  border-bottom:1px solid #ddd;
}
/* QUICK JUMP ENDS */ 
  
    
  
/* ICONS */  
li.unread .miniMe{height: 24px!important; padding-left: 24px !important; background-image:url("http://www.3rdw.net/themes/MuroBBS/xf/light/icons24/thread_new_me.png") !important; width: 0 !important; background-repeat: no-repeat;}
li:not(.unread) .miniMe{height: 24px!important; padding-left: 24px !important; background-image:url("http://www.3rdw.net/themes/MuroBBS/xf/light/icons24/thread_old_me.png") !important; width: 0 !important; background-repeat: no-repeat;}
li.unread IMG[src*="xenforo/icons_vaalea/thread_lock_new.png"]{height: 24px!important; padding-left: 24px !important; background-image:url("http://www.3rdw.net/themes/MuroBBS/xf/light/icons24/thread_new_lock.png") !important; width: 0 !important; background-repeat: no-repeat;}  
li:not(.unread) IMG[src*="xenforo/icons_vaalea/thread_lock_new.png"]{height: 24px!important; padding-left: 24px !important; background-image:url("http://www.3rdw.net/themes/MuroBBS/xf/light/icons24/thread_old_lock.png") !important; width: 0 !important; background-repeat: no-repeat;}  
IMG[src*="xenforo/icons_vaalea/thread.png"]{height: 24px!important; padding-left: 24px !important; background-image:url("http://www.3rdw.net/themes/MuroBBS/xf/light/icons24/thread_old.png") !important; width: 0 !important; background-repeat: no-repeat;}  
IMG[src*="xenforo/icons_vaalea/thread_new.png"]{height: 24px!important; padding-left: 24px !important; background-image:url("http://www.3rdw.net/themes/MuroBBS/xf/light/icons24/thread_new.png") !important; width: 0 !important; background-repeat: no-repeat;}  
  
a:after {
/* content: "(" attr(href) ")" !important;*/
  }

.inner-wrapper footer {
  display:none!important;
}

/* REMOVE STUFF */
.footer-om {
  display:none;
}

/* END OF STYLE */
}

