style.css:
@-moz-document domain("www.gyakorikerdesek.hu")
{

  
  
body
  {
    background: #2a2a2a !important;
    font-family: 'Droid Sans', sans-serif !important;
    ;
  }

  h1,h2,h3,h4,h5,h6,h1>span,.body:before, #firstHeading
  {
    font-family: 'Droid Sans', serif !important;
    color: #ffffff !important;
    background-color: transparent !important;
    border: none !important;
    border-bottom: 1px solid #ccc !important;
  }

  *
  {
    color: white !important;
  }

  a:link
  {
    color: white !important;
  }

  a:visited
  {
    color: white !important;
  }

  a:hover
  {
    color: white !important;
  }

  a:active
  {
    color: white !important;
  }



.nepszeru_temak {
    background-color: #2a2a2a;
    border: solid #ed4216 1px;
    box-shadow: 2px 4px 6px black;
}


.gykinfo {
    background-color: #2a2a2a;
    border: solid #ed4216 1px;
    box-shadow: 2px 4px 6px black;
}





.menu1 {
    margin-bottom: 20px;
    padding: 15px 20px;
    line-height: 0;
    background: linear-gradient(#394076, #ed4216);
    box-shadow: 2px 4px 6px black;
}






.menu1 a:hover {
    background-color: transparent;
    opacity: .5;
    box-shadow: none;
}

.menu1 > div:first-child {
    position: relative;
    z-index: 1;
    margin-right: 20px;
}

.menu1 > div:first-child img {
    display: block;
    height: 44px;
    margin: 0 !important;
    opacity: 0;
    /*border-radius: 8px;
    box-shadow: 0 4px 20px 0 rgba(0, 0, 0, .2);*/
}

.menu1 > div:first-child:after {
    position: absolute;
    display: block;
    content: "";
    z-index: -1;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background: url("https://i.imgur.com/DD4xkJw.png") center center no-repeat;
    background-size: contain;
}




.menu1 input,
.menu1 select {
    background: linear-gradient(#7b4052, #aa4139);
    border: solid #074b94 1px;

}







.kismenu {
    border: solid black 1px;
    background: linear-gradient(#ed4216, #394076);
    box-shadow: 2px 4px 6px black;
}

.kismenu .flex:hover {
    background: transparent;
    box-shadow: none;
}




.tovabbi a,
button {
    display: block;
    margin: 3px 0 3px 0;
    padding: 10px 20px;
	text-align: center;
    font-weight: 600;
    background: linear-gradient(#394076, #ed4216);
    box-shadow: 2px 4px 6px black;
    border: solid #2a2a2a 1px;

}









input:not([type="radio"]), input:not([type="checkbox"]), select, textarea, .editor 

textarea {
    background: linear-gradient(#7b4052, #aa4139);
    border: solid #074b94 1px;

}






.figyelmeztetes {
    padding: 10px;
    background: black;
    box-shadow: 3px 3px 3px black;
}








.menu2
  {
    background-color: #ed4216;
    box-shadow: 2px 4px 6px black;

  }




.menu_kivalasztott {
    background: linear-gradient(to right, #394076, #ed4216);
    padding-left: 10px;
}




.kerdes_fejlec {
    background: linear-gradient(to left, #394076, #ed4216);
}


.kerdes
  {
    background-color: #444444;
    box-shadow: 2px 4px 6px black;
  }

.kerdes_statusz
  {
    border-top: solid black 1px;
    background-color: #444444;
  }



.szavazas
  {
    background: #444444;
	box-shadow: 2px 4px 6px black;
  }



.szavazas .sz {
    background-color: #333333;
    box-shadow: 2px 4px 6px black;

}

.szavazas .sz:hover {
    background-color: #ed4216;
}



.szavazas .sz .sz2 {
    background: #444444;

}

.szavazas .sz .sz2 span {
    background: #394076;
    background: white;
}




.valasz_fejlec
  {
    background: linear-gradient(to right, #394076, #ed4216);
  }


.valasz
  {
    background-color: #444444;
    box-shadow: 2px 4px 6px black;
    border: solid black 1px;
    margin: 0 0 20px 0;
    flex: 1;
  }

.valasz_statusz
  {
    background-color: #444444;
    font-size: 80%;
    width: 100%;
    border-top: solid black 1px;
    padding: 5px 0 5px 0;
    margin: 5px 0 0 0;
    align-items: center;
  }

.editor {
    line-height: 25px;
    color: black !important;
    background-color: #444444;
    color: #FF0000;
    box-shadow: 1px 3px 3px #aaaaaa;
    padding: 5px;
    margin: 10px auto 20px auto;
}


.editor TEXTAREA {
    display: block;
    width: 90%;
    height: 120px;
    font-size: 120%;
    color: ffffff !important;
    background-color: #444444 !important;

}





.kerdeslista
  {
    background-color: #394076;
    background: linear-gradient(to right, #394076, #394076);
    text-align: left;
    border: solid black 1px;
    border-right: solid black 2px;
    border-bottom: solid black 2px;
    margin: 3px 0 3px 0;
    padding: 3px 0 3px 0;
    align-items: center;
    min-height: 40px;
  }


.kerdeslista:hover {
    background: #ed4216;

}


.kerdeslista_valasz {
    background-color: #ed4216;
}





.uzenetlista {
    background: #394076 ;
    background: linear-gradient(#394076 70%, #2a2a2a);
    word-break: break-word;
    box-shadow: 1px 3px 3px black;
    border: solid black 1px;
    min-height: 35px;
    align-items: center;
    margin: 10px 0 10px 0;
}

.uzenetlista:hover {
    background: #ed4216;
    background: linear-gradient(#7b4052 60%, #aa4139);
}


.uzenet {
    margin: 10px;
    background-color: #394076;
    box-shadow: 1px 1px 1px black;
}












.oldalszamok A, .oldalszamok SPAN
  {
    display: inline-block;
    border: solid black 1px;
    padding: 3px;
    background-color: #2a2a2a;
    border-radius: 5px;
  }



.oldalszamok > a:hover,
.oldalszamok a:hover:not(.active){
    text-decoration: none;
    background-color: #444444;
}

.oldalszamok > span {
    background-color: #394076 !important;
    box-shadow: 3px 3px 3px black;
}



#ujkerdes_menu span {
    height: auto;
    padding: 10px;
    line-height: inherit;
    background: #444444;
    box-shadow: none;
}

#ujkerdes_menu span[style] {
    color: #124bca;
    background: linear-gradient(to right, #394076, #ed4216) !important;
}

.ujkerdes {
    background: linear-gradient(to right, #394076, #ed4216);
    box-shadow: 3px 3px 3px black;
}

  
  }
