style.css:
@-moz-document domain("www.animalcrossing-online.com") {
body {background-color:#111; color:#fff;}
#top{width:100%;float:left;background: #0f0f0f;}
.button{border: 1px solid #11a249;border-radius: 3px;background: #222;padding: 8px 10px;color: #11a249;font-size: 13px;font-family: 'Roboto', sans-serif;float: left;margin: 0 5px 10px 0;cursor: pointer;height: 35px;box-sizing: border-box;}
.quicksearch{border-left: 1px solid #11a249;border-radius: 3px 0 0 3px;background: #222;padding: 0 0 0 10px;color: #11a249;font-size: 13px;font-family: 'Roboto', sans-serif;float: right;margin: 0 0 10px 5px;cursor: text;width: 165px;border-top: 1px solid #11a249;border-bottom: 1px solid #11a249;border-right: 0;box-sizing: border-box;height: 35px;}
h2{float: left;font-size: 24px;color: #eee;font-weight: 900;}
h3{float: left; font-size: 24px; color: #eee; font-weight: 400;}
.ariane span{float: left;font-size: 24px;color: #eee;font-weight: 900;margin: 0 10px;}
.affichage-mosaique .mosaique .fond{background-size:cover !important;width:100%;text-align:center;margin-bottom: 3px;background-position: center !important;background:#dae4de;border-radius: 5px 5px 0px 0px;}

}
