style.css:
@-moz-document url-prefix("https://horriblesubs.info/") {
:root {
        /*
         *
         * CHANGE THE PRIMARY LINK, HIGHLIGHT BACKGROUND AND HEADER COLORS FOR THE PREDOMINANT COLOR OF YOUR WAIFU PIC,
         * SO YOU HAVE A CONSISTENTLY COLORED EXPERIENCE ACROSS THE ENTIRE HS WEBSITE
         * USE A COLORPICKER EXTENSION TO CHOOSE THE DESIRED COLOR FROM THE IMAGE, THEN COPY AND PASTE THE COLOR HEX NUMBER (E.G. #123456) BELOW
         * THEN USE THE STYLUS OR OTHER SIMILAR EXTENSION TO APPLY THE NEW STYLES
         *
        */
        --primary-link-color: 	#747eb7;
        --primary-background-color: #282828;
        --primary-text-color: #E0E0E0;
        --secondary-link-color: #E0E0E0;
        --secondary-background-color: #383838;
        --tertiary-background-color: #080808;
        --quaternary-background-color: #424242;
        --quinary-background-color: #484848;
        --highlight-background-color: 	#747eb7;
        --header-color: 	#747eb7;
        --box-shadow-color: #181818;
    }

    body {
        background-color: var(--primary-background-color) !important;
        color: var(--primary-text-color) !important;
    }

        body.home,
        body.page-template {
            /*
             *
             * REPLACE YOUR WAIFU'S URL HERE
             *
            */
            background-image: url(https://i.imgur.com/KFlrgp3.png) !important;
            background-size: 22%!important;
            background-repeat: no-repeat;
            background-position: right bottom;
            background-attachment: fixed;
        }

img.alignnone.size-large.wp-image-135617 {
  content: url(https://i.imgur.com/co8uuEi.png) !important;
} 
    a {
        color: var(--secondary-link-color);
    }

        a:hover,
        a:focus {
            color: var(--primary-link-color);
        }

    h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6 {
        color: var(--header-color) !important;
    }

    table, tr, td {
        border: 1px solid transparent !important;
    }

    .navbar.navbar-default {
        background-color: var(--tertiary-background-color);
        box-shadow: 1px 1px 4px var(--box-shadow-color);
    }

    .navbar-brand {
        background-image: none !important;
        font-size: 0px !important;
    }

        .navbar-brand::before {
            content: "FeelingAliveSubs";
            background-image: -webkit-gradient(linear, left top, right top, color-stop(0, #f22), color-stop(0.15, #f2f), color-stop(0.3, #22f), color-stop(0.45, #2ff), color-stop(0.6, #2f2),color-stop(0.75, #2f2), color-stop(0.9, #ff2), color-stop(1, #f22)) !important;
            background-clip: border-box;
            -webkit-background-clip: text;
            -moz-background-clip: text;
            -webkit-text-fill-color: transparent;
            -moz-text-fill-color: transparent;
            font-size: 30px !important;
        }

    .navbar-default .navbar-nav > li > a,
    .navbar-nav > li:hover > .caret {
        color: var(--secondary-link-color);
    }

    .dropdown-menu > li > a {
        color: var(--secondary-link-color);
        padding: 0;
    }

    .dropdown-menu {
        background-color: var(--tertiary-background-color);
    }

        .navbar-default .navbar-nav .dropdown-menu > li:hover,
        .navbar-default .navbar-nav .dropdown-menu > li:focus,
        .dropdown-menu > .active {
            background-color: var(--quaternary-background-color);
        }

            .dropdown-menu > .active > a,
            .dropdown-menu > .active > a:focus,
            .dropdown-menu > .active > a:hover,
            .dropdown-menu > .active > .caret,
            .dropdown-menu > li > a:focus,
            .dropdown-menu > li > a:hover,
            .dropdown-menu > li:hover > a,
            .dropdown-menu > li:hover > .caret {
                color: var(--primary-link-color);
            }

    .navbar-default .navbar-nav > .active > a,
    .navbar-default .navbar-nav > .active > .caret,
    .navbar-default .navbar-nav > .active > a:hover,
    .navbar-default .navbar-nav > .active > a:focus,
    .navbar-default .navbar-nav > li > a:hover,
    .navbar-default .navbar-nav > li:hover > a,
    .navbar-default .navbar-nav > li > a:focus,
    .navbar-default .navbar-nav > .open > a,
    .navbar-default .navbar-nav > .open > a:hover,
    .navbar-default .navbar-nav > .open > a:focus {
        color: var(--primary-link-color);
    }

    .alert-success {
        color: var(--primary-text-color);
        background-color: var(--primary-background-color);
        border-color: var(--box-shadow-color);
    }

    .post-inner-content {
        background-color: var(--secondary-background-color);
        box-shadow: 1px 1px 4px var(--box-shadow-color);
    }

        .post-inner-content .la-badge {
            background-color: var(--highlight-background-color);
        }

    .series-image img {
        box-shadow: 1px 1px 4px var(--box-shadow-color);
    }

    .featured > .row > div .featured-item .featured-image {
        box-shadow: 1px 1px 4px var(--box-shadow-color);
    }

    .well {
        background-color: var(--secondary-background-color);
        box-shadow: 1px 1px 4px var(--box-shadow-color);
    }

    .latest-spons img,
    .latest-sponsor img {
        width: 100%;
    }

    .latest-releases {
        background-color: var(--secondary-background-color);
    }

        .latest-releases .active {
            background-color: var(--quaternary-background-color);
        }

            .latest-releases a:hover,
            .latest-releases .active a:hover,
            .latest-show-more a:hover {
                background-color: var(--quinary-background-color) !important;
                color: var(--primary-link-color);
            }

    .latest-releases-res .badge {
        background-color: var(--main-background-color);
    }

    .latest-releases-res:hover .badge:hover,
    .rls-label:hover .badge:hover {
        background-color: var(--tertiary-background-color);
    }

    .latest-releases a:hover .badge,
    .rls-label:hover .badge {
        background-color: var(--secondary-background-color);
    }

    .latest-releases a,
    .latest-show-more a,
    .schedule-page-show a,
    .schedule-table,
    .full-schedule a {
        color: var(--secondary-link-color);
    }

        .schedule-table tr:hover td {
            background-color: var(--secondary-background-color);
            color: var(--primary-link-color);
        }

    .table-hover > tbody > tr:hover {
        background-color: var(--quaternary-background-color) !important;
    }

        .table-hover > tbody > tr:hover a,
        .table-hover > tbody > tr:hover .schedule-time,
        .full-schedule:hover a {
            color: var(--primary-link-color);
        }

    .form-control {
        color: var(--primary-text-color);
        background-color: var(--primary-background-color);
        border-color: var(--box-shadow-color);
    }

    #disqus_thread {
        background-color: var(--secondary-background-color) !important;
        box-shadow: 1px 1px 4px var(--box-shadow-color);
    }

    .hs-shows {
        border-top: 1px solid transparent;
    }

    .latest-releases ul li {
        border-bottom: 1px solid transparent;
    }

    .ind-show:hover {
        background-color: var(--quaternary-background-color) !important;
    }

    .rls-label:hover {
        background-color: var(--quaternary-background-color) !important;
        color: var(--primary-link-color);
    }

    .rls-info-container {
        border-bottom: 1px solid transparent;
    }

        .rls-info-container a:focus {
            color: var(--primary-link-color);
        }

    .schedule-today {
        background-color: var(--quaternary-background-color);
    }

    .footer-content {
        font-size: 0px;
    }

        .footer-content i {
            display: none;
        }

        .footer-content a {
            font-size: 14px;
        }

            .footer-content a:not(:last-child):after {
                content: " | ";
            }

        .footer-content::before {
            font-size: 14px;
            content: "Â© FeelingAliveSubs 2020 | ";
        }


    h4.hrf-title {
        color: var(--primary-text-color);
        background-color: #484848;
    }

    .hrf-content {
        color: var(--primary-text-color);
    }

    .hrf-title.close-faq span {
        background-color: var(--secondary-background-color);
    }
}

