style.css:
@-moz-document domain("cdaction.pl"), domain("forum.cdaction.pl") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 2px;
             background-color: #111;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 10px;
             background-color: #111;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 2px;
             -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, .6);
             background-color: #3d4456;
        }

#news_content, .window, .window .content, .back_bright, #iframePopupContainer, .iframe-popup-content, .iframe-popup, .tresc blockquote, .cProfileRepScore_neutral, #gry_az, #magazyn, #magazyn #dzielony, #magazyn #nawigacja li, #pasek_gora #lista select, option {
    background: #0F1112;
}
#pasek {
    background: #0f1112 !important;
}
.nawi_wykop {
    border-color: #0f1112;
}
p, .marg_b8, span, input, .login_txt, .window .content .left .register, .window h2, .window a.close, .opis, .back_bright, .iframe-popup-content p, .iframe-popup-content ul li, #zasoby .tag_info td, #zasoby .embed .tytul, #zasoby .data, #galeria .zawartosc .datax, #gry_az {
    color: #ccc;
}
.longarrow_white a, .window .content .right h4, .news_table .td_lead p, strong, #news_content .lead, #news_content .tresc, #zasoby h2, #zasoby .tag_info td.premiera, #zasoby .tag_info td.premiera h3, #zasoby .embed textarea, .cProfileRepScore .ipsType_minorHeading, #pasek_gora #lista select, option {
    color: #e1e0e0;
}
 .widget_kalendarzmodul table td, .wyszukiwarka input, #cda_szukaj input, #loginform input, .window a.close, .iframe-popup-content button.popup-later, #zasoby .embed textarea, .gry_az_top tbody tr td.last input {
    background: #222;
    border-color: #333;
}
.kom_i_wykopki, #news_content .powrot, .window .content, .not_last_news, .news_list_img_frame, .window .content .left, #pasek_gora #lista select, option {
    border-color: #34383e;
}
.longarrow_white_content, .ipsItemStatus.ipsItemStatus_large, .ipsItemStatus.ipsItemStatus_large.ipsItemStatus_read {
    background: #3d4456;
}
.longarrow_white_right {
    background: url(https://www.cdaction.pl/views/img1/longarrow_right.gif) no-repeat;
}
.longarrow_white_left {
    background: url(https://www.cdaction.pl/views/img1/longarrow_left.gif) no-repeat;
}
div.pagination .c1s {
    background: #222;
    border-radius: 4px;
    color: #ccc;
}
#news_content hr {
    border-color: #34383e !important;
}
.powrot > .pagination {
    position: relative;
    bottom: -5px !important;
}
.iframe-popup-content button.popup-later:hover {
    background: #333;
    border-color: #444;
}
.ipsType_break > span {
    color: #87b8d7;
}
.ipsType_break > span:hover { 
    color: #afd3e9
}
a.twitter-timeline {
    visibility: hidden;
}
a.twitter-timeline:after {
    font-size: 33px;
    color: #09f;
    visibility: visible;
    content: 'Twitter @cdaction';
    position: relative;
    bottom: 50px;
    left: -1px;
}
#news_content .lead a:hover, #news_content .tresc a:hover, #newsy a:hover {
    color: #697f9f;
}
.tresc blockquote > p, #news_content {
    color: #999;
}
#strzala_l, #strzala_p, #strzala_r {
    filter: invert(100);
}
.avatar > a > img {
	display: none;
}
.padBottom > .avatar > a > img {
	display: block;
	border-radius: 10px;
}
.avatar  {
	border: 1px solid #626a75;
	border-radius: 11px;
}
iframe {
	border-color: #34383e !important;
	background-color: #0000 !important;
}
}
