style.css:
@-moz-document domain(yourdomain.com) {

.messageList .b-content .messageListItem {
    position: relative;
    height: 52px;
    max-height: 60px;
    font-size: 12px;
    line-height: 21px;
    overflow: hidden;
    cursor: pointer;
    margin: 0;
    border: 0 solid transparent;
    z-index: 100;
    background-color: #30343c;
}

.messageList .b-content .messageListItem .sidebarParent {
    display: inline-block;
    width: 6px;
    background-color: #495163;
    float: left;
    height: 100%;
}

.messageList .b-content .messageListItem .subjectParent {
    display: block;
    overflow: hidden;
    -o-text-overflow: ellipsis;
    text-overflow: ellipsis;
    color: #fff;
}

.messageList .b-content {
    background-color: #22262d;
}

.messageList .b-content .messageListItem .dateParent, .messageList .b-content .messageListItem .senderParent, .messageList .b-content .messageListItem .subjectParent {
    white-space: nowrap;
    color: #fff;
}

.thm-message-list-top-toolbar, .thm-message-list-bottom-toolbar {
    background-image: -webkit-gradient(linear,0 0,0 100%,from(#30343c),to(#21262d)) !important;
    background-image: linear-gradient(to bottom,#30343c,#21262d) !important;
}
.messageList .second-toolbar {
    background-image: -webkit-gradient(linear,0 0,0 100%,from(#30343c),to(#21262d)) !important;
    background-image: linear-gradient(to bottom,#30343c,#21262d) !important;
}

.messageList .b-message-list-wrapper {
    position: absolute;
    top: 58px;
    right: 0;
    left: 0;
    bottom: 13px;
    border: 1px solid #222;
    -webkit-box-shadow: 0 2px 8px rgba(0,0,0,.2);
    box-shadow: 0 2px 8px rgba(0,0,0,.2);
    border-radius: 5px;
    z-index: 101;
}

.messageList .mainDelimiter {
    position: absolute;
    left: 0;
    right: 0;
    height: 1px;
    z-index: 101;
    background-color: #bbb;
}

.messageList .mainDelimiter {
    position: absolute;
    left: 0;
    right: 0;
    height: 1px;
    z-index: 101;
    background-color: #30343c;
}

.messageList .b-content .messageListItem.unseen {
    background-color: blueviolet;
}

/* Right */
.messageView .b-content {
    position: absolute;
    margin: 0;
    top: 58px;
    bottom: 13px;
    right: 8px;
    left: -2px;
    border: 1px solid #30343c;
    border-radius: 3px;
    background-color: #30343c;
}

.messageView .b-content .messageItemHeader {
    position: absolute;
    padding: 10px;
    background-color: #21262d;
    border-top: 0;
    border-bottom: 1px solid #21262d;
    border-top-right-radius: 0px;
    border-top-left-radius: 0px;
    z-index: 1;
    top: 0;
    left: 0;
    right: 0;
    color:white;
}

.messageView .b-content .messageItemHeader .informationFull {
    margin-top: 10px;
    border: 1px solid #444;
    background-color: #30343c;
    border-radius: 5px;
    padding: 8px 0;
    overflow: hidden;
    color: #ddd;
}

.close {
    float: right;
    font-size: 20px;
    font-weight: 700;
    line-height: 20px;
    color: #eee;
    text-shadow: 0 1px 0 #fff;
    opacity: .5;
}

span.subject-suffix {
    color: #777;
}

.b-system-drop-down .accountPlace {
    color:#777;
}

.b-folders .b-content .e-item .e-link {
    font-size: 13px;
}

/* Compose */
.b-compose .b-header {
    padding: 10px;
    background-color: #353941;
    color: #ddd;
}

.b-compose .b-header .e-identity {
    color: #ddd;
    text-decoration: none;
    font-weight: 700;
}

.g-ui-link {
    color: #3f9af5;
    text-decoration: underline;
    cursor: pointer;
    padding: 2px;
}

.b-compose .b-header-toolbar {
    height: 40px;
    color: #fff;
    background-color: #aaa;
    background-color: rgba(0e, 0f, 12, 0.8)!important;
}

.modal-header {
    padding: 9px 15px;
    border-bottom: 1px solid #111;
}
.cke_top {
    padding: 6px 4px 1px 6px!important;
    -webkit-box-shadow: none!important;
    box-shadow: none!important;
    border-bottom: 1px solid #353940!important;
    background: #2d3038!important;
}

.cke_chrome {
    border: 1px solid #6b6b6f!important;
}

.b-compose .attachmentAreaParent {
    padding: 10px 10px 6px 10px;
    background: #808080;
    border-top: 1px solid #a2a2a2;
    overflow-y: auto;
}

.popups .modal .modal-body {
    background-color: #474d56;
    max-height: none;
    color: #fff;
}
.modal-footer {
    padding: 14px 15px 15px;
    margin-bottom: 0;
    text-align: right;
    background-color: #2e3038;
    border-top: 1px solid #555;
    border-radius: 0 0 6px 6px;
    -webkit-box-shadow: inset 0 1px 0 #fff;
    box-shadow: inset 0 1px 0 #555;
}

.btn-group>.btn.single {
    border-radius: 3px!important;
    background-color: #30343c;
}
.btn-group>.btn.first {
    border-top-left-radius: 3px!important;
    border-bottom-left-radius: 3px!important;
    background-color: #30343c;
}
.btn.btn-dark-disabled-border.disabled, .btn.btn-dark-disabled-border[disabled] {
    border-color: #111;
}
.btn-toolbar .btn+.btn, .btn-toolbar .btn+.btn-group, .btn-toolbar .btn-group+.btn {
    margin-left: 5px;
    background-color: #30343c;
    color: #ddd;
}
html.textshadow .btn {
    text-shadow: 0 1px 0 #000;
    color: #ddd;
}
.btn[disabled] [class*=" icon-"], .btn[disabled] [class^=icon-], .disabled [class*=" icon-"], .disabled [class^=icon-] {
    color: #777;
}
.messageList .inputSearch {
    width: 258px;
    background-color: #30343c;
    color: #ddd;
}

input[type=email], input[type=password], input[type=search], input[type=text], textarea {
    border: 1px solid #555;
}

.btn {
    color: #ddd;
    background-color: #30343c;
}

.caret {
    border-top: 4px solid #ddd;
}

/* Folder List*/

.b-folders .b-content .e-item .e-link {
    height: 25px;
}

.badge {
    padding: 1px 7px 1px;
    border-radius: 7px;
    background-color: #79187f;
}
.btn.btn-success {
    background-color: #668405;
}

.messageList .b-content .messageListItem .wrapper {
    padding: 5px 0;
    color: #fff;
}

.messageList .b-content .messageListItem.withAttachments .attachment {
    display: inline-block;
    color: #eee;
    text-shadow: 0 1px 0 #111;
}

.messageList .b-content .messageListItem.focused {
    background-color: #78187f;
}
.messageList .b-content .messageListItem.selected {
    background-color: #1858d8;
    z-index: 101;
}

.icon-checkbox-checked, .icon-checkbox-partial, .icon-checkbox-unchecked, .icon-radio-checked, .icon-radio-unchecked {
    cursor: pointer;
    color: #797979;
    font-size: 1em;
}
span.subject-suffix {
    color: #929292;
}
.messageList .b-content .messageListItem.checked.focused .sidebarParent {
    background-color: #a656ad!important;
}

.e-pagenator .e-page.current .e-page-number {
    font-size: 25px;
    color: #fff;
    border-bottom: 2px solid #fff;
}

.messageList .b-content .messageListItem:last-child {
    border-bottom: 1px solid #31343c;
}
.messageList .b-content .listSearchDesc {
    font-size: 18px;
    padding: 15px;
    border-bottom: 1px solid #31343c;
}
.messageList .b-content .listSearchDesc {
    font-size: 18px;
    padding: 15px;
    border-bottom: 1px solid #31343c;
    color: #e2e2e2;
}

.icon-checkbox-unchecked:hover, .icon-radio-checked:active, .icon-radio-checked:hover, .icon-radio-unchecked:active, .icon-radio-unchecked:hover {
    color: #797979;
}
.thm-message-view-background-color {
    background-color: #31343c !important;
}

.messageView .b-content .content {
    background-color: #fff;
}

}
