style.css:
@-moz-document domain("www.brick-hill.com") {
/* Written by Tech */
body {
    background-color: #212121;
    color: #eee!important;
}

.card .content {
    background-color: #313131;
    color: #eee!important;
}

.divide {
    background-color: #444;
}

a {
    color: #CCC!important;
}

.thread-row, hr {
    border-color: #444;
}

footer {
    background: #1f1f1f;
}

.card .footer {
    background-color: #313131;
    border-color: #444;
}

input.rigid, textarea.rigid, .select, select {
    background-color: #272727;
    color: #eee;
    border-color: #444;
}
.friend-card:hover {
    background-color: #484848;
}

.card .item-card-details, .card .item-content {
    background-color: #272727;
    border-color: #444;
}

.card .name {
    color: #eee!important;
}

.box.item-img {
    background-color: #4f4f4f;
    border-color: #272727;
}

.ablack-text {
    color: #eee;
}

.item-data .ellipsis span:last-of-type {
    float: right;
}

.item-data .ellipsis span:last-of-type::before {
    content: "Type: ";
    color: #8a8a8a;
}

.darkest-gray-text {
    color: #d4d4d4;
}

.agray-text {
    color: #ccc;
}

.card .item-creator {
    color: #888;
}

.item-data .agray-text.bold {
    font-weight: 100;
}

.tabs .tab.active {
    background-color: #555;
}

.tabs .tab {
    color: #eee;
    border-color: #444;
    background-color: #404040;
}

.tab-body.active {
    background: #313131;
    border-color: #444;
}

input, textarea {
    background-color: #272727;
    color: #eee;
    border-color: #444;
}

.dark-gray-text {
    color: #9e9e9e;
}

.box.shaded {
    background-color: #4f4f4f;
    border-color: #272727;
}

.recommended-item .box.shaded img {
    padding: 15px;
}

.special-e-icon, .special-icon {
    filter: grayscale(1)brightness(0.6)invert(1);
}

.thumbnail.dark[style="position:relative;border:5px solid #FFD52D;border-bottom:0;padding:15px 15px 20px;"] {
    border-color: #7f7f7f!important;
}

.sellers .seller:not(:last-child) {
    border-color: #444;
}

.hover-card:hover {
    background-color: #404040;
}

.profile-card:hover {
    background-color: #404040;
}

.dropdown-content li :hover {
    background-color: #565656;
}

.dropdown-content li * {
    background-color: #444;
}

.content .board div.stat span {
    color: #969696!important;
}

.black-text {
    color: #eee;
}

.m0.thread-card:not(.viewed) {
    border-left: 3px solid #484848;
}

.hover-card.viewed {
    background-color: #313131;
}

.label.dark, .board-info a, .stat .title {
    color: #eee;
}

.crate-types .active, .crate-types :hover {
    background-color: #484848;
}

ul.crate-types {
    border-color: #444;
}

.profile-card.crate span {
    color: #eee!important;
}

.profile-card.crate img {
    background-color: #4e4e4e;
    border-color: #222;
}

.trade-serial {
    background-color: #333;
}

.trade-item-box {
    border-color: #444;
}

.trade-item img {
    border-color: #444;
}

.trade-item .trade-name {
    color: #eee;
}

.trade-ev {
    color: #eee;
}

.trade-item .serial {
    color: #999;
}

.button, button, .modal .cancel-button, .input-group .input-button {
    background-color: #313131;
    border-color: #676767;
    color: #eee;
}
.input-group {
    border-color: #444;
}
.gray-text {
    color: #eee;
}
.membership-top .membership-cubes {
    background-image: url(https://i.imgur.com/POWxukw.png);
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    width: 1140px;
    height: 200px;
    padding-left: 1140px;
}
.membership-top.unselectable {
    z-index: -999;
}
.mem-cols ul li, .mem-cols ul li b {
    color: #ddd;
}
.mem-cols .col {
    border-color: #444;
}
.avatar-card.crate img {
    background: transparent;
}
.avatar-card.crate .img-holder {
    border-color: #444;
}
.avatar-card.crate span[style="color:#767676"] {
    color: #eee!important;
}
.toggle-user-desc:not(.open):after, .read-more-desc {
    background-color: #313131;
}
.user-desc {
    color: #eee;
    font-weight: 200;
}
.modal .modal-content {
    background-color: #313131;
}
.award img {
    border-color: #444!important;
}
/* Top Cards */
.card .top:not(.absolute) {
    background-color: #424242;
    border-color: #565656;
}

.card .top:not(.absolute).green, .button.green, button.green {
    background-color: #283e25;
    border-color: #205d18;
}

.card .top:not(.absolute).blue, .button.blue, button.blue {
    background-color: #09293a;
    border-color: #0d405e;
}

.card .top:not(.absolute).red, .button.red, button.red {
    background-color: #2d1e1f;
    border-color: #562b2e;
}

.card .top:not(.absolute).orange, .button.orange, button.orange {
    background-color: #3e3100;
    border-color: #614c04;
}

}
