style.css:
/* 
 * @date 2020-05-29T05:12:48.020
 */
/* 
 * 变量配置
 * var.css
 */
@-moz-document domain("keylol.com") {
	/*
	 * 变量配置
	 */
	:root {
	    --dark-accent: var(--color600);
	
	    --bg-dark2: #151515;
	    --bg-dark1: #222;
	    --bg-dark3: #444;
	    --bg-dark4: #777;
	
	    --t-dark: #d6d6d6;
	
	    --color500: var(--bg-dark2);
	}
}

/* 
 * @date 2020-05-29T05:12:48.033
 */
/* 
 * 导航栏
 * nav.css
 */
@-moz-document domain("keylol.com") {
	/*
	 * 导航栏
	 * nav.css
	 */
	/* region 导航栏 */
	#nav-menu ul li ul li a {
	    color: var(--t-dark);
	}
	
	.dropdown-menu > li > a {
	    color: var(--t-dark);
	}
	
	#nav-user-action-bar .divider {
	    background: var(--bg-dark1) !important;
	}
	
	.dropdown-menu > li > a:hover {
	    color: var(--t-dark);
	}
	
	/* endregion 导航栏 */
	
	/* 全局 */
	body {
	    background: var(--bg-dark1);
	}
	
	a {
	    color: var(--t-dark);
	}
}

/* 
 * @date 2020-05-29T05:12:48.033
 */
/* 
 * 首页
 * home.css
 */
@-moz-document regexp("https?://keylol.com/(forum.php)?") {
	/*
	 * 首页
	 * home.css
	 */
	/* region 首页 */
	
	/* region banner */
	.xl1 ol > li:nth-child(odd) {
	    background: var(--bg-dark1) !important;
	}
	
	.xl1 ol > li:nth-child(even) {
	    background: var(--bg-dark1) !important;
	}
	
	/* endregion banner */
	
	#wp > div:nth-child(odd):not(:nth-child(1)) {
	    background: var(--bg-dark1) !important;
	}
	
	#wp > div:nth-child(even) {
	    background: var(--bg-dark2) !important;
	}
	
	.subject_row_detail_text_down {
	    color: var(--t-dark) !important;
	}
	
	/* endregion 首页 */
}

/* 
 * @date 2020-05-29T05:12:48.034
 */
/* 
 * 帖子内容
 * post.css
 */
@-moz-document url-prefix("https://keylol.com/t"), url-prefix("https://keylol.com/forum.php?mod=viewthread&t") {
	/*
	 * 帖子内容
	 * post.css
	 */
	/* region 帖子 */
	
	.subforum_left_title_left_down a:nth-child(1) {
	    color: var(--dark-accent);
	}
	
	.subforum_left_title_left_down a:before {
	    background: var(--dark-accent);
	}
	
	/* region 字体 */
	#postlist,
	.ptm.pnpost + div,
	.ptm.pnpost + div + label {
	    color: var(--t-dark);
	}
	
	/* region 分页 */
	.pgs.mtm.mbm.cl {
	
	}
	
	/* endregion 分页 */
	
	/* endregion 字体 */
	
	#postlist,
	#postlist .pls {
	    background: var(--bg-dark2);
	}
	
	.steam_connect_user_bar,
	.pi,
	.po {
	    border: unset;
	}
	
	/* region 帖子*/
	
	/*region code*/
	#postlist .blockcode {
	    border-radius: 5px;
	    border: 1px solid var(--bg-dark1);
	    background: var(--bg-dark1);
	    color: var(--orange500);
	}
	
	#postlist .blockcode li:hover {
	    background: var(--bg-dark1);
	    color: var(--t-dark);
	}
	
	/*endregion code*/
	
	/* region 回复间隔 */
	.ad .pls,
	.ad .plc {
	    background: var(--bg-dark1) !important;
	}
	
	/* endregion 回复间隔 */
	a[id^=postnum] {
	    border-color: var(--bg-dark1) !important;
	}
	
	/* region 引用 */
	.pl blockquote {
	    background: var(--bg-dark1) !important;
	}
	
	.pl .quote:before {
	    background: var(--bg-dark3);
	}
	
	/* endregion 引用 */
	
	/* region 评分 */
	.ratl th, .ratl td, .ratc {
	    border: unset;
	}
	
	/* endregion 评分 */
	
	/* endregion 帖子*/
	
	/* region 分页小条条 */
	.pg strong {
	    /*color: var(--white1);*/
	    background-color: var(--bg-dark1);
	}
	
	#f_pst form {
	    border-top: 5px solid var(--bg-dark1);
	}
	
	.pg a {
	    color: var(--bg-dark1);
	}
	
	.pg a:hover {
	    color: var(--t-dark);
	}
	
	.pg a.nxt:after {
	    border-color: transparent var(--bg-dark1) var(--bg-dark1) transparent;
	}
	
	.pg a.nxt:hover:after {
	    border-color: transparent var(--t-dark) var(--t-dark) transparent;
	}
	
	/* endregion 分页小条条 */
	
	/* region 回复 */
	.tedt .bar,
	.tedt .area,
	.tedt .pt {
	    background: var(--bg-dark1);
	}
	
	.tedt .bar {
	    border: 1px solid var(--bg-dark2);
	}
	
	/* endregion 回复 */
	/* endregion 帖子 */
}

/* 
 * @date 2020-05-29T05:12:48.034
 */
/* 
 * 板块（帖子列表）
 * postList.css
 */
@-moz-document regexp(".+keylol.com/f[0-9]{3}.+"), regexp(".+keylol.com.+forumdisplay.+fid=[0-9]+.+") {
	/*
	 * 板块（帖子列表）
	 * postList.css
	 */
	/* region 板块 */
	
	.subforum_left_title_left_down {
	    color: var(--dark-accent);
	}
	
	
	#ct,
	#ct .subforum_subject2.clearfix div,
	#ct .subforum_subject2 .subforum_subject_detail_text_down .active a {
	    color: var(--t-dark);
	}
	
	#ct,
	#ct .mn,
	#ct .subforum_subject.clearfix,
	#ct .bmw,
	#ct .bm_c,
	#ct .subforum_subject2,
	.forumrowdata,
	.bm {
	    background: var(--bg-dark2) !important;
	}
	
	.pbn {
	    padding-bottom: unset;
	}
	
	#ct .tl .ts th,
	#ct .tl .ts td {
	    background: var(--bg-dark2);
	    border-bottom: 1px solid var(--bg-dark1);
	}
	
	#ct .tl th,
	#ct .tl td {
	    border-bottom: 1px solid var(--bg-dark1);
	}
	
	#ct #threadlisttableid tr:hover {
	    background: var(--bg-dark1) !important;
	}
	
	#ct .tl tr:hover th,
	#ct .tl tr:hover td,
	#ct .tl tr:hover th:hover,
	#ct .tl tr:hover td:hover {
	    background: var(--bg-dark1);
	}
	
	#ct #f_pst form,
	.tedt {
	    border: unset;
	}
	
	#fastposteditor .tedt .area textarea {
	    color: var(--t-dark);
	}
	
	/* region 图片板块 */
	#ct .waterfall li,
	#ct .waterfall .c,
	#ct .waterfall h3,
	#ct .waterfall .auth {
	    background: var(--bg-dark1) !important;
	    border: unset;
	}
	
	#ct .waterfall .c .nopic {
	    width: 214px !important;
	    background: var(--t-dark);
	    -webkit-mask-image: url("data:image/png;base64,");
	    -webkit-mask-repeat: no-repeat;
	    -webkit-mask-position: center;
	}
	
	/* endregion 图片板块 */
	
	/* region 分页小条条 */
	.pg strong {
	    /*color: var(--white1);*/
	    background-color: var(--bg-dark1);
	}
	
	#f_pst form {
	    border-top: 5px solid var(--bg-dark1);
	}
	
	.pg a {
	    color: var(--bg-dark1);
	}
	
	.pg a:hover {
	    color: var(--t-dark);
	}
	
	.pg a.nxt:after {
	    border-color: transparent var(--bg-dark1) var(--bg-dark1) transparent;
	}
	
	.pg a.nxt:hover:after {
	    border-color: transparent var(--t-dark) var(--t-dark) transparent;
	}
	
	/* endregion 分页小条条 */
	
	/* region 回复 */
	.tedt .bar,
	.tedt .area,
	.tedt .pt {
	    background: var(--bg-dark1);
	}
	
	.tedt .bar {
	    border: 1px solid var(--bg-dark2);
	}
	
	/* endregion 回复 */
	
	/* endregion 板块 */
}

/* 
 * @date 2020-05-29T05:12:48.035
 */
/* 
 * 通知
 * notification.css
 */
@-moz-document regexp(".+keylol.com.+mod=space&do=(pm|notice(&view=.*)?).*") {
	/*
	 * 通知
	 * notification.css
	 */
	/* region 消息 */
	#ct {
	    color: var(--t-dark);
	}
	
	.ct2_a, .ct3_a,
	.bbda,
	.nts dl {
	    border: unset;
	}
	
	.bm {
	    background: unset;
	}
	
	.bbda:hover {
	    background: var(--bg-dark2);
	}
	
	/* border: 1px solid #f7f7f7;
	/* endregion 消息 */
}

/* 
 * @date 2020-05-29T05:12:48.035
 */
/* 
 * 高级编辑器
 * new-thread.css
 */
@-moz-document regexp(".+keylol.com.+mod=post&action=(newthread|reply|edit).*") {
	/*
	 * 发新贴
	 * new-thread.css
	 * @url https://keylol.com/forum.php?mod=post&action=newthread&fid=127&cedit=yes&extra=
	 */
	#postform {
	    --icon-width: 26px;
	    --icon-height: 26px;
	    --icon-margin: 2px;
	    --icon-bg: var(--bg-dark1);
	    --icon-bg-hover: var(--color700);
	    --btn: var(--bg-dark1);
	    --btn-bg: var(--color600);
	    background: #0d47a1;
	}
	
	#post_extra {
	    --tab-bg: var(--bg-dark1);
	    --tab-bg-avtive: var(--bg-dark2);
	    border: 1px solid var(--bg-dark2);
	}
	
	#ct.ct2_a,
	.edt .bar,
	.bm {
	    background: var(--bg-dark1) !important;
	}
	
	.edt .bar {
	    border-bottom: 1px solid var(--bg-dark2);
	}
	
	#e_body {
	    color: unset !important;
	    border: 1px solid var(--bg-dark2);
	}
	
	#e_switchercheck {
	    background: unset !important;
	}
	
	.edt .area {
	    background: var(--bg-dark1) !important;
	}
	
	body {
	    color: unset;
	}
	
	.edt .bbar {
	    background: unset;
	    border-top: 1px solid var(--bg-dark2);
	}
	
	#e_body, #e_body .bar {
	    border-radius: 5px 5px 0 0;
	}
	
	#post_extra_c .exfm {
	    border: 1px solid var(--bg-dark2);
	}
	
	.mtm.mbm.pnpost > div:last-child {
	    line-height: 20px;
	    background: var(--bg-dark2) !important;
	    color: var(--t-dark);
	}
	
	.edt .pt {
	    background: var(--bg-dark1) !important;
	    color: var(--t-dark);
	}
	
	#rstnotice {
	    background: var(--bg-dark1) !important;
	}
	
	#subject {
	    color: var(--icon-bg-hover);
	    border-color: var(--bg-dark2);
	}
	
	#postsubmit {
	    color: var(--bg-dark2) !important;
	}
	.edt .btn{
	    border: unset;
	}
}
