style.css:
/* i really want this to be global */
.js-result-snippet, .result__date, .result__snippet, .result__snippet b, 
.result__snippet .sponsored__sitelink__comma, .tw-align-items-center, 
.tw-flex, .tw-flex-grow-1, .tw-flex-shrink-1, .tw-full-width, .tw-justify-content-end, * {
  border: #666666 !important;
  color: #666666 !important;
  background: rgba(0, 0, 0, .3) !important;
}
.video-player__overlay {
	background: transparent !important;
}
.cm-searching {
  background: #444444 !important;
}
.CodeMirror-code {
  background: #aaaaaa !important;
}
.xterm-cursor-layer {
  z-index: 0 !important;
  background: rgba(136, 136, 136, .3) !important;
}
.cm-s-ipython span.cm-comment {
  color: #886666 !important;
  font-style: italic !important;
}
.cm-s-ipython span.cm-error {
  color: #990000 !important;
  background: #440000 !important;
}
.cm-s-ipython span.cm-number {
  color: #007777 !important;
}
.cm-s-ipython span.cm-builtin {
  color: #009999 !important;
}
.cm-s-ipython span.cm-keyword {
  color: #336666 !important;
  font-weight: bold !important;
}
.cm-s-ipython span.cm-operator {
  color: #996666 !important;
  font-weight: bold !important;
}
.cm-s-ipython span.cm-string {
  color: #662222 !important;
}
.cm-s-ipython span.cm-def {
  color: #884444 !important;
}
.CodeMirror-cursor {
  border-left: 2px solid #008888 !important;
}
div.output.output_scroll {
  box-shadow: none;
}
::-webkit-scrollbar, ::-webkit-scrollbar-track, .gb_6a.gb_l::-webkit-scrollbar-track, 
.gb_6a.gb_l::-webkit-scrollbar-track:hover, [watch-color-update] yt-live-chat-app 
::-webkit-scrollbar-track {
  background: #222222;
  border: none;
  width: 11px;
  max-height: 9px;
}
::-webkit-scrollbar-thumb, .gb_6a.gb_l::-webkit-scrollbar-thumb, 
[watch-color-update] ::-webkit-scrollbar-thumb {
  border-radius: 2px;
  border: none;
  background: #444444;
  background-clip: content-box;
  width: 11px;
}
tooltiptext bigtooltip {
  overflow-x: visible;
  overflow-y: scroll;
  height: 400px;
  max-width: 800px;
}
