style.css:
@-moz-document url-prefix("https://translate.google") {
/*by Zero_162*/

/*This is editing header block height*/
 #gb {
    height: 135px !important;
    background: url(https://i.imgur.com/fU5zfoY.gif) 50% 100% no-repeat !important;
    /*gif | ссылка на гиф*/
    background-size: 101px 128px !important;
    /*resizing the image| размер изображения*/
}
/*==============================================================*/
 .frame {
    height: calc(99.9vh - 135px) !important;
}
.feedback-link {
    margin-bottom: 60px !important;
}
/*==============================================================*/
 body.displaying-history-page .history-page {
    height: calc(100vh - 135px) !important;
    top: 135px !important;
}
.history-top-header {
    top: 135px !important;
}
.history-body {
    height: calc(100vh - 130px -135px)!important;
    margin-top: 130px !important;
}
body.displaying-saved-page:not(.with-lang-list) .phrasebook-page {
    height: calc(100vh - 135px) !important;
    top: 135px !important;
}
.phrasebook-top-header {
    top: 135px !important;
}
.phrasebook-body {
    height: calc(100vh - 130px -135px)!important;
    margin-top: 170px !important;
}
.placeholder-image {
    background-size: 70% !important;
    height: 151px !important;
    margin-bottom: 5px !important;
}
.instant-page {
    top: 135px !important;
}
}
