style.css:
@-moz-document domain("www.flickr.com") {


/* === FLICKR - 00 - Flickr WideScreen - ALLinONE - Favorites Small v.1 - TEST - === */

.nav-banner-container {
	display: none ;
}
.fluid.html-favorites-page-view .fluid-centered {
    display: inline-table !important;
    box-sizing: border-box;
    margin-left: auto;
    margin-right: auto;
    width: 100% !important;
    min-width: 100% !important;
    max-width: 100% !important;
}

.fluid.html-favorites-page-view #content .view.photo-list-view  {
    position: absolute !important;
    width: 99.9% !important;
/* POSITION / HEIGHT for LARGE for ALLinONE */
/* height: 6427px !important; */
/* HEIGHT for SMALL */
    height: 2000px !important;
    top: 150px !important;
/* margin-bottom: 90px !important; */
left: 0 !important;

/* POSITION for LARGE ? */
/* top: 60px !important; */
/* margin-bottom: -90px !important; */
    text-align: center !important;
    border: 1px solid #333 !important;
    border: 1px solid red !important;
}

/* PAGINATION */
.fluid.html-favorites-page-view #content .view.photo-list-view + .view.pagination-view {
    position: fixed !important;
    display: inline-block !important;
    margin: 20px auto;
    width: 100% !important;
    height: 30px !important;
    bottom: -20px !important;
    text-align: center;
    z-index: 5000 !important;
background: black !important;
}


/* FAVS THUMB */
.fluid.html-favorites-page-view #content .view.photo-list-view .view.photo-list-photo-view {
    position: relative !important;
    display: inline-block !important;
/* FAVS SHOTS LARGE */
/* height: 300px !important; */
/* width: 300px !important; */
/* margin: 3px 35px 5px 3px !important; */

/* FAVS SHOTS SMALL */
     height: 180px !important;
     width: 180px !important;
     margin: 3px 2px 2px 2px !important;
	
     transform: translate(0 , 0) !important; 
background-size: contain !important;
background-color: transparent !important;
outline: 1px solid transparent !important;
box-shadow: none !important;
border: 1px solid red !important;
border: 1px solid #333 !important;
}



.fluid.html-favorites-page-view #content .view.photo-list-view .view.photo-list-photo-view .photo-list-photo-interaction {
    cursor: pointer;
    height: 100%;
    position: relative !important;
    width: 100%;
    opacity: 1 !important;
/* background: blue !important; */
}
.fluid.html-favorites-page-view #content .view.photo-list-view .view.photo-list-photo-view .photo-list-photo-interaction a.overlay  {
    left: 0 !important;
    outline: 1px solid transparent !important;
border: 1px solid #222 !important;
}
.fluid.html-favorites-page-view #content .view.photo-list-view .view.photo-list-photo-view .photo-list-photo-interaction a.overlay:visited  {
border: 1px solid red !important;
}
.fluid.html-favorites-page-view #content .view.photo-list-view .view.photo-list-photo-view .photo-list-photo-interaction .interaction-bar .text .title {
    line-height: 8px !important;
    margin-top: 15px !important;
    font-size: 8px !important;
    color: gray !important;
    opacity: 0 !important;
    transition: all ease 0.7s !important;
}
.fluid.html-favorites-page-view #content .view.photo-list-view .view.photo-list-photo-view .photo-list-photo-interaction:hover .interaction-bar .text .title {
    opacity: 1 !important;
    line-height: 15px !important;
    font-size: 13px !important;
    color: gold !important;
    transition: all ease 0.7s !important;
}
.fluid.html-favorites-page-view #content .view.photo-list-view .view.photo-list-photo-view .photo-list-photo-interaction .interaction-bar .engagement .engagement-item.fave .engagement-icon  ,
.fluid.html-favorites-page-view #content .view.photo-list-view .view.photo-list-photo-view .photo-list-photo-interaction .interaction-bar .engagement:hover .engagement-item, 
.fluid.html-favorites-page-view #content .view.photo-list-view .view.photo-list-photo-view .photo-list-photo-interaction .interaction-bar .engagement .engagement-item {
font-size: 10px  !important;
}
.photo-list-photo-interaction .interaction-bar .engagement .engagement-item .engagement-icon svg {
    display: block;
    height: 10px !important;
    margin-top: 3px !important;
    width: 10px !important;
}


/* COUNTER - start at 01 */
.fluid.html-favorites-page-view #content .view.photo-list-view {
    content: counter(myIndex, decimal-leading-zero);
    counter-increment: myIndex  -1 !important;
}
.fluid.html-favorites-page-view #content .view.photo-list-photo-view::before {
    content: counter(myIndex, decimal-leading-zero);
    position: absolute;
    width: auto;
    line-height: 15px;
    min-width: 17px;
    left: -10px !important;
    bottom: -5px !important;
    text-align: center;
    border-radius: 10px;
    font-size: 10px;
    z-index: 100;
color: tomato;
box-shadow: 0 0 2px rgba(162, 160, 160, 0.6) inset;
background: rgba(62, 59, 59, 0.6) none repeat scroll 0 0;
opacity: 0.5 !important;
/* opacity: 1 !important; */
}
.fluid.html-favorites-page-view #content .view.photo-list-photo-view:hover::before {
opacity: 1 !important;
}

/* === END === */
}
