style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("blogblogs.com.br") {

/**/

	#publicidade {display: none !important;}

	.publicidade { display: none !important;}

	#barra_principal {display: none !important;}


	#coluna_esquerda p, .post, #full .conteudo_destaque_500  {	line-height: 18px !important;
	text-align: left !important;
	font-family:  "Trebuchet MS", Arial, sans-serif !important;
	vertical-align: top !important;
	background: #f3f3f3 !important;
	color: #000 !important;
}

	#coluna_esquerda h3{	line-height: 18px !important;
	text-align: left !important;
	margin-top:10px !important;
	padding-top: 10px !important;
	font-family:  "Trebuchet MS", Arial, sans-serif !important;
	font-weight: normal !important;
	vertical-align: top !important;
	background: #f3f3f3 !important;
	color: #000 !important;

}

	.post h4, #full .conteudo_destaque_500 h4 {
	margin: 0 0 12px !important;
	font-family: Arial !important;
	font-size: 18px !important;
	font-weight: normal !important;
	line-height: 28px !important;
	color: #FF2E9A !important;
}


	.texto_destaque_post h4 {
	font-family:  "Trebuchet MS", Arial, sans-serif !important;
	font-weight: normal !important;
	line-height: 18px !important;
	color: #FF2E9A !important;
}


	.post h4 a, #full .conteudo_destaque_500 h4 a, .texto_destaque_post h4 a {
	text-decoration: none !important;
	color: #ff2e9a !important;
}

	.post h4 a:hover, #full .conteudo_destaque_500 h4 a:hover, .texto_destaque_post h4 a:hover {
	text-decoration: underline !important;
}


	#messageContainer a, #messageContainer .post a, #full .conteudo_destaque_500 a, .texto_destaque_post a, #coluna_direita a {
	text-decoration: underline !important;
	color: #3987FC !important;
}

	#messageContainer a:hover, #messageContainer .post a:hover, #full .conteudo_destaque_500 a:hover, .texto_destaque_post a:hover, #coluna_direita a:hover {
	text-decoration: none !important;
}
/**/

}
