    background: #91aed5;
    color: #fff;
    border: 0;
}
.history-wrap .time-label{
    border: 1px solid #6982a4;
    color: #6982a4;
}
.history-wrap .active{
    background-color: #91aed5;
    color: #fff;
    border-color: #91aed5;
}
.history-wrap .time-label:before{
    border-left: 8px solid #2c3848;
}
