    border-left: 8px solid #91aed5;
}
.history-wrap .active:after{
    border-left: 8px solid #91aed5;
}
.history-list{
    padding-top: 5px;
    padding-bottom: 5px;
    margin-top: 10px;
}
.history-list .history-record{
    padding-top: 10px;
    padding-bottom: 10px;
}
.history-list .l-info{
    border-right: 1px solid #6982a4;
}
.history-list .l-info .lastplay-time .history-red-round{
    border: 5px dashed #6982a4;
    border-right-width: 0;
    border-left-style: solid;
    border-top-color: transparent;
    border-bottom-color: transparent;
}
.history-list .l-info .lastplay-time .lastplay-t{
    color: #6982a4;
}
.history-list .r-info{
    background: #314259;
    padding: 20px;
    border-radius: 4px;
}
.history-wrap .history-list .r-info .r-txt {
    width: 546px;
    border-bottom: 1px solid #2c3848;
}
.history-list .r-info .title,
