style.css:
@-moz-document url-prefix("https://panzoid.com/"), url-prefix("https://panzoid.com/users"), url-prefix("https://panzoid.com/discussions"), url-prefix("https://panzoid.com/creations"), url-prefix("https://panzoid.com/about") {
:root {
    --background-color: #1f1f1f;
    --background-color-lighter: #2e2f31;
    --border-color: #505050;
    --text-color: #fff;
    --text-color-invert: #000;
    --text-color-darker: #b0b0b0;
    --text-unread-color: #8ac3f2;
    --text-read-color: #bdbdbd;
    --outline-color: #1862ab;
    --highlight-color: #afb5bb;
}

body {
    background-color: var(--background-color);
    font-family: 'Segoe UI', sans-serif;
    outline-color: var(--outline-color)
}
user agent stylesheet html {
    color: var(--text-color)
}

a {
    color: var(--text-unread-color)
}

a.creationTitle {
    color: var(--text-color)
}

a.creationUser {
    color: var(--text-color-darker)
}

a.homeheading {
    color: var(--text-color-darker)
}

a.seen {
    color: var(--text-read-color)
}

td {
    border-bottom: 1px solid var(--border-color);
    color: #c8c8c8
}

span {
    color: var(--text-color-darker)
}

.more {
    background-color: var(--background-color);
    color: var(--text-unread-color)!important
}

.sections a {
    color: var(--text-color-darker)
}

th {
    color: var(--text-color);
    border-bottom: 1px solid var(--border-color)
}

td {
    border-bottom: 1px solid var(--border-color)
}

.num {
    color: var(--text-color)
}

button.cat {
    background-color: var(--background-color);
    border: 2px solid var(--border-color);
    color: var(--text-color)
}

#categorydd.catlist {
    background-color: var(--background-color);
    border-color: var(--border-color);
    color: var(--text-color)
}

li {
    background-color: var(--background-color)!important;
}

li.active {
    color: var(--text-color-invert)!important;
    border-color: var(--border-color);
    background-color: var(--highlight-color)!important
}

div {
    color: var(--text-color);
}

div.post {
    border-bottom: 1px solid var(--border-color)
}

div.creation_user {
    color: var(--text-color)
}

div.post_message {
    color: var(--text-color)
}
div.post_info {
    color: var(--text-color-darker)
}

div.post_likes {
    color: var(--text-color-darker)
}

div button {
    color: var(--text-color)
}

div.post_controls svg {
    fill: var(--background-color-lighter);
    color: var(--text-color)
}

div.userheader {
    background-color: var(--background-color-lighter)!important;
    border: 1px solid var(--background-color-lighter)!important;
}

div.emptymessage {
    color: var(--text-color-darker)
}

div.firstpost {
    border-top: 1px solid var(--border-color)
}

div#discussion_title {
    background-color: var(--background-color);
    border-bottom: 1px solid var(--border-color)
}

div#discussion_controls {
    background-color: var(--background-color)!important;
    color: var(--text-color);
    border-top: 1px solid var(--border-color)!important
}

textarea.post_text {
    background-color: var(--background-color)!important;
    border: 1px solid var(--border-color);
    color: var(--text-color)
}

#user_name {
    color: var(--text-color)
}

#editor_padding {
    color: var(--text-color-invert)
}

#editor_title {
    background-color: var(--background-color-lighter);
    color: var(--text-color);
    border: 1px solid var(--border-color)
}

#editor_content {
    background-color: var(--background-color-lighter);
    border: 1px solid var(--border-color);
    color: var(--text-color)
}

#discussion_title {
    border-bottom: 1px solid var(--border-color)
}

#editor {
    box-shadow: 0 0 25px var(--border-color);
    background-color: var(--background-color)
}

a#creation_user_link {
    color: var(--text-color-darker)!important
}

.category {
    color: var(--text-color-darker)
}

li {
    background-color: var(--background-color);
    border: 1px solid var(--border-color)
}

li.active {
    background-color: var(--highlight-color)
}

li.active div {
    color: var(--text-color-invert)
}

ul li {
    border-color: var(--background-color);
}

span {
    color: var(--text-color-darker)
}
}
