style.css:
@-moz-document domain("toonhq.org") {
body {
    background-image: url(https://i.imgur.com/7mAL7hh.png);
    color: rgba(255,255,255,0.7)
}

.navbar {
    background: #121212;
}

.home-content {
    background-color: #121212;
}
}

@-moz-document url-prefix("https://toonhq.org/invasions") {
.media.media-invasion {
    background-color: #121212;
    box-shadow: 4px 4px 12px #121212;
}

.subnav>li>a {
    color: #121212;
}

.subnav>li .active>a {
    color: #121212;
}

.navbar .navbar-nav li.active>a {
    color: #121212;
}

.modal-content {
    background-color: #121212;
}

.well.well-ttr {
    background-color: #121212;
}

.notification-specific ul.specific-cogs>li {
    background-color: #121212;
}

.well {
    border: 1px solid #121212;
}

.media.media-invasion:hover {
    box-shadow: 4px 4px 20px #000000;
}
}

@-moz-document url-prefix("https://toonhq.org/groups") {
.media.media-group {
    background-color: #121212;
    box-shadow: 4px 4px 12px #121212;
}

.modal-content {
    background-color: #121212;
}

.speedchat__footer {
    background: #000000;
}

.select2-container--default .select2-selection--single {
    background-color: #121212;
}

.select2-dropdown {
    background-color: #121212;
}

input {
    background-color: #121212;
}

.select2-container--default.select2-container--disabled .select2-selection--single {
    background-color: #121212;
}

.media.media-group:hover {
    box-shadow: 4px 4px 20px #000000;
}

.dropdown-menu {
    background-color: #121212;
}

}

@-moz-document url-prefix("https://toonhq.org/fishing") {
.fishing-card {
    background-color: #121212;
}

.fishing-abbrev {
    background-color: #121212;
}

body.fishing {
    background-image: url(https://i.imgur.com/XUQyWm1.png);
}
}

@-moz-document url-prefix("https://toonhq.org/apps") {
.well.well-ttr {
    background-color: #121212;
}

.well {
    border: 1px solid #121212;
}
}

@-moz-document url-prefix("https://toonhq.org/ccg") {
.ccg-card {
    background: #121212;
}
}
