style.css:
@-moz-document domain(flightrising.com)
{
/* DRAGON PROFILE */
		 #dragon-profile-likes {
		    display: none;
		 }
	    .dragon-profile-header-number {
	    font-size: 14px !important;
	    color: #a6a6a6 !important;
	    }
        .dragon-profile-energy-meter-frame, .dragon-profile-familiar-meter-frame {
	    height: 10px !important;
	    margin-top: 4px !important;
	    opacity: 0.5 !important;
	    }
    /* Stats and Derg Info */
        #dragon-profile-show-details, #dragon-profile-hide-details{
        width: 20px !important;
        }
        #dragon-profile-details-misc .common-tabs{
        margin-right: 0px !important;
        }
	    .dragon-profile-details-frame {
	    border: none !important;
	    border-radius: 5px !important;
	    background: #f9f9f9 !important;
        overflow: auto;
	    }
	    .dragon-profile-xp-meter-frame {
	    opacity: 0.6 !important;
	    }
        #dragon-profile-apparel-items {
        border-bottom: 1px solid #d5cfc2 !important;
        margin-bottom: 6px !important;
        }
    /* Bio */
        #dragon-profile-bio.dragon-profile-bio, #dragon-profile-bio.dragon-profile-vista-bio {
        border: none !important;
	    border-radius: 5px !important;
	    background: #f9f9f9 !important;
        }
    /* Buttons */
        .common-ui-button, .common-ui-button-disabled {
	    border: 1px solid #d5cfc2 !important;
	    }
        .common-ui-button-disabled{
        opacity: 0.6 !important;
        }
    /* FAMILIAR */
	    #dragon-profile-button-name-familiar {
	    opacity: 0.4 !important;
	    height: 10px !important;
	    top: -1px !important;
	    }
        #dragon-profile-familiar-frame {
        top: 110px !important;
        }
	    #dragon-profile-familiar-name {
	    position: absolute !important;
	    top: 210px !important;
	    padding-left: 0px !important;
	    }
	    #dragon-profile-familiar-type {
	    position: absolute !important;
	    top: 230px !important;
	    padding-left: 0px !important;
	    }
	/*APPAREL CUSTOMISE*/
        #choose-apparel-tabs, #choose-apparel-apparel-button .common-tab .common-tab-big , #choose-apparel-skin-button .common-tab .common-tab-big  {
        margin-bottom: 0px !important;
        height: 24px !important;
        }
	    .choose-dragon-apparel-slots {
	    margin-left: 10px !important;
	    }
	    .common-tab {
	    border-color: #d5cfc2 !important;
	    border-top-left-radius: 5px !important;
        border-top-right-radius: 10px !important;
	    }
        .common-tab-big {
        height: 24px !important;
        }
        .common-tab-big a {
        padding: 2px 12px !important;
        }
        .common-tab-big .common-tab-icon, .common-tab-big .common-tab-name, .common-tab-big .common-tab-count, .common-tab-big .common-tab-center {
        height: 24px !important;
        line-height: initial !important;
        }
        .common-tab-big .common-tab-center img {
        display: none;
        }
        #choose-apparel-apparel-button.common-tab-big a {
        height: 100% !important;
        }
        .common-pagination .common-pagination-page-active, .common-pagination .common-pagination-page-active:hover, .common-pagination .common-pagination-page {
        border: none !important;
        }
}
