}

.list-tsearch {
    border: 1px solid #ddd;
    background: #000000;
    padding: 20px 15px 3px;
    margin-bottom: 15px;
}

.list-tsearch .text-search > select {
    padding: 0 3px;
    margin-right: 5px;
    background-color: #1a1a1a;
}

.list-tsearch .categories input {
    border: 1px solid #ccc;
