}

.progress-bar {
    float: left;
    width: 0;
    height: 100%;
    font-size: 15px;
    line-height: 20px;
    color: #fff;
    text-align: center;
    background-color: #ff6a00;
    -webkit-box-shadow: inset 0 -1px 0 rgba(0,0,0,.50);
    box-shadow: inset 0 -1px 0 rgba(0,0,0,.50);
    -webkit-transition: width .6s ease;
    -o-transition: width .6s ease;
    transition: width .6s ease;
}


.widget-index .at-main, .widget-index .at-side {
