style.css:
@-moz-document url-prefix("https://www.solsticegamestudios.com/") {
html {
    color:#fff;
    background:#222;
}
.panel-body {
    background-color: #222222;
}
.breadcrumb {
    color: rgb(20, 20, 20);
    background-color: #111;
}
.node .nodeIcon {
    color: #eee;
}
.nodeList .categoryStrip {
    color: rgb(255, 255, 255);
    border-top: 1px solid #1af;
    border-left: 1px solid #1af;
    border-right: 1px solid #1af;
    border-bottom: 1px solid #1af;
    background-color: #222;
}
.messageText {
    color: #fff
}
.discussionListItem .stats .major {
    color: #fff
}
.discussionListItem .stats .minor {
    color: #fff
}
.discussionListItem .lastPostInfo .username {
    color: #fff
}
.sectionFooter {
    color: #111;
    background-color: #222
}
.node .nodeDescription {
    color: #aaa
}
.node .nodeLastPost {
    color:#aaa
}
.pageContent {
    background-color: #222
}
body {
    color: #aaa
}
a:link, a:visited {
    color: #ddd
}
.nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo {
    background-color: #222;
}
.secondaryContent {
    background-color: #222;
}
#content .pageContent {
    background-color: #222;
    border-top: 1px solid #ccc;
    border-bottom: 1px solid #ccc;
    border-left: 1px solid #ccc;
    border-right: 1px solid #ccc;
}
.discussionListItem {
    background-color: #222;
    border-bottom: 1px solid rgb(235, 235, 235);
}
.sectionFooter {
    color: #222;
}
.textCtrl {
    font-size: 13px;
    font-family: Calibri, 'Trebuchet MS', Verdana, Geneva, Arial, Helvetica, sans-serif;
    color: #fff;
    background-color: #000;
}
.Menu .primaryContent {
    background-color: #111;
}
.Menu .secondaryContent {
    background-color: #111;
}
body .muted, body a.muted, body .muted a {
    color: rgb(113, 113, 113);
}
.Menu .sectionFooter {
    background-color: #222;
}
.primaryContent {
    background-color: #222;
}
.profilePage .tabs.mainTabs {
background: #222;
}
.profilePage .tabs.mainTabs li.active a {
    background-color: #ddd;
}
.messageList .message {
    background-color: #222;
}
.messageUserBlock div.avatarHolder {
    background-color: #111;
}
.messageUserBlock .arrow span {
    border-left-color: #111;
}
.messageUserBlock h3.userText {
    border-top: 1px solid #111;
    border-bottom: 1px solid #111;
    background-color: #111;
}
.messageUserBlock .extraUserInfo dl {
background-color: #111
}
.messageUserBlock .extraUserInfo {
background-color: #111;
}
.messageUserBlock .userTitle {
background-color: #111;
}
.node .tinyIcon {
    color: #aaa;
}
.textCtrl:focus, .textCtrl.Focus {
    background-color: #111;
}
.quickReply {
    background-color: #111;
}
.redactor_toolbar {
    background-color: #111;
}
body {
    color: #aaa;
    background-color: #222;
}
.panel-default > .panel-heading {
    color: #111;
    background-color: #111;
    border-color: #ddd;
}
.panel-footer {
    background-color: #111;
    border-top: 1px solid #ddd;
}
.navbar-inverse .navbar-collapse, .navbar-inverse .navbar-form {
    border-color: #222;
}
.tabs {
    background-color: #222;
}
.footer, .footerLegal {
	background-color: #222;
}
.bottomHelper {
    background-color: #222;
}
.PageNav a {
    color: #aaa;
    background-color: #111;
}
.thread_view .threadAlerts dd {
    color: #aaa;
}
.hasJs .bbCodeSpoilerText {
    background-color: #111;
}
.bbCodeQuote .quoteContainer .quote {
	background-color: #111
}
.bbCodeQuote .quoteContainer {
    background-color: #111;
}
.bbCodeBlock .type {
    color: #aaa;
    background-color: #111;
}
}
