style.css:
@-moz-document url-prefix("https://gest-hordes.eragaming.fr/") {
/* GLOBAL */
body {
    background-image: url(http://data.hordes.fr/gfx/design/bg_big2.jpg);
    background-size: contain;
    background-repeat: no-repeat;
    background-position: top center;
    background-color: #100C0B;
    font-size: 1em;
    height: auto;
    margin: auto;
    width: 1300px;
    font-family: Arial, sans-serif;
}
strong, b {
    color: #fff;
}
#formOption {
    border: none;
}
/* ACTU GH */
#corpsNews {
    background-color: #5c2b20;
    border: 1px solid #eca557;
    outline: 2px solid #2d1b0f;
    color: white;
    padding: 0 15px;
}
#entetecorps {
    width:100%;
}
#corps {
    font-size: 1em;
    margin: 0 40px 20px 40px;
    color: #ECB98A;
    background: #5C2B20;
    border: 1px solid #ECB98A;
    padding: 10px 15px;
}
.PanneauElements {
    background: #5C2B20;
    border: 1px solid #ECB98A;
    color: #F0D79E;
}
/* POP UP */
.PopAvert {
    background-color: #965c36;
    border: none;
    color: white;
    font-variant: all-small-caps;
}
#retourAjax p {
    background: transparent;
}
#informationImportante p, #popUpEspacePerso p, #popupconfirmationB p, #retourAjax p, #textPopUpNewsSite {
    background: transparent;
}
#menuSite {
    margin: auto;
}
/* LISTE DES VILLES */
.boutonJ {
    background: #965c36;
    font-size: 0.8em;
    font-weight: bold;
    font-variant: small-caps;
    color: white;
    border: 1px solid #3e2417;
}
#menuSite, #menuSite ul {
    width: 82%;
    display: flex;
    flex-flow: row;
    justify-content: space-evenly;
}
#menuSite li>ul {
    display: flex;
    flex-flow: column;
}
section {
    background-image: url(http://data.hordes.fr/gfx/design/content_bg.jpg);
    background-position: 0px 155px;
    background-size: 100%;
    background-repeat: repeat-y;
    background-color: #7e4d2a;
    border-left: 1px solid black;
    border-right: 1px solid black;
    width: 1300px;
    margin: auto;
}
a {
    color: #f0d79e;
    font-variant: all-small-caps;
    outline: 0;
}
/* PROFIL */
#Espace_perso {
    background: #5C2B20;
    border: 1px solid #ECB98A;
    color: #F0D79E;
}
#Espace_perso .fa {
    margin-left: -5px;
}
footer {
    background: #5C2B20;
    border: 1px solid #ECB98A;
    color: #F0D79E;
}
#entetecorps h1 {
    width: 100%;
}
#entetecorps img, #piedcorps img {
    display: none;
}
/* CARTE */
#form_carte, #form_option, #form_expe {
