style.css:
/*! Darkbit - Dark mode para o Promobit v0.3.1 */
/*! Repository: https://github.com/mathcale/darkbit */
/*! License:    https://creativecommons.org/licenses/by/4.0/ */
/*! Author:     Matheus Calegaro <hello@matheus.me> */

@-moz-document domain("promobit.com.br") {
  /* General */
  :root {
    --promobit-yellow: #faba01;
    --promobit-yellow-dimmed: #faba014a;
    --background-color: #303030;
    --subheader-color: #424242;
    --divider-color: rgba(255, 255, 255, 0.12);
    --text-primary-color: #ffffff;
    --text-secondary-color: rgba(255, 255, 255, 0.7);
    --text-tertiary-color: rgba(255, 255, 255, 0.5);
    --icon-color: #ffffff;
    --search-input-color: rgba(255, 255, 255, 0.09);
    --search-input-focus-color: #ffffff;
    --search-input-text-color: #4c4c4c;
    --header-tab-color: var(--search-input-color);
    --header-tab-active-color: #424242;
    --subheader-button-transparent-color: var(--search-input-color);
    --general-button-color: var(--search-input-color);
    --sidebar-color: var(--subheader-color);
    --timeline-card-color: var(--subheader-color);
    --timeline-card-title-color: #565656;
    --offer-card-color: var(--subheader-color);
    --offer-card-bottom-color: var(--divider-color);
    --forum-thread-container-color: #4f4f4f;
    --forum-fixed-thread-color: var(--offer-card-color);
  }

  body {
    background: var(--background-color);
  }

  .in-alert {
    background: linear-gradient(90deg, #faba01, #b78a07);
    background-size: 400% 400%;
    animation: MovingAlertBg 3s ease infinite;
  }

  .banner-cta {
    box-shadow: none;
  }

  /* Header */
  .pr-top {
    background: var(--background-color);
  }

  .pr-top .pr-menu-tabs .pr-tab-item,
  .pr-top .pr-menu-tabs .pr-tab-item:hover {
    background-color: var(--header-tab-color);
  }

  .pr-top .pr-menu-tabs .pr-tab-item:hover {
    box-shadow: 0 -2px 1px var(--promobit-yellow);
  }

  .pr-top .pr-menu-tabs .pr-tab-item .pr-label__container {
    color: var(--text-secondary-color);
  }

  .pr-top .pr-menu-tabs .pr-tab-item.active {
    background-color: var(--header-tab-active-color);
    border: 1px solid var(--divider-color);
    border-bottom: none;
    box-shadow: 0 -2px 1px var(--promobit-yellow);
  }

  /* "Term" nao tem H :p */
  .pr-top .pr-pagemenu-search .search_therm {
    background-color: var(--search-input-color);
    color: var(--text-secondary-color);
  }

  .pr-top .pr-pagemenu-search .search_therm.focus {
    background-color: var(--search-input-focus-color);
    color: var(--search-input-text-color);
  }

  .pr-top .pr-pagemenu-search .search_submit {
    background-color: transparent;
    border: 1px solid var(--divider-color);
  }

  .pr-top .pr-pagemenu-search .search_submit:hover {
    background-color: var(--divider-color);
  }

  .pr-pagemenu {
    background: var(--subheader-color);
    border-bottom-color: var(--divider-color);
  }

  .pr-pagemenu .pr-submenu-item {
    color: var(--text-secondary-color);
  }

  .pr-pagemenu .pr-submenu-item:hover {
    font-weight: bold;
    color: var(--text-tertiary-color);
  }

  .pr-dd-container {
    background: var(--subheader-color);
    border-color: var(--divider-color);
  }

  .pr-pagemenu .pr-submenu-item .pr-dd-container .pr-submenu-categories .cat-item {
    border-bottom-color: var(--divider-color);
  }

  .pr-pagemenu .pr-submenu-item .pr-dd-container .pr-submenu-categories .cat-item a,
  .pr-pagemenu .pr-submenu-item .pr-dd-container .pr-submenu-categories .cat-item h4 {
    color: var(--text-primary-color);
  }

  .pagemenu__btn--transparent {
    border-color: var(--divider-color);
    color: var(--text-primary-color);
  }

  .pagemenu__btn--transparent:hover {
    background-color: var(--subheader-button-transparent-color);
  }

  .pagemenu__btn--transparent .pagemenu__btn--icon {
    border-right-color: var(--divider-color);
  }

  .pagemenu__btn--icon .fa-heart {
    color: var(--icon-color);
  }

  .pagemenu__btn--green {
    background: transparent;
    border: 1px solid var(--promobit-yellow);
  }

  .pagemenu__btn--green:hover {
    background: var(--promobit-yellow);
  }

  .pagemenu__btn--icon-green {
    border-right: 1px solid var(--promobit-yellow);
  }

  .pagemenu__btn--green:hover .pagemenu__btn--icon-green {
    border-right-color: #ffffff;
  }

  .pagemenu__btn--icon-green .fa-plus,
  .pagemenu__btn--offer {
    color: var(--promobit-yellow);
  }

  .pagemenu__btn--green:hover .pagemenu__btn--icon-green .fa-plus,
  .pagemenu__btn--green:hover .pagemenu__btn--offer {
    color: #ffffff;
  }

  /* Offer Timeline */
  .pr-tl .pr-tl-banner h1 {
    color: var(--text-secondary-color);
  }

  .tooltip-last-offers {
    color: var(--text-secondary-color);
  }

  .tooltip-last-offers:hover {
    color: var(--text-tertiary-color);
  }

  .timeline-opt__btn {
    background-color: var(--general-button-color);
    border-color: var(--divider-color);
    color: var(--text-secondary-color);
  }

  .timeline-opt__btn:hover {
    background-color: var(--divider-color);
  }

  .pr-tl .pr-tl-card,
  .pr-tl .pr-tl-card .product_image,
  .pr-tl .pr-tl-card .rating {
    background-color: var(--timeline-card-color);
  }

  .pr-tl .pr-tl-card .info .oldprice,
  .pr-tl .pr-tl-card .where,
  .pr-tl .pr-tl-card .info .comments-box {
    color: var(--text-tertiary-color);
  }

  .pr-tl .list .pr-tl-card {
    border-top: 10px solid var(--timeline-card-color);
  }

  .pr-tl .pr-tl-card .bottom_title {
    background-color: var(--divider-color);
    border-bottom: 1px solid var(--divider-color);
  }

  .pr-tl .pr-tl-card .bottom_title h2 a {
    color: var(--text-secondary-color);
  }

  .pr-tl .pr-tl-card .bottom_title:hover {
    background-color: var(--timeline-card-title-color);
  }

  .pr-tl .pr-tl-card .bottom_title:hover .pr-tl .pr-tl-card .bottom_title h2 {
    color: var(--text-primary-color);
  }

  .pr-tl .pr-tl-card .info .price {
    color: var(--promobit-yellow);
  }

  .pr-tl .pr-tl-card .cardpub .name {
    color: var(--text-secondary-color);
  }

  .pr-tl .pr-tl-card .cardpub .timeago {
    color: var(--text-tertiary-color);
  }

  .bt-s-default {
    background-color: var(--general-button-color);
    border-color: var(--divider-color);
    color: var(--text-secondary-color);
  }

  .bt-s-default:hover {
    background-color: var(--divider-color);
  }

  .bt-s-default .divider {
    background-color: var(--divider-color);
  }

  .prs-box-topoffers .pr-cs-item .offer-likes {
    background-color: var(--general-button-color);
    border-color: var(--divider-color);
    color: var(--text-secondary-color);
  }

  .prs-box-topoffers .pr-cs-item .offer-likes strong {
    border-right: 1px solid var(--divider-color);
  }

  /* Offer page */
  .offer-card {
    background-color: var(--offer-card-color);
    color: var(--text-primary-color);
  }

  .offer-card__main,
  .offer-card__side-content {
    background-color: var(--offer-card-color);
  }

  .offer-card__main {
    border-left: 1px solid var(--divider-color);
  }

  .offer-card__price {
    color: var(--text-tertiary-color);
  }

  .offer-card__sale-price {
    color: var(--promobit-yellow);
  }

  .offer-card__store-infos {
    color: var(--text-secondary-color);
  }

  .offer-card__store-site {
    color: var(--text-tertiary-color);
  }

  .offer-card__bottom-bar {
    background-color: var(--offer-card-bottom-color);
    border-top-color: var(--divider-color);
    color: var(--text-tertiary-color);
  }

  .offer-card__report-dp {
    background-color: var(--offer-card-bottom-color);
  }

  .offer-card__report-item {
    background-color: var(--offer-card-color);
    border: 1px solid var(--divider-color);
  }

  .offer-card__report-item:hover {
    background-color: var(--timeline-card-title-color);
  }

  .author-review__title {
    color: var(--text-secondary-color);
  }

  .author-review__header {
    border-bottom-color: var(--divider-color);
  }

  .pr-offerPage .top-mark.green {
    background-color: #94B62C;
  }

  .author-review {
    background-color: var(--sidebar-color);
    color: var(--text-secondary-color);
  }

  .author-review__info {
    background-color: var(--offer-card-bottom-color);
  }

  .author-review__stats {
    border-left-color: var(--divider-color);
  }

  .author-review__stat {
    border-right-color: var(--divider-color);
  }

  .author-review__date {
    border-bottom-color: var(--divider-color);
  }

  .wl-box-suggestions {
    background-color: var(--sidebar-color);
    color: var(--text-secondary-color);
  }

  .prs-sidebarLayout .prs-box h3 {
    color: var(--text-secondary-color);
  }

  .pr-offerPage .pr-of-gr {
    background: var(--sidebar-color);
  }

  .pr-of-comments .pr-of-comment-box {
    border-color: var(--divider-color);
  }

  /* WONTFIX: style prop on element has greater specificity */
  .pr-of-comments .pr-of-comment-box textarea {
    background: var(--search-input-color) !important;
  }

  .pr-of-comments .pr-of-mark,
  .pr-offerPage .pr-of-gr .pr-of-gr {
    background: var(--offer-card-bottom-color);
  }

  .pr-of-comments .pr-of-mark,
  .pr-of-comments .pr-of-mark.pr-of-comment-preview {
    border-color: var(--divider-color);
  }

  .pr-of-comments .pr-of-mark .fa,
  .pr-of-comments .pr-of-mark.pr-of-comment-preview .fa {
    color: var(--text-secondary-color);
  }

  .pr-of-comments__titles-bottom,
  .pr-of-comments__user-name {
    color: var(--text-secondary-color);
  }

  .pr-of-comments .pr-of-comment-block .pr-of-comment-textbox .pr-of-comment-text {
    background: var(--offer-card-bottom-color);
    border-color: var(--divider-color);
  }

  .pr-of-comments .pr-of-comment-block .pr-of-comment-textbox p {
    color: var(--text-primary-color);
  }

  .pr-of-comments .pr-of-comment-block .pr-of-comment-textbox p .profile,
  .pr-of-comments .pr-of-comment-block .pr-of-comment-textbox p .profile:before,
  .pr-of-comments__opt,
  .pr-of-comments__p a {
    color: var(--promobit-yellow);
  }

  .pr-of-comments .pr-of-comment-block .pr-of-comment-textbox p .profile:hover,
  .pr-of-comments .pr-of-comment-block .pr-of-comment-textbox p .profile:hover ~:hover,
  .pr-of-comments__opt:hover,
  .pr-of-comments__p a:hover {
    color: var(--promobit-yellow);
  }

  .pr-of-comments__like-counter {
    color: var(--text-tertiary-color);
  }

  .pr-of-comments__dp-menu-container {
    background-color: var(--offer-card-color);
    border: 1px solid var(--divider-color);
    color: var(--text-secondary-color);
  }

  .pr-of-comments__dp-menu-item {
    border-top-color: var(--divider-color);
  }

  .pr-of-comments__dp-menu-item:hover {
    background-color: var(--offer-card-bottom-color);
  }

  .pr-of-comments__dp-menu-icon {
    color: var(--text-secondary-color);
    border-right-color: var(--divider-color);
  }

  .pr-of-comments__titles {
    color: var(--text-tertiary-color);
  }

  .pr-of-info.prs-box a,
  .pr-of-info.prs-box a:visited {
    color: var(--promobit-yellow);
  }

  /* Forum */
  .forum,
  .forum .details__container {
    background-color: var(--subheader-color);
    border-bottom-color: var(--divider-color);
    box-shadow: none;
    color: var(--text-tertiary-color);
  }

  .forum__header,
  .paginator-bottom .pages__container,
  .thread-content,
  .forum .pr-of-comments,
  .paginator--bottom .pages__container, .paginator--top .pages__container {
    background-color: var(--offer-card-color);
    border-bottom-color: var(--divider-color);
  }

  .paginator--bottom .pages__container {
    border-top-color: var(--divider-color);
  }

  .pages__container .stepper--start {
    color: var(--text-tertiary-color);
  }

  .forum__header-title {
    color: var(--text-secondary-color);
  }

  .paginator-header .stepper--start {
    color: var(--text-tertiary-color);
  }

  .pages__container .stepper {
    background-color: var(--offer-card-bottom-color);
    border-color: var(--divider-color);
    color: var(--text-secondary-color);
  }

  .pages__container .stepper:hover {
    background-color: var(--divider-color);
    color: var(--text-tertiary-color);
  }

  .pages__container .stepper-active {
    background-color: #A78B5B;
    border: 1px solid #A78B5B;
    color: var(--text-primary-color);
  }

  .pages__container .stepper-active:hover {
    background-color: #93794c;
    border: 1px solid #93794c;
  }

  .forum__thread-container,
  .thread-content__paragraph {
    background-color: var(--forum-thread-container-color);
    border-color: var(--divider-color);
  }

  .forum__thread-container--fixed {
    background-color: var(--forum-fixed-thread-color);
  }

  .user-avatar__image {
    border-color: var(--divider-color);
  }

  .user-avatar__badge-adm {
    border-color: var(--promobit-yellow);
    color: var(--promobit-yellow);
  }

  .forum__thread-title,
  .forum .details__container .forum__thread-title {
    color: var(--promobit-yellow);
  }

  .forum__thread-container:hover .forum__thread-title {
    color: var(--promobit-yellow);
  }

  .forum__thread-user,
  .forum__thread-user a {
    color: var(--text-secondary-color);
  }

  .forum__tag {
    background-color: var(--divider-color);
    border-color: var(--divider-color);
    color: var(--text-secondary-color)
  }

  .forum__tag:hover {
    background-color: var(--divider-color);
  }

  .forum__divider-edge,
  .forum__divider-circle {
    background-color: var(--divider-color);
  }

  .forum__last-update-photo {
    border-color: var(--promobit-yellow);
  }

  .forum__last-update-name {
    color: var(--text-secondary-color);
  }

  .forum__last-update-time {
    color: var(--text-tertiary-color);
  }

  .forum__pinned {
    background-color: var(--promobit-yellow);
    color: #333;
  }

  .forum .thread-content__paragraph {
    color: var(--text-secondary-color)
  }

  .forum .thread-content a,
  .forum .thread-content a:hover {
    color: var(--promobit-yellow);
  }

  .forum .thread-content a:hover {
    text-decoration: underline;
  }

  .pr-of-comments__dp-menu-label--delete {
    color: var(--promobit-yellow);
  }

  .forum .slim-title {
    border-top-color: var(--divider-color);
    color: var(--text-secondary-color);
  }

  .prs-page_heading {
    color: var(--text-secondary-color) !important;
  }

  .user-profile-activity__content {
    background-color: var(--offer-card-color) !important;
  }

  .tabbed-nav {
    border-bottom-color: var(--divider-color);
  }

  .tabbed-nav__tab {
    background-color: var(--subheader-color) !important;
    color: var(--text-secondary-color);
  }

  .tabbed-nav__tab--active {
    color: var(--promobit-yellow);
  }

  .tabbed-nav__tab--active:after,
  .tabbed-nav__tab:not(.tabbed-nav__tab--active):hover:after {
    background-color: var(--promobit-yellow);
  }

  .user-profile-activity__comment-box {
    background-color: var(--offer-card-bottom-color);
    border-color: var(--divider-color);
    color: var(--text-secondary-color);
  }

  .user-profile-activity__comment-box:hover {
    background-color: var(--divider-color);
  }

  .user-profile-activity__from-text,
  .user-profile-activity__from-text:hover {
    color: var(--promobit-yellow);
  }

  .timeago {
    color: var(--text-tertiary-color);
  }

  /* Coupons */
  .coupons-carousel,
  .coupons-search {
    background-color: var(--offer-card-color);
  }

  .coupons-search__subtitle,
  .index-search p {
    margin-bottom: 5px;
    color: var(--text-tertiary-color);
  }

  .coupons-search .index-search input,
  .coupons-carousel .index-search input {
    background-color: var(--search-input-color);
    border-color: var(--divider-color);
    color: var(--text-secondary-color);
  }

  .coupons-dividerLine {
    background-color: var(--divider-color);
  }

  .coupons-carousel h1,
  .coupons-carousel h2 {
    color: var(--text-secondary-color);
  }

  .carousel-container .slick-next,
  .carousel-container .slick-prev {
    background-color: var(--forum-thread-container-color);
    border: 1px solid var(--divider-color);
    transition: all 300ms ease;
  }

  .carousel-container .slick-next:hover,
  .carousel-container .slick-prev:hover,
  .carousel-container .slick-next:focus,
  .carousel-container .slick-prev:focus {
    background-color: var(--forum-fixed-thread-color);
  }

  .carousel-container .slick-prev:before,
  .carousel-container .slick-next:before,
  .carousel-container .slick-prev:not(.slick-disabled):hover:before,
  .carousel-container .slick-next:not(.slick-disabled):hover:before {
    color: var(--promobit-yellow);
  }

  .slick-prev.slick-disabled:before,
  .slick-next.slick-disabled:before {
    color: var(--promobit-yellow-dimmed);
  }

  .carousel-container .coupon_store-item {
    background-color: var(--offer-card-color);
    border-color: var(--divider-color);
    color: var(--text-secondary-color);
  }

  .carousel-container .coupon_store-item .image__container {
    background-color: var(--offer-card-bottom-color);
    border-bottom-color: var(--divider-color);
  }

  .carousel-container .coupon_store-item .image__container img {
    border-color: var(--divider-color);
  }

  .carousel-container .coupon_store-item h3 {
    color: var(--text-secondary-color);
  }

  .carousel-container .item p,
  .carousel-container .coupon_store-item .no-coupons {
    color: var(--text-tertiary-color);
  }

  .carousel-container .item:hover .coupon_store-item,
  .carousel-container .item:hover h3,
  .carousel-container .item:hover p {
    border-color: var(--promobit-yellow);
    color: var(--promobit-yellow);
  }

  .carousel-container .slick-dots li.slick-active button {
    background-color: var(--promobit-yellow);
  }

  .carousel-container .slick-dots li.slick-active button:hover {
    background-color: var(--promobit-yellow-dimmed);
  }

  .coupons-carousel h1,
  .coupons-carousel h2 {
    border-bottom-color: var(--divider-color);
  }

  .last-added h2 a.small,
  .side-intro .btn-saiba_mais {
    color: var(--promobit-yellow);
  }

  .last-added h2 a.small:hover,
  .side-intro .btn-saiba_mais:hover {
    color: var(--promobit-yellow-dimmed);
  }

  .last-added .last-added-container {
    background-color: var(--offer-card-bottom-color);
  }

  .last-added .last-added-item--b {
    border-color: var(--divider-color);
    color: var(--text-secondary-color);
  }

  .border-gray-3 {
    border-color: var(--divider-color);
  }

  .last-added .last-added-item--b .img-link {
    color: var(--text-tertiary-color);
  }

  .last-added .last-added-item--b .coupon-title {
    color: var(--promobit-yellow);
  }

  .last-added .coupons-more {
    color: var(--promobit-yellow);
  }

  .last-added .coupons-more:hover {
    color: var(--promobit-yellow-dimmed);
  }

  .coupons-carousel .index-search button {
    background-color: #A78B5B;
    border-color: #A78B5B;
  }

  .all-stores .stores-nav {
    background-color: var(--forum-fixed-thread-color);
    border-color: var(--divider-color);
    color: var(--promobit-yellow);
  }

  .coupon-index {
    color: var(--promobit-yellow);
  }

  .coupon-index.active,
  .coupon-index:not(.inactive):hover {
    background-color: var(--promobit-yellow);
    color: #333;
  }

  .index__container .index-letter {
    color: var(--text-tertiary-color);
  }

  .index__container .list-item.act a {
    color: var(--promobit-yellow);
  }

  .index__container .list-item.act a .count {
    border-color: var(--promobit-yellow);
  }

  .index__container .list-item.act:hover a {
    color: var(--promobit-yellow-dimmed);
  }

  .index__container .list-item.act:hover .count {
    border-color: var(--promobit-yellow-dimmed);
  }

  .prs-text-content h2 {
    border-bottom-color: var(--divider-color);
    color: var(--text-secondary-color);
  }

  .prs-text-content h3,
  .prs-text-content h4 {
    border-bottom-color: var(--divider-color);
    color: var(--text-tertiary-color);
  }

  .prs-text-content p {
    color: var(--text-tertiary-color);
  }

  .prs-text-content a {
    color: var(--promobit-yellow);
  }

  .prs-text-content a:hover {
    color: var(--promobit-yellow);
  }

  .pr-ccode .seocoupon-title,
  .pr-ccode h1 {
    color: var(--text-secondary-color);
  }

  .coupon-card {
    box-shadow: none;
    color: var(--text-tertiary-color);
  }

  .coupon-card__main {
    background-color: var(--forum-fixed-thread-color);
  }

  .coupon-card__divider-edge,
  .coupon-card__divider-circle {
    background-color: var(--divider-color);
  }

  .coupon-card__footer {
    background-color: var(--forum-thread-container-color);
  }

  .pr-ccode .coupon-title,
  .pr-tl-coupon__title {
    color: var(--text-secondary-color);
  }

  .text-btn {
    color: var(--promobit-yellow);
  }

  .text-btn:hover {
    color: var(--promobit-yellow-dimmed);
  }

  /* Footer */
  .pr-footer {
    background: var(--background-color);
  }

  .pr-footer .pr-footer-center .about-block {
    background-color: var(--offer-card-bottom-color);
    box-shadow: 0 -20px var(--offer-card-bottom-color);
  }

  .pr-footer .pr-footer-center .about-block p {
    color: var(--text-secondary-color);
  }

  .pr-footer .pr-footer-center .left {
    border-left-color: var(--divider-color);
  }

  .pr-footer .pr-footer-center .left h4 {
    color: var(--text-secondary-color);
  }

  .pr-footer .pr-footer-center .left a {
    color: var(--text-tertiary-color);
  }

  .pr-footer .pr-footer-stores {
    background: var(--offer-card-bottom-color);
    color: var(--text-tertiary-color);
  }

  /* Buttons */
  .bt-s-primary {
    background-color: #94B62C;
    border: 1px solid #94B62C;
  }

  .bt-hvr-s-primary:hover {
    background-color: #728f19 !important;
    border-color: #728f19 !important;
  }

  .bt-s-secondary {
    background-color: #A78B5B;
    border-color: #A78B5B;
  }

  .bt-s-secondary .divider {
    background-color: var(--text-tertiary-color);
  }

  .bt-s-secondary:hover,
  .bt-hvr-s-secondary:hover {
    background-color: #93794c !important;
    border-color: #93794c !important;
  }

  .bt-s-secondary:hover .divider,
  .bt-s-secondary--set .divider,
  .bt-s-secondary--set:hover .divider {
    background-color: var(--divider-color);
  }

  .cat-suggestions__wrapper {
    border-color: var(--divider-color);
  }

  .cat-suggestions .bt-wl:hover {
    background-color: var(--divider-color) !important;
  }

  .bt-hvr-s-active:hover {
    background-color: var(--divider-color) !important;
    border-color: var(--divider-color) !important;
  }

  /* Sidebars */
  .prs-sidebarLayout .prs-box,
  .ranking-list,
  .ranking-list__top {
    background: var(--sidebar-color);
    border-top: 1px solid var(--divider-color);
    box-shadow: none;
    color: var(--text-secondary-color);
  }

  .prs-box.pr-of-spbox.px3.py4 {
    color: var(--text-secondary-color);
  }

  .prs-lastActions.prs-box h3 {
    color: var(--text-secondary-color);
  }

  .prs-box-topoffers .pr-cs-item {
    border-top: 1px solid var(--divider-color);
  }

  .prs-box-topoffers .pr-cs-item .title {
    color: var(--text-secondary-color);
  }

  .prs-box-topoffers .pr-cs-item .price__container {
    color: var(--promobit-yellow);
  }

  .prs-sidebarLayout .prs-box .box__btn-bottom {
    border-top: 1px solid var(--divider-color);
    color: var(--text-primary-color);
  }

  .prs-sidebarLayout .prs-box .box__btn-bottom:hover {
    color: var(--promobit-yellow);
  }

  .sidebar__last-coupons .feed-item {
    min-height: 130px;
    border-top: 1px solid var(--divider-color);
  }

  .sidebar__last-coupons .coupon-focus .coupon-amount {
    background: var(--promobit-yellow);
    border: 1px solid var(--promobit-yellow);
  }

  .sidebar__last-coupons .coupon-focus .coupon-amount-label {
    margin-top: 5px;
    color: var(--promobit-yellow);
  }

  .sidebar__last-coupons .last-coupon__desc strong {
    padding: 7px 10px 0 10px;
    color: var(--text-secondary-color);
  }

  .sidebar__last-coupons .last-coupon__desc button {
    background-color: transparent;
    border-color: var(--divider-color);
    color: var(--text-primary-color);
  }

  .sidebar__last-coupons .last-coupon__desc button:hover {
    background-color: var(--promobit-yellow);
  }

  .sidebar__last-coupons .last-coupon__desc small {
    top: 3px;
    color: var(--text-tertiary-color);
  }

  .cat-suggestions {
    background-color: var(--sidebar-color);
    border-top: 1px solid var(--divider-color);
    color: var(--text-secondary-color);
  }

  .cat-suggestions__title {
    color: var(--text-secondary-color);
  }

  .prs-store_offer_count {
    background: var(--sidebar-color);
    border-top-color: var(--divider-color);
  }

  .prs-store_offer_count .prs-related_count--item .prs-box-count {
    background: var(--divider-color);
    color: var(--text-secondary-color);
  }

  .prs-store_offer_count .prs-related_count--item .prs-box-count-desc {
    color: var(--text-tertiary-color);
  }

  .prs-lastActions.prs-box h3.border-bottom {
    border-bottom-color: var(--divider-color);
  }

  .sidebar__rank .feed-item .rank__user {
    border-bottom-color: var(--divider-color);
  }

  .sidebar__rank .feed-item .rank__pos,
  .ranking-list__item-position {
    background: var(--divider-color);
    border-bottom-color: var(--divider-color);
    border-left-color: var(--promobit-yellow);
    color: var(--text-secondary-color);
  }

  .sidebar__rank .feed-item .rank__user .rank__details strong,
  .ranking-list__item-name {
    color: var(--text-secondary-color);
  }

  .sidebar__rank .feed-item .rank__user .rank__details p {
    color: var(--text-tertiary-color);
  }

  .ranking-list__item {
    border-bottom-color: var(--divider-color);
  }

  .sidebar__rank-dropdown {
    color: var(--promobit-yellow);
  }

  .side-intro h1 {
    border-left-color: var(--promobit-yellow);
    color: var(--promobit-yellow);
  }

  .side-intro p {
    color: var(--text-tertiary-color);
  }

  .prs-horizontalNav .prs-horizontalNavContent .item {
    border-right-color: var(--divider-color);
    color: var(--text-secondary-color);
  }

  .prs-horizontalNav .prs-horizontalNavContent .item.active,
  .prs-horizontalNav .prs-horizontalNavContent .item.active:hover {
    background-color: var(--divider-color);
  }

  .prs-r .side-store h4 {
    color: var(--text-tertiary-color);
  }

  .prs-r .side-store p {
    margin: 10px 20px;
  }

  .prs-box-topoffers .seeall-offers {
    background-color: var(--forum-thread-container-color);
    color: var(--text-tertiary-color);
  }

  .prs-sidebarLayout .prs-lastActions .small-store_list-subtitle {
    color: var(--text-tertiary-color);
  }

  .prs-sidebarLayout .prs-lastActions .small-store_list-container {
    border-top-color: var(--divider-color);
  }

  .prs-sidebarLayout .prs-lastActions a.small-store-item {
    background-color: var(--divider-color);
    border-color: var(--divider-color);
    color: var(--text-secondary-color);
    transition: all 300ms ease;
  }

  .prs-sidebarLayout .prs-lastActions a.small-store-item:hover {
    background-color: var(--subheader-color);
  }

  .prs-sidebarLayout .pr-of-adds {
    background-color: var(--subheader-color);
  }

  .sidebar__post-categories .feed-item {
    border-top-color: var(--divider-color);
    color: var(--text-secondary-color);
  }

  .sidebar__post-categories .feed-item strong {
    color: var(--text-secondary-color);
  }

  .sidebar__post-categories .feed-item p {
    color: var(--text-tertiary-color);
  }

  .pr-of-top-users .pr-of-top-users-box a .item {
    border-bottom-color: var(--divider-color);
  }

  .pr-of-top-users .pr-of-top-users-box a .name {
    color: var(--text-secondary-color);
  }

  .pr-of-top-users .pr-of-top-users-box a:hover,
  .pr-of-top-users .pr-of-top-users-box a .item.darker {
    background-color: var(--forum-thread-container-color);
  }

  /* Blog */
  /* FIXME: implement actual blog styles */
  .single-post .pr-of-comments .pr-of-comment-block .pr-of-comment-textbox p,
  .single-post .pr-of-comments__titles-bottom,
  .single .pr-of-comments__user-name,
  .single .timeago {
    color: initial;
  }

  .single .pr-of-comments .pr-of-comment-box textarea {
    background: initial !important;
    border-color: var(--divider-color);
  }

  .home.page-template-default .prs-box-topoffers .pr-cs-item .title,
  .home.page-template-default .prs-box-topoffers .pr-cs-item .offer-likes,
  .home.page-template-default .sidebar__last-coupons .last-coupon__desc strong {
    color: initial;
  }

  .home.page-template-default .sidebar__last-coupons .last-coupon__desc button {
    background: var(--promobit-yellow);
  }

  /* Other pages */
  .breadcrumb__nav {
    color: var(--text-tertiary-color);
  }

  .breadcrumb__link {
    color: var(--text-secondary-color);
  }

  .landing_page .prs-sidebarLayout .article,
  .landing__black-friday-original .prs-sidebarLayout h1,
  .landing__black-friday-original .infos-bf {
    color: var(--text-primary-color);
  }

  .landing__black-friday-original .article p {
    color: var(--text-secondary-color);
  }

  .landing__black-friday-original .infos-bf__header-2 {
    border-bottom: 1px solid var(--divider-color);
  }

  .landing__black-friday-original .infos-bf a {
    color: var(--promobit-yellow);
  }

  .landing_page .prs-sidebarLayout h1 {
    color: var(--text-secondary-color);
  }

  .pr-post.prs-box p {
    color: var(--text-tertiary-color)
  }

  /* Animations */
  @keyframes MovingAlertBg {
    0% {
      background-position: 0% 50%;
    }

    50% {
      background-position: 100% 50%;
    }

    100% {
      background-position: 0% 50%;
    }
  }
}
