style.css:
@-moz-document url-prefix("http://seasonvar.ru/serial") {
noindex,
iframe[style="position: fixed; display: block; top: 0px; left: 0px; width: 100%; height: 100%;"],
.td-for-left-block,
#___plusone_1td,
#player_msoc,
.sidebar.lside,
div [align="center"] {
    display: none !important;
    position: absolute;
    left: -9999px;
}

.pgs-player-inside,
.pgs-player-inside object {
    height: 754px;
}
.middle.left {
    border-left-width: 20px !important;
    padding-left: 20px !important;
}
#pjsfrrshtmlPlayer {
    display: block !important;
}
}

@-moz-document domain("seasonvar.ru") {
html body {
    font-family: arial, sans-serif;
    padding-top: 1px !important;
    background-color: #0b0707;
}
noindex,
.lside-player div div {
    display: none !important;
    position: absolute;
    left: -9999px;
}
.lside-select {
    background-image: linear-gradient(0deg, #160c0b 0, #381e1b);
}
div.body.wrapper {position: absolute;
    margin: 0 !important;
}
div.wrapper {
    margin: 0px auto 0!important;
}
.middle {
     box-shadow: none !important; 
}
.header_icon,
.header_txt {
    display: none !important;
}
.header_line {
    /*overflow: hidden !important; */
    background: none !important;
    height: auto;
    margin: auto;
    padding: 10px 0px 0px;
}
.header_line:after, .header_line:before {display: none !important;}
.middle {
    padding: 20px 20px 15px;
}
html body div iframe {
    display: none !important;
}
.content-wrap,
.pgs-sinfo-actor [data-tabt=actorSpoiler],
.pgs-seaslist .tabs-result,
.pgs-seaslist a,
.news-blog,
#svc_new,
#svc_new:focus,
.b-addform textarea,
.b-addform textarea.focus,
.pgs-seaslist span,
form input:focus,
form textarea:focus,
.headblock-search-txt:focus,
.news.img a:nth-child(odd) {
    background-color: #0e0909;
    color: #e5d3b9;
}
.headblock-search-txt {
    background-color: #0b0707;
}
href,
a,
.pgs-sinfo-actor-title,
.b-taglist-form-lnk,
.pgs-sinfo-actor a,
.pgs-sinfo_list.rating span,
.tabs-close-btn,
.b-addform-rules-link,
.pgs-afterplay [data-tabr=epinfo] b,
.svc_form-rules-link,
.href,
a:hover,
.news-blog-title,
.news a:hover,
.news_s:hover,
.profile-menu a:hover .lside-serial a:hover,
.lside-btn-addon {
    color: #c1b9bc;
    text-decoration: none;
}
.rside .svico-mnotwatch .rside .svico-mblock,
.rside .svico-mwant,
.rside .svico-mwatch,
.rside .svico-mwatched {
   background-image: linear-gradient(0deg, #160c0b 0, #381e1b);
}
.middle,
.tabs-result-comments,
.pgs-review,
.news,
.news-head:after,
.news_s,
select,
.lside-blockg .btn_grp span,
.pgs-marks-el a,
.pgs-marks-remove {
    background-color: #0e0909;
    color: #e5d3b9;
    text-shadow: 0px 0px 0px #fff0;
}


*,
.pgs-marks-name,
.pgs-marks-mess span,
.doptxt a {
    color: bisque;
}
.pgs-marks-bn .pgs-marks-el a {
    background-color: #160c0b;
    color: bisque;
}

.svc_info,
.b-pagelist .btn,
.b-pagelist button,
.pgs-seaslist .tabs-title li,
.pgs-seaslist .tabs-title li:hover,
.tabs-title,
.header_txt,
.btn,
.btn_grp,
.pgs-back,
.pgs-back:before,
.poster-top:after,
.b-taglist.dl a,
.pgs-msg.error,
.pgs-head,
.tabs-title li.act,
.tabs-title li:hover,
.news-head,
.news-head:before,
.rside-title li.act,
.rside-title li:hover,
.rside-title li,
.news-blog .b-removebnews,
.lside-in .btn,
.profile-menu-head,
.profile-menu a,
.lside-serial a {
    background-color: #4c2924;
    color: #e5d3b9;
    background-image: linear-gradient(0deg, #160c0b 0, #381e1b);
    text-shadow: 0px 0px 0px #fff0;
}
.tabs-result,
.tabs-title,
.rside-title {
    border: 0px solid #2f2b2c;
}
.svc_info:after,
.svc_info:before {
    border-right-color: #g5a5c62;
}
.pgs-back:after,
.pgs-head:after,
.pgs-head:before,
.pgs-afterplay,
.news a:nth-child(odd) {
    background: #000;
    border-right-color: #j5a5c62;
}
.header_icon a:last-child,
.header_icon span:last-child {
    background: #y5a5c62;
}
.pgs-sinfo-actor-title:after,
.pgs-sinfo-actor-title:before {
    border-top: 2px solid #ac989f;
}
.lside-in.letter,
.rside-result,
.sidebar.rside,
.lside-block {
    background: black;
    border: 0px solid black;
}
.news [class*=" svico-"],
.news [class^=svico-] {
    color: #fff;
}
.lside-serial a:nth-child(2n) {
    background-image: linear-gradient(0deg, #h5a5c62 0, #h5a5c62);
}
.lside-blockg .btn_grp input[type=checkbox]:checked + span,
.lside-blockg .btn_grp input[type=radio]:checked + span {
    color: #fff;
}
.lside-blockg .btn_grp input[type=checkbox] + span,
.lside-blockg .btn_grp input[type=radio] + span {
    opacity: 1;
}
.tabs-title li {
    text-shadow:none;
}
.pgs-head:after, .pgs-head:before, .pgs-back:after, .pgs-back:before {
    content: none;
}
.b-switch .btn, .b-switch button {
    background: none !important;
    text-shadow: none;
}
.news-switch .btn, .news-switch button {
    font-size: 15px;
}
}
