style.css:
@-moz-document regexp("[a-zA-z]+://[^\s]*") {
    html,
    #wrapper,
    body {
        background-image: url(https://s1.ax1x.com/2020/07/06/UihCfP.png) !important/*背景图片*/
        ;
        background-repeat: no-repeat !important;
        background-position: center right !important;
        background-size: 55% !important;
        background-attachment: fixed !important;
        font-family: "Microsoft YaHei" !important;
    }
    html,
    body,
    #container > div,
    #container,
    .container,
    .content,
    html > div,
    section,
    div {
        /*background-color: rgba(255, 255, 255, 0)!important;*/
    }
    html,
    a,
    b,
    p,
    span,
    pre,
    em,
    html div,
    body div html div a,
    .container a,
    #container a,
    content a,
    .content a,
    ul li a,ul li {
        font: "思源黑体"!important;
        font-size: 15px!important;
        font-weight: bold!important;
    }
}
