style.css:
@-moz-document url-prefix("https://www.nitrotype.com") {

body
{
    
    background: url(https://www.nitrotype.com/dist/site/images/backgrounds/bg-tires.png), linear-gradient(130deg, #0088ff 0%, #157bd4 20%, #2373b9 20%, #2274bb 25%, #2273ba 25%, #27669c 27%, #1f67a7 27%, #2b72b1 50%, #3271a9 50%, #2e6fa9 85%, #256eaf 85%, #2d679a 90%, #255f92 90%);
    background-repeat: repeat-x;
    background-attachment: fixed;
    font-size: 100%;
    height: 100%;
    margin: 0;
    min-height: 100vh;
    min-width: 1064px;
    overflow-y: scroll;
    position: relative;
}

*, *:before, *:after {
    box-sizing: inherit;
}
.tab {
    flex: 0 0 auto;
    border-radius: 27px 27px 0 0;
    padding: 20px 20px;
    margin-right: 10px;
    text-align: left;
}
.theme--pDefault.is-gold .profile-goldBorder {
    background: linear-gradient(to bottom, #00e6ff 0, #2eabb9 100%);
    height: 101%;
    left: 0;
    overflow: hidden;
    top: 0;
    width: 100%;
    z-index: 1;
}
.racev3Pre-tipIcon {
    background: #167ac3;
    color: #fff;
    flex: 0 0 50px;
    position: relative;
    border-radius: 60px 0 0 60px;
}
.racev3Pre-tipText {
    padding: 5px 15px;
    font-size: 14px;
    background: #0e4d7a;
    width: auto;
    max-width: 75%;
    flex: 0 0 auto;
    border-radius: 0 60px 60px 0;
}
.nav-link {
    cursor: pointer;
    text-decoration: none;
    color: rgba(255, 255, 255, 0.5);
    font-weight: 600;
    font-size: 14px;
    height: 100%;
    padding: 15px;
    transition: all 0.25s ease-in-out;
    width: 100%;
    text-align: center;
    line-height: 1;
}
.btn--d {
    background-color: #fff;
    border-radius: 60px;
    border: 1px solid #eee;
    color: rgba(2, 2, 2, 0.8);
    font-weight: 600;
}
.btn--b:hover {
    background: linear-gradient(to right, #47cc80, #47cc80);
    color: #fff;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.65);
}
.btn--e {
    background-color: #ff751b;
    border-radius: 60px;
    border: 1px solid #e0bb2f;
    color: rgba(2, 2, 2, 0.7);
    font-weight: 600;
}
.profile.is-gold .mysteryBox-copy {
    background: url(https://www.nitrotype.com/dist/site/images/mystery-box/mystery-box-qs.png) center center no-repeat, linear-gradient(to bottom, rgba(23, 119, 189, 0.3) 0%, #1866a1 30%, #1866a1 80%, #167ac3 100%);
