style.css:
@-moz-document domain("subscribestar.com") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 0px;
             background-color: #212121;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 5px;
             background-color: #212121;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 0px;
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, .3);
             background: linear-gradient(280deg, #009f7c, #b00052)
        }

body, .top_bar, .top_bar:after, .top_bar:before, .top_bar-shadow, .goals-goal_inner, .goals-goal_meter, .is-locked .flat_button.for-subscribe-2c-more, .is-locked .flat_button.for-tier-unlock, .flat_button.for-subscribe-2c-more, .search_results, .home-section.is-features, .home-section.is-how, .home-section.is-why, .home-section.is-features:after, .home-section.is-features:before, .home-section.is-how:after, .home-section.is-how:before, .home-section.is-why:after, .home-section.is-why:before, .pricing-calculator_result-amount, .home-meet_the_stars, .home-section_sign, .home-samples_item-img, .overlay-bg, .signup-social_button.is-facebook:hover:not(.is-disabled), .textarea-input, .settings_timezone-select, .star-items_wrapper .star_item.is-featured .flipper-back, .star-items_wrapper .star_item.is-featured .flipper-front, .star_item .star_item-avatar img {
	background: #0e0e0e;
}
.search_results .search_entry:hover {
	background: #111;
}
.textbox-field, .tier, .signup-role_star, .signup-role_subscriber, .textarea.for-profile_settings .textarea-field, .settings_login, .for-profile_welcome_sidebar .profile_features-item:not(:first-child), .star-items_wrapper .star_item.is-featured .flipper-back, .star_item {
	border-color: #151515;
}
.section-title, .tier-avatar_image, .search_results .search_entry, .home-meet_the_stars-avatar img, .card:not(:last-child), .card-notice.with-border, .star_item .star_item-avatar img, .for-tier_modal .tier-body {
	border-color: #222;
}
.flat_button.is-grey:focus, .flat_button.is-grey:hover {
	background: #222;
	border-color: #0e0e0e;
}
.flat_button.is-grey {
	border-color: #222;
	color: #666;
}
body, .section-title_text, .goals-goal_description, .subscribe-2c-more_title, .link-accented.for-all_features, .link-accented.for-all_stars, .settings_shipping, .star_item .star_item-info, .star_item-actions {
	color: #ccc;
}
.top_bar, .home-section_title, h2.home-section_subtitle {
	color: #c6c6c6;
}
.for-profile_welcome_sidebar .profile_features-item {
	color: #bbb;
}
.star_link-types, .section-body.is-hint, .post-date, .search_results .search_results-item .star-types, .donate-description, .signup-description, .signup-role_role, .signup-role_description, .card-notice, .settings_media-formats, .tiers-settings_item-cost-period {
	color: #999;
}
.post-date:hover {
	color: #bbb;
}
.post-body.is-locked {
    background-color: #0e0f0e;
}
.post-actions {
	border-top-color: #131313 !important;
	border-bottom-color: #222 !important;
}
.subscribe-2c-more {
    background-color: #0e0f0f;
}
.profile_features-introduction > .is-stars.md_icon > svg > circle, .top_bar-icon.is-stars.md_icon > svg > circle, .signup-step.is-finished path, .is-fa_envelope.md_icon > svg > path {
	fill: #0e0e0e;
}
.layout-footer {
	background: #333;
}
input, .textarea-input, .settings_timezone-select {
	color: #eee;
}
.profile_main-warning, .search_results-warning, .star_item-warning {
    color: #eee;
    background-color: #ff4700;
}
.home-section.is-money, .home-section.is-money:after, .home-section.is-money:before {
    background-color: #0e0e0e;
	background-image: url('https://ss-staging-assets.s3-us-west-1.amazonaws.com/watermarks_green.png')
}
.is-down.home-samples_item-img.with-tail:after, .is-down.home-success_item-quote.with-tail:after, .is-down.pricing-calculator_result-amount.with-tail:after {
    border-top: 1.4rem solid #0e0e0e;
}
.home-section.is-support, .home-section.is-support:after, .home-section.is-support:before {
    background-color: #0e0f0f;
}
.checkbox.for-signup {
	background: #0000 !important;
	color: #ccc;
}
.arrow_button {
	border-color: #333;
}
.arrow_button path {
	fill: #333;
}
.overlay-close {
	background: #0e0e0e;
}
.signup-step {
	border-color: #222;
}
.signup-step path {
	fill: #222;
}
.signup-step_arrow {
	background: #222;
}
.signup-step_arrow:after {
    border-left-color: #222;
}
.textbox .textbox-icon_wrap, [src="https://assets.subscribestar.com/assets/public/images/sample_userpic-729d57082377951bfb1bbac409c75804.png"] {
	filter: invert(100) !important;
}
.textarea.for-profile_settings:last-child, .checkbox.for-communication_settings:last-child, .post-actions {
	background: #0000 !important;
}
.warning-message, .notice-content {
	color: #000;
}
.settings_timezone-select:hover {
    background-color: #0f1111;
}
.croppie-container .cr-viewport {
    box-shadow: 0 0 2000px 2000px hsla(0, 0%, 0%, 0.7);
    border: 1px dotted #ff4081;
}
.edit_avatar-crop_area_inner {
    background: hsla(0, 0%, 0%, 0.3);
    border: 1px dashed #151515;
}
.star-items_wrapper .star_item.is-featured:after {
    border-color: transparent #0e0e0e #0e0e0e transparent;
}
.profile_main_info-userpic img {
	background-color: #222;
	border-color: #0e0e0e;
}
.top_bar-avatar img {
	background: #222;
}
}
