style.css:
@-moz-document url-prefix("https://www.blast.hk/") {
.uix_headerContainer > *:not(.p-nav) {
    display: none;
}
.p-body {
    background: #17191f;
}
.block-tabHeader .tabs-tab:hover {
    color: #5a5c65;
	background: #17191f;
}
.bbCodeBlock--code.bbCodeBlock--screenLimited .bbCodeBlock-grip {
    display: flex;
    align-items: center;
    justify-content: center;
    height: 20px;
    border-top: 1px solid #505662;
    background: #22242b;
    color: #959fb4;
    transition: color .2s;
}
.CMTV_QT.button--solve {
    background: #398239;
}
.block-minorTabHeader {
    padding: 0;
    margin: 0;
    font-weight: 400;
    font-size: 1.3rem;
    color: #7daccb;
    background: #31333a;
    border-bottom: 1px solid #505662;
    font-size: 0;
}
.p-nav {
    color: #bdc6db;
    ;
    font-weight: 400;
    background: #22242b;
    transition: ease-in .15s all;
}
.p-staffBar .pageContent a {
    color: #d9dde5;
    padding-top: 4px;
    padding-bottom: 4px;
    margin: 0;
}

.uix_tabList {
    z-index: 100;
    margin: 0;
    padding: 0;
    display: flex;
    background: #383d48;
    position: fixed;
    text-transform: capitalize;
    bottom: 0;
    left: 0;
    right: 0;
    box-shadow: 0 0 2px 0 rgba(0,0,0,0.14), 0 -2px 2px 0 rgba(0,0,0,0.12), 0 -1px 3px 0 rgba(0,0,0,0.2);
}
.p-nav-list .p-navEl.is-selected {
    color: #a2a2a4;
    font-weight: 700;
}
.userBanner.userBanner--orange {
    color: #000;
    background: #ffcb00;
    border-color: #ffcb00;
    filter: drop-shadow(0 0 3px #c69e02);
    border-radius: 8px;
}
.userBanner.userBanner--royalBlue {
    color: #fff;
    background: #4169e1;
    border-color: #4169e1;
    filter: drop-shadow(0 0 3px #3454b5);
    border-radius: 8px;
}
.userBanner.userBanner--green {
    color: #fff;
    background: #008000;
    border-color: #008000;
    filter: drop-shadow(0 0 3px #026c02);
    border-radius: 8px;
}
.userBanner.userBanner--cyan {
    color: #fff;
    background: #11806a;
    border-color: #11806a;
    filter: drop-shadow(0 0 3px #0f7460);
    border-radius: 8px;
}
.p-nav-list .p-navEl {
    font-size: 1.3rem;
    color: #bdc6db;
    text-decoration: none;
    text-transform: uppercase;
    line-height: 1;
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
    height: 65px;
    -webkit-transition: all .2s ease;
    transition: all .2s ease;
    position: relative;
}
.p-nav-list .p-navEl.is-selected:after {
    position: absolute;
    bottom: 10px;
    left: 12px;
    right: 12px;
    height: 4px;
    border-radius: 8px;
    background: #3f424a;
    display: block;
    transition: ease-in .2s right;
    background: linear-gradient(-270deg, #3e4047 0%, #3d3f45 100%);
}
.uix_nodeList .block-container {
    border-width: 0;
    border-style: solid;
    /*box-shadow: 0 0px 10px 0 #31333a52;*/
    ;
}
.block--category .block-header {
    display: flex;
    align-items: center;
    font-size: 1.6rem;
    color: #fff;
    font-weight: 700;
    background: #31333a;
    border-bottom-width: 0;
    border-bottom-style: solid;
    padding: 16px;
    line-height: 1;
    background-size: cover;
    background-position: center;
    text-shadow: none;
    /*0 0 1px rgba(0,0,0,0.7)*/
    position: relative;
    transition: ease-in .15s border-bottom-left-radius, ease-in .15s border-bottom-right-radius;
}
.block-container {
    color: #e0e6f0;
    background: #22242b;
    border-width: 0;
    border-style: solid;
    border-top-color: transparent;
    border-right-color: transparent;
    border-bottom-color: transparent;
    border-left-color: transparent;
    border-radius: 8px;
    transition: all .2s ease;
}
.fsp .tabGroup {
    color: #e0e6f0;
    background: #22242b;
    border-width: 0;
    border-style: solid;
    border-top-color: #505662;
    border-right-color: transparent;
    border-bottom-color: transparent;
    border-left-color: transparent;
    border-radius: 0;
    padding: 8px;
    display: -webkit-flex;
    display: flex;
    -webkit-flex-direction: column;
    flex-direction: column;
    flex: 1 1 100%;
    -webkit-transition: all .25s ease;
    transition: all .25s ease;
    -webkit-transition-property: border margin;
    transition-property: border margin;
    margin-right: 2px;
    max-width: 100%;
    overflow: hidden;
    margin-top: 20px;
}
.block--messages.block .message, .js-quickReply.block .message, .block--messages .block-row, .js-quickReply .block-row {
    color: #c0c9d8;
    background: #22242b;
    border-width: 0;
    border-style: solid;
    border-top-color: #505662;
    border-right-color: #505662;
    border-bottom-color: #505662;
    border-left-color: #505662;
    border-radius: 8px;
    border-radius: 0;
    border: none;
}
.fsp .tabs--list {
    font-size: 1.3rem;
    color: #bdc6db;
    background: #22242b;
    border-bottom: 1px solid #31333a;
    margin-bottom: 5px;
    min-height: 27px;
    width: 100%;
    display: block;
}
.fsp .tabs .tab.is-selected {
    color: #bdc6db;
    border-color: #bdc6db;
    border-bottom-width: 3px;
    border-bottom-style: solid;
    font-weight: bold;
}
.is-unread .structItem-title {
    color: #bdc6db;
    font-weight: 700;
}
.structItem--thread:hover {
    background-color: transparent;
}
.structItem-title a:hover {
    color: #a8acb2;
}
.structItem-minor {
    font-size: 1.2rem;
    color: #bdc6db;
}
.fsp .structItem-counters {
    float: right;
    margin-left: 4px;
    color: #e0e6f0;
    font-size: 1.2rem;
}
.fsp .changeLimit {
    color: #e0e6f0;
    background: transparent;
    position: absolute;
    top: 10px;
    right: 40px;
    z-index: 50;
	margin-top: 20px;
}
.fsp .refreshButton {
    color: #e0e6f0;
    background: transparent;
    position: absolute;
    top: 10px;
    right: 15px;
    z-index: 50;
	margin-top: 20px
}
.node-body {
    table-layout: fixed;
    width: 100%;
    transition: all cubic-bezier(.4, 0, .2, 1) .15s;
    background: #22242b;
    padding: 5px;
    overflow: hidden;
}
.node--unread .node-icon i:before {
    opacity: 1;
    color: #93959c;
    text-shadow: 0 4px 10px 0 rgba(2, 50, 109, 1);
}
.node-title a {
    color: inherit;
}
.node-subNodeFlatList > li a {
    color: #e0e6f0;
}
.subNodeLink.subNodeLink--unread:before {
    color: #e6e7ebcf;
}
.subNodeLink::before {
    font-family: 'Font Awesome 5 Pro';
    font-style: normal;
    font-weight: 400;
    display: inline-block;
    font-size: inherit;
    text-rendering: auto;
    line-height: 1;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    transform: translate(0, 0);
    width: auto !important;
    width: 1em;
    padding-right: .3em;
    text-decoration: none;
    color: #bdc6db;
}
.block-container .uix_newIndicator {
    font-size: 12px;
    color: #fff;
    background: #31333a;
    border-radius: 4px;
    padding-top: 1px;
    padding-right: 4px;
    padding-bottom: 1px;
    padding-left: 4px;
    box-shadow: none;
}
.node-description {
    font-size: 1.2rem;
    color: #bdc6db;
    line-height: 1.5;
}
.pairs > dt {
    padding: 0;
    margin: 0;
    color: #bdc6db;
}
.uix_sidebarInner:not(.offCanvasMenu-content) .block .block-body {
    color: #e0e6f0;
    background: #22242b;
    border-width: 0;
    border-style: solid;
    border-top-color: transparent;
    border-right-color: transparent;
    border-bottom-color: transparent;
    border-left-color: transparent;
    border-radius: 8px;
    box-shadow: none;
}
.block[data-widget-id] .block-minorHeader:before,
.p-body-sideNav .block-minorHeader:before,
.p-body-sidebar .block-minorHeader:before,
.uix_extendedFooterRow .block-minorHeader:before,
.columnContainer-sidebar .block-minorHeader:before,
.block[data-widget-id] .block-header:before,
.p-body-sideNav .block-header:before,
.p-body-sidebar .block-header:before,
.uix_extendedFooterRow .block-header:before,
.columnContainer-sidebar .block-header:before {
    font-size: 18px !important;
    padding-right: 10px;
    color: #bdc6db;
}
.block-minorHeader a {
    color: inherit;
    text-decoration: none;
}
.block-footer {
    padding: 16px 16px;
    font-size: 1.2rem;
    color: #cfd6e5;
    background: #31333a;
    border-top: 1px solid #3f424a;
    padding-top: 10px;
    padding-bottom: 10px;
}
.block-minorHeader {
    padding: 16px 16px;
    margin: 0;
    font-weight: 400;
    text-decoration: none;
    font-size: 1.3rem;
    color: #cfd6e5;
    font-weight: 700;
}
.fsp .tabGroup-content::-webkit-scrollbar-thumb {
    border-radius: 8px;
    background-color: #bdc6db;
}
.node-body:hover {
    background: transparent;
    position: relative;
    cursor: pointer;
}
.node--depth2:nth-child(even) .node-body:hover {
    background: transparent;
    position: relative;
}
.p-footer {
    display: flex;
    flex-direction: column;
    font-size: 1.2rem;
    color: #e0e6f0;
    background: #31333a;
}
.p-footer-inner {
    order: 30;
    padding-top: 10px;
    padding-bottom: 10px;
    background: #31333a;
    padding-top: 0;
}
a:hover {
    color: #f6f7f9;
    text-decoration: underline;
}
a {
    color: #c0c9d8;
    text-decoration: none;
}
.uix_socialMedia li a {
    margin: 6px;
    line-height: 1;
    display: inline-block;
    font-size: 22px;
    color: #bdc6db;
}
.p-footer-linkList > li a:hover {
    color: #e0e6f0;
    text-decoration: none;
}
.p-footer-linkList.p-footer-choosers a {
    margin: 6px;
    color: #bdc6db;
    border: 2px solid #3f424a;
}
.p-footer-linkList.p-footer-choosers a:hover {
    color: #e0e6f0;
    background: #3f424a;
}
.button.button--scroll:active,
a.button.button--scroll:active {
    background: #31333a;
}
.button.button--scroll,
a.button.button--scroll {
    color: #fff;
    background: #31333a;
    box-shadow: 0 4px 15px 0 rgba(0, 0, 0, 0.2);
    border: none;
    background: linear-gradient(#31333a, #31333a);
    padding: 5px 8px;
    box-shadow: 1px 2px 2px 0 rgba(0, 0, 0, 0.25);
}
.username {
    color: #c0c9d8;
}
.notice.notice--primary {
    color: #e0e6f0;
    background: #22242b;
    border: 2px solid #31333a;
}
.notice.notice--primary .uix_noticeIcon {
    background: #31333a;
    box-shadow: 2px 0px 10px 0 rgba(0, 0, 0, 0.2);
}
.block--messages .message,
.js-quickReply .message,
.block--messages .block-row,
.js-quickReply .block-row {
    color: #e0e6f0;
    background: #22242b;
    border-width: 0;
    border-style: solid;
    border-top-color: transparent;
    border-right-color: transparent;
    border-bottom-color: transparent;
    border-left-color: transparent;
    border-radius: 8px;
    border-radius: 0;
    border: none;
}
.message-cell.message-cell--user,
.message-cell.message-cell--action {
    position: relative;
    background: #22242b;
    border-right: 0 solid;
    min-width: 0;
}
.message-avatar-wrapper .message-avatar-online:before {
    font-family: 'Font Awesome 5 Pro';
    font-size: inherit;
    font-style: normal;
    font-weight: 400;
    text-rendering: auto;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    content: "\f2bd";
    line-height: 1;
    font-weight: bold;
    color: #7fb900;
    border: #22242b solid 2px;
    background: #22242b;
    border-color: #22242b;
    border-radius: 50%;
    display: inline-block;
}
.message-attribution {
    color: #bdc6db;
    font-size: 1.2rem;
    border-bottom: 1px solid #3f424a;
    padding-bottom: 3px;
    border-width: 0;
    border-style: solid;
    padding-bottom: 16px;
    display: flex;
    align-items: center;
    justify-content: space-between;
}
.message-attribution-opposite a:hover {
    text-decoration: none;
    color: #cccccc;
}
.bbCodeSpoiler-button {
    border: 1px solid;
    min-height: 30px;
    line-height: 1em;
}
.p-breadcrumbs > li:last-child a {
    font-weight: 700;
    color: #cfd6e5;
}
.p-breadcrumbs > li a {
    display: inline-block;
    vertical-align: bottom;
    max-width: 300px;
    overflow: hidden;
    white-space: nowrap;
    word-wrap: normal;
    text-overflow: ellipsis;
    color: #bdc6db;
}
.u-concealed,
.u-concealed a,
.u-cloaked,
.u-cloaked a,
.u-concealed--icon,
.u-concealed--icon a {
    text-decoration: inherit !important;
    color: #afa3d0 !important;
}
.message .reactionsBar {
    margin-top: 16px;
    background: #2d2f36;
    border-width: 0;
    border-style: solid;
    border-radius: 20px;
    padding: 16px;
    padding-top: 10px;
    padding-bottom: 10px;
    margin: 0 0 16px;
}
.button.button--cta,
a.button.button--cta {
    color: #fff;
    box-shadow: 0 0px 10px 0 rgba(0, 0, 0, 0.2);
    border: none;
    background: linear-gradient(#31333a, #31333a);
    border-color: #31333a;
}
.button,
a.button {
    display: inline-block;
    border: 1px solid transparent;
    -webkit-transition: background-color .2s ease;
    transition: background-color .2s ease;
    font-size: 15px;
    font-weight: 400;
    border-radius: 8px;
    padding-top: 0;
    padding-right: 15px;
    padding-bottom: 0;
    padding-left: 15px;
    text-align: center;
    outline: none;
    line-height: 35px;
    height: 35px;
    text-decoration: none;
    display: inline-flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;
    box-sizing: content-box;
    -webkit-appearance: none;
    will-change: box-shadow;
    transition: all .3s cubic-bezier(.25, .8, .25, 1);
    border: none;
    white-space: nowrap;
    border-radius: 4px;
    margin-bottom: 5px;
    color: #e0e6f0;
    background: transparent;
    border-color: #343232;
}
.button.button--link:active,
a.button.button--link:active {
    background: #505662
}
.button.button--link:hover,
a.button.button--link:hover,
.button.button--link:focus,
a.button.button--link:focus {
    text-decoration: none;
    background: #31333a;
}
.menu-content {
    margin: 0;
    padding: 0;
    list-style: none;
    text-align: left;
    font-size: 1.2rem;
    color: #e0e6f0;
    background: #22242b;
    border: 0 solid #505662;
    border-radius: 4px;
    box-shadow: 0px 10px 20px rgba(0, 0, 0, 0.15), rgba(0, 0, 0, 0.1) 0px 0px 0px 1px;
    border-radius: 8px;
}
.menu-content:before {
    content: '';
    background: linear-gradient(-270deg, #3f424a 0%, #3f424a 100%);
    left: 0;
    right: 0;
    top: 0;
    height: 4px;
    display: block;
    border-top-left-radius: 8px;
    border-top-right-radius: 8px;
    position: absolute;
}
.menu-separator {
    margin: 0 0px;
    padding: 0;
    border: none;
    border-top: 1px solid #3f424a;
}
.menu-linkRow {
    display: block;
    padding: 12px 12px;
    border-left: 2px solid transparent;
    color: #e0e6f0;
    text-decoration: none;
}
.menu-linkRow.is-selected,
.menu-linkRow:hover,
.menu-linkRow:focus {
    color: #919398;
    text-decoration: none;
    background: #31333a;
}
.menu-linkRow {
    display: block;
    padding: 12px 12px;
    border-left: 2px solid transparent;
    color: #e0e6f0;
    text-decoration: none;
    transition: all .3s cubic-bezier(.25, .8, .25, 1);
}
.menu-header {
    padding: 12px;
    margin: 0;
    font-weight: 400;
    text-decoration: none;
    font-size: 1.6rem;
    color: #e0e6f0;
    background: #1d1f25;
    border-bottom: 1px solid #3f424a;
}
.button.button--cta:hover,
a.button.button--cta:hover,
.button.button--cta:focus,
a.button.button--cta:focus {
    box-shadow: 0 4px 15px 0 rgba(0, 0, 0, 0.2);
    background: linear-gradient(#3f424a, #3f424a);
}
.p-body-header .p-title-value {
    padding: 0;
    margin: 0 0 3px 0;
    font-size: 3.6rem;
    font-weight: 400;
    margin-right: auto;
    min-width: 0;
    font-size: 2em;
    color: #e0e6f0;
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Oxygen', 'Ubuntu', 'Cantarell', 'Fira Sans', 'Droid Sans', 'Helvetica Neue', sans-serif;
}
.reactionsBar .reactionSummary > li,
.message-responseRow .reactionSummary > li {
    background: #2d2f36;
}
.message-lastEdit {
    margin-top: .5em;
    color: #bdc6db;
    font-size: 1.1rem;
    text-align: right;
    padding: 0 16px 16px;
}
.message-signature {
    margin-top: 16px;
    font-size: 1.2rem;
    color: #959fb4;
    border-top: 1px solid #3f424a;
    padding-top: 16px;
    padding-bottom: 16px;
    margin-top: 0;
}
.actionBar .actionBar-action {
    border: 1px solid transparent;
    border-radius: 8px;
    margin-left: 5px;
    color: #bdc6db;
    background: rgba(189, 198, 219, 0.1);
    padding-right: 5px;
    padding-left: 5px;
    border-radius: 4px;
    display: inline-block;
}
.message:not(:last-child):after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 16px;
    right: 16px;
    height: 4px;
    border-radius: 8px;
    background: #3f424a;
    display: block;
}
.fr-toolbar {
    color: #e0e6f0;
    background: #2d2f36;
    position: relative;
    z-index: 4;
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Oxygen', 'Ubuntu', 'Cantarell', 'Fira Sans', 'Droid Sans', 'Helvetica Neue', sans-serif;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    user-select: none;
    -o-user-select: none;
    -moz-user-select: none;
    -khtml-user-select: none;
    -webkit-user-select: none;
    -ms-user-select: none;
    padding: 0 1px;
    border-radius: 4px;
    -moz-border-radius: 4px;
    -webkit-border-radius: 4px;
    -moz-background-clip: padding;
    -webkit-background-clip: padding-box;
    background-clip: padding-box;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
    text-align: left;
    border: 1px solid #3f424a;
    border-top: 2px solid #31333a;
    text-rendering: optimizelegibility;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    line-height: 1.2;
}

.button.button--link,
a.button.button--link {
    font-size: 1.2rem;
    padding: 0;
    padding-right: 8px;
    padding-left: 8px;
    height: 28px;
    line-height: 28px;
    background: #22242b;
    border: 1px solid #3f424a;
    text-transform: initial;
}
.button.button--primary:active,
a.button.button--primary:active {
    background: #b599ff;
}
.button.button--primary,
a.button.button--primary {
    color: #fff;
    background: #31333a;
    box-shadow: none;
    border: none;
    border-color: transparent;
}
.button.button--primary:hover,
a.button.button--primary:hover,
.button.button--primary:focus,
a.button.button--primary:focus {
    color: #fff;
    box-shadow: 0 4px 15px 0 rgba(0, 0, 0, 0.2);
    background: linear-gradient(#31333a, #31333a);
}
.fr-element {
    color: #e0e6f0;
    padding: 16px;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    overflow-x: auto;
    min-height: 52px;
}
.fr-box.fr-basic {
    background: #292b31;
}
.input:focus, .input.is-focused, .inputGroup:focus-within, #XF .fr-box.fr-basic.is-focused, .codeEditor.CodeMirror.CodeMirror-focused {
    --input-border-heavy: #637692;
    --input-border-light: #637692;
}

.fr-box.fr-basic.is-focused {
    background: #292b31;;
}
.fr-box.fr-basic.is-focused .fr-toolbar.fr-top {
    background: #292b31
}
.fr-command.fr-btn + .fr-dropdown-menu .fr-dropdown-wrapper {
	background: #22242b;
	/* border: 1px solid #3f424a; */
	border-top: transparent;
}
