*/

@-moz-document domain(hardwareonline.dk)
{

/***** colors *************************************************

background 1.........#000000
background 2.........#1a1a1a
background 3.........#202020
background 4.........#282828
borders..............#323232
font.................#ccc
link.................#2f8cb7
link visited.........#bd3539
       

/***** backgrounds ********************************************/
/** #000 **/
body {
    background: #1C1F26;
}


/** #1a1a1a **/
td.containerleft,
.wysibb .wysibb-text,
.wysibb,
.dropzone,
.wysibb .wysibb-toolbar .wysibb-toolbar-container .wysibb-toolbar-btn:hover {
    background: #1C1F26;
}

/** #202020 **/
select,
input,
td.containerright,
.pure-form input[type="text"][disabled],
.pure-table thead,
.ui-widget-content,
.ui-widget-header,
.pure-table thead,
.pure-form select {
    background: #1C1F26;
}

#ui-carousel-next,
#ui-carousel-prev {
    background-color: #272B35;
}

/** #282828 **/
textarea,
aside.info-box-hidden,
aside.info-box,
.info-box-green,
#footer_midt,
.containerright div[style*="border:1px solid #e9e9e9"],
.containerleft div[style*="border:1px solid #e9e9e9;"],
.bluebg,
.greybg,
.forumgruppehr,
.ks-oversigt-odd,
.ks-validationbox-content,
.artikel_info,
.pure-table-striped tr:nth-child(2n-1) td,
.pure-button,
.wysibb-text-editor,
.blackbg {
    background: #272B35;
}

#ContentPlaceHolder_ContentPlaceHolder_kategori [style*="background-color:#f5f5f5"] {
    background: #272B35 !important;
}

/** #2b5076 **/
.greytop,
.blacktop {
    background: #2b5076;
}


/***** borders ************************************************/
/** border #000 **/
td.containeremptyspace {
    border-color: #272B35;
}

/** border #1a1a1a **/
#banner[style*="border-left: 5px solid #ffffff"],
td.containerleft {
    border-color: #1C1F26 !important;
}

/** border #323232 **/
#ContentPlaceHolder_ContentPlaceHolder_traaddesc,
textarea,
select,
input,
div.newsitem,
img.newsitem_image,
fieldset,
[style*="border-bottom: 1px dotted #dddddd;"],
.pure-table td,
.pure-table th,
.pure-g[style*="border-bottom:2px solid #ccc;"],
.t47-svar,
.t47-svar-content,
.containerright div[style*="border:1px solid #e9e9e9"],
.containerleft div[style*="border:1px solid #e9e9e9;"],
.wysibb .wysibb-text,
.wysibb,
.wysibb .wysibb-toolbar,
.wysibb .wysibb-toolbar .wysibb-toolbar-container,
.articlepage_thumb,
.ks-validationbox-content,
.ks-validationbox,
.artikel_info,
.ks-validationbox-seperator,
.pure-table,
.ui-widget-content,
.ui-widget-header,
.pure-form legend,
.pure-form select {
    border-color: #272B35 !important;
}


/** transparent **/
td.containeremptyspace {
    border-color: transparent !important;
}

/***** font color *********************************************/
/** #ccc **/
textarea,
body,
p,
select,
input,
h1,
h2,
h3,
h4,
h5,
h6,
h5 a:link,
h5 a:visited,
a.link_list:link,
a.link_list:visited,
.blacklink_nosize a:link,
.blacklink_nosize a:visited,
.koebsalgblack a:link,
.koebsalgblack a:visited,
.widget_top_header,
.wysibb .wysibb-text,
.pure-table thead,
.ui-widget-header,
.pure-form legend,
.pure-button,
.wysibb .wysibb-toolbar .wysibb-toolbar-container .wysibb-toolbar-btn .fonticon {
    color: #ccc;
}

span[style*="color:rgb(0, 0, 0);"] {
    color: #ccc !important;
}

/** #9c9c9c **/
#forum_oversigt a:link,
#forum_oversigt a:visited,
.text_small,
.traad_nav a:link,
.traad_nav a:visited,
.artikel_forum_headline,
.date {
    color: #9c9c9c;
}


/***** imgs ***************************************************/
#banner_top {
    /*background-image: url(http://i.imgur.com/WBjHRjO.jpg);*/
    background-image: url(https://i.imgur.com/NKnSi31.jpg);
}

#banner_bottom {
    /*background-image: url(http://i.imgur.com/lgBZQtL.jpg);*/
    background-image: url(https://i.imgur.com/PV5tHK0.jpg);
}

#banner_logo {
    /*background-image: url(http://i.imgur.com/iuW5Z0k.jpg);*/
    background-image: url(https://i.imgur.com/pqYHtAt.jpg);
}


/***** misc ***************************************************/
.pure-form input[type="text"],
.pure-form input[type="password"],
.pure-form input[type="email"],
.pure-form input[type="url"],
.pure-form input[type="date"],
.pure-form input[type="month"],
.pure-form input[type="time"],
.pure-form input[type="datetime"],
.pure-form input[type="datetime-local"],
.pure-form input[type="week"],
.pure-form input[type="number"],
.pure-form input[type="search"],
.pure-form input[type="tel"],
.pure-form input[type="color"],
.pure-form select,
.pure-form textarea,
.wysibb {
    box-shadow: none;
}

.wysibb .wysibb-toolbar .wysibb-toolbar-container .wysibb-toolbar-btn .fonticon {
    text-shadow: none;
}

code.highlight {
    background: #484848;
    color: #ccc;
}

/** Ego mode 
a[href="show_user.aspx?uname=wazer%5e"]{
	color: #f1cb00;
    text-shadow: gold 1px 1px 30px;
    font-weight: bold;
    background-image: url("https://i.imgur.com/TFKMHoR.gif");
}
**/
/***** forum toolbar icons ******************************************/
#ContentPlaceHolder_ContentPlaceHolder_TraadToolbar_btnAddForetrukne .fa-stack {
    color: #a0a0a0;
}
#ContentPlaceHolder_ContentPlaceHolder_TraadToolbar_btnDelForetrukne .fa-stack {
    color: #4d9101;
}
#ContentPlaceHolder_ContentPlaceHolder_TraadToolbar_btnAddNotification .fa-stack {
    color: #a0a0a0;
}
#ContentPlaceHolder_ContentPlaceHolder_TraadToolbar_btnDelNotification .fa-stack {
    color: #8c9101;
}
#ContentPlaceHolder_ContentPlaceHolder_TraadToolbar_hlReportToAdmin .fa-stack {
    color: #330000;
}


/***** message warning 6 months old ******************************************/
.t70-message-body {
    padding: .5rem 1.5rem 0.1rem 8.0rem;
}

.t70-message.t70-is-warning {
    background-color: #300;
}
.t70-message.t70-is-warning .t70-message-body {
    border-color: #300;
    color: #a0a0a0;
}
.t70-message.t70-is-danger {
    background-color: #fdf6f7;
}


/** hidden and or removed messages **/
.forum_svar_row_hidden {
    background-color: #300;
}


/****** Remove Nags *******/
.centerfull {
    display: none;
}

#top {
    display: none;
}
}
