style.css:
@-moz-document domain('blog.csdn.net'){
 #rightAside, .operating, .c-gray, .un-collection, .read-count, .article-read-img, .article-type-img, .hide-article-box, .blog-expert-recommend-box, .recommend-item-box.type_hot_word, .recommend-ad-box, .isGreatIcon, .tool-box, .meau-gotop-box, .recommend-end-box, .login-mark, .blog_title_box.oneline, .recommend-item-box::before, .pulllog-box,.recommend-box, .template-box, .more-toolbox, .comment-box, .right-message, .main-login, .identity-icon, .show-txt, .title .flag, #passportbox, .column-advert-box, .article-plan-img, .article-plan-text, .up-time, .article-vip-img, .article-vip-text
  {
    display: none!important;
  }

  code, pre, code span, pre span
  {
    font-family: "Source Code Pro", Monaco, Menlo, Consolas, monospace!important;
  }
.csdn-toolbar {
  justify-content: center;
  align-items: center;
  height: 60px;
  display: flex;
}
body {
  background: #f4f5f5 !important;
}
aside div #asideSearchArticle {
  display: none;
}
aside div #asideCategory {
  display: none;
}
#csdn_tool_otherPlace .pull-right,
.csdn-toolbar .container .pull-right {
  display: none;
}
.csdn-toolbar .container .search_bar {
  display: none;
}
body .csdn-toolbar .container {
  display: flex;
  justify-content: center;
  align-items: center;
}
#csdn_tool_otherPlace li,
.csdn-toolbar .container li {
  padding-right: 30px;
}
.csdn-toolbar .container .left-menu li a {
  font-size: 15px;
}
.main_father {
  padding: 30px 12px 0 12px;
}
aside #asideProfile .item-rank {
  display: none !important;
}
aside #asideProfile .aside-box-footer {
  display: none !important;
}
.profile-intro-name-boxOpration {
  display: none !important;
}
.groupfile {
  width: 250px !important;
}
aside #asideProfile .data-info {
  display: none !important;
}
aside #asideProfile .profile-intro {
  flex-direction: column;
  justify-content: center;
  align-items: center;
}
aside #asideProfile .profile-intro .user-info {
  margin: 10px 0 0 0 !important;
  justify-content: center;
  align-items: center;
}
aside div.aside-box {
  margin-bottom: 15px !important;
  border-radius: 3px;
  width: 250px;
}
aside div.aside-box h3.aside-title {
  display: none !important;
}
@media screen and (max-width: 1550px) {
  .nodata #asidedirectory,
  .nodata #asideCategory {
    overflow: hidden;
    border-radius: 3px;
  }
}

.nodata .container,
.nodata .recommend-right {
  width: 960px !important;
}
aside {
  display: none !important;
}
.h1,
.h2,
.h3,
.h4,
.h5,
.h6,
a,
abbr,
body,
cite,
dd,
dl,
dt,
h1,
h2,
h3,
h4,
h5,
h6,
iframe,
input,
li,
object,
ol,
p,
pre,
span,
ul {
  font-family: monospace;
}
main div.blog-content-box {
  padding: 20px;
}
main
  div.blog-content-box
  .article-header-box
  .article-header
  div.article-title-box {
  margin-bottom: 30px;
}
main
  div.blog-content-box
  .article-header-box
  .article-header
  div.article-info-box
  div.blog-tags-box {
  display: none !important;
}
main
  div.blog-content-box
  .article-header-box
  .article-header
  div.article-info-box {
  padding-left: 10px;
}

.htmledit_views span {
  color: #333 !important;
}
main div.blog-content-box {
  padding: 20px;
  border-radius: 5px;
  box-shadow: 2px 2px 14px #ddd;
}

}
