style.css:
/* i really want this to be global */

.landingpage {
    background: url(https://i.ibb.co/37WybfR/Momio-Clean-Design.png) center top no-repeat #6bdaff;
}

.landingpage-btnsContainer {
    top: 350px;
}

.skyButton {
    color: #ffffff;
    box-shadow: none;
    background: #56c5e9 no-repeat left center;
    border: solid 2px #fff;
    border-radius: 20px;
}

.landingpage-host-select-btn {
    background-color: #56c5e9;
}

.landingpage-parents-btn {
    background-color: #56c5e9;
}

.landingpage-host-select-btn-flag {
    box-shadow: none;
    -webkit-box-shadow: none;
}

.landingpage-host-select-btn-flag {
    height: 32px;
}

.landingpage-host-select-flag {
    width: 80px;
    height: 52.7px;
    background-size: cover;
    margin: 0 auto !important;
    -webkit-box-shadow: none;
    border: solid 3px #56c5e9;
}

.hFixedSizeOverlay .bottom-green-stripes {
    position: absolute;
    height: 25px;
    bottom: 0;
    width: 320px;
    background: #6bdaff;
    z-index: 2;
    box-shadow: none;
    border-radius: 0 0 12px 12px;
    -webkit-border-radius: none;
}

.hFixedSizeOverlay .top-green-stripes {
    position: relative;
    height: 25px;
    background: #6bdaff;
    top: 0;
    z-index: 2;
    box-shadow: none;
    border-radius: 12px 12px 0 0;
    -webkit-border-radius: none;
}

.hFixedSizeOverlay {
    transform: scale(1);
    -webkit-transform: scale(1);
    border: solid 2px #ffffff;
    box-shadow: 0px 0px 10px 0px #55c4e8;
}

.skyButton.purple {
    background-color: #56c5e9;
}
