@-moz-document domain("youtube.com") {
/* #  —É–º–µ–Ω—å—à–∏—Ç—å —Å–≤–æ–±–æ–¥–Ω–æ–µ –ø—Ä–æ—Å—Ç—Ä—Ä–∞–Ω—Å—Ç–≤–æ –º–µ–∂–¥—É –ì–û–†–ò–ó–û–ù–¢–ê–õ–¨–ù–´–ú–ò –±–ª–æ–∫–∞–º–∏ —Å –í–∏–¥–µ–æ –Ω–∞ —Å—Ç—Ä–∞–Ω–∏—Ü–µ –ö–∞–Ω–∞–ª–∞  # */
