style.css:
@-moz-document domain("howtogeek.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

#content #primary #post-networkbox strong,
#content #primary .columns .header,
#masthead ul#topmenu li,
#masthead .subscribe-button,
#page .entry-header .entry-meta,

.full-screen-menu nav ul a,
.full-screen-menu nav ul li,
.load-more,

h4
{
	text-transform: capitalize!important;
}

#articlelabel,
#content #primary #post-networkbox,
#content #primary #post-recentguidesbox,
#content #primary .columns .column.one,
#content #primary .post-navigation ul li,
#content #primary article.type-facts,

.featured-article-stream,
.message span,
.splashbox,
	
:focus,
[style="border: 1px solid #eee;padding: 15px;margin-bottom: 20px;"]
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

.sidebar-popular-post .number {
    text-align: right;
    float: left;
}

#content #secondary {
    float: right;
}

/* Fonts 
_____________________________________________________________________________________________________*/

#masthead .subscribe-button,
#masthead ul#topmenu li,

.oswald-header-menu-link,

body
{
    font-family: arial, sans-serif;
}

#content #primary #post-recentguidesbox h3,

.sidebar-popular-post .number
{
	font-size: 2.5rem!important;
}

#content #primary #post-networkbox strong,

.entry-content,
.entry-summary,
.page-content
{
	font-size: 1.85rem!important;
}

#content #primary .entry-meta,
#content #primary #post-networkbox a,
#content #primary #post-recentguidesbox a,
#content .product-features
{
	font-size: 1.52rem!important;
}

selector
{
	line-height: 2.5rem!important;
}

#content .product-features
{
	line-height: 1.85rem!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

#ctMedia img,

.bordeaux-anchored-container,
.bordeaux-slot.bordeaux-filled-slot,
.adslot970:not(.collapse),
.homepage-bottom-ad,
.jw-loaded,
	
[id*="purch_"]
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Left/Right/Top/Bottom 
_____________________________________________________________________________________________________*/

.sidebar-popular-post .post-title {
    padding-left: 0;
}

.sidebar-popular-post .number {
    margin-right: 10px;
}

.sidebar-popular-post:first-child {
    padding-bottom: 81px;
}

/* Margins 
_____________________________________________________________________________________________________*/

#secondary,

.article-stream,
.entry-footer,
.featured-article-stream,
.splashbox
 {
	margin: 12px!important;
}

#primary
 {
	margin: 12px 2rem!important;
}

#content #primary .columns img
 {
	margin: 12px 0!important;
}

#content #primary .columns .column.one img {
    margin: 12px 51px!important;
}

#content #primary article img {
    margin: 7px 20px 12px 50px!important;
}

.ct_wallpaper #page {
    margin: 46px 0 auto;
}

/* Paddings 
_____________________________________________________________________________________________________*/

#secondary,
#primary
{
	padding: 1.2rem!important;
}

selector
{
	padding: 13px 26px!important;
}

/* Heights 
_____________________________________________________________________________________________________*/

selector
{
	max-height: none!important;
}

.splashbox,
.article-quilt
 {
	height: auto!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

#content #primary article .article-text,
#content,
#page .entry-header,
#primary,

.article-quilt,
.ct_wallpaper #page,
.lfroundup_tablewrap_desktop_cell, .lfroundup_tablewrap,
.product-box,
.product-img,

[style="float: left; max-width: 15%; width: 145px;"],
body #content
 {
	max-width: none!important;
}

selector
 {
	width: auto!important;
}

#content #primary .columns .column.three .story a,
#content #primary .columns .column.three .story img,
#content #primary .columns .column.two,
#content #primary.content-area,
#page .entry-header,

.single-post #page .entry-header
{
    width: 100%!important;
}

#content #primary article .article-text,

.sidebar-popular-post .title-rendered {
    width: 84%;
}

x#content #primary.content-area,
x#page .entry-header
{
    width: 75%!important;
}

#primary.content-area {
    width: 65%!important;
}

#content #primary {
    width: 65%!important;
}

#content #primary .columns .column.two .story a {
    width: 64%!important;
}

#content #primary .columns .column.two,
#content #primary .columns .column.three .story a
{
    width: 59%;
}

.article-stream,
.featured-article-stream,
.splashbox
{
    width: 48%!important;
}

#content #primary .columns .column.two .story img
{
    width: 30%!important;
}

.sidebar-popular-post .number {
    width: 12%;
}

#primary .relatedside {
    width: 33rem;
}
}

