@-moz-document domain("howtogeek.com") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

#articlelabel,
#content #primary .post-navigation ul li.current,

.full-screen-menu nav ul li:hover,
.full-screen-menu nav ul li.explore-menu-active,
.sidebar-popular-post:hover .number,

a[href]:hover span,
a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label

 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

#content #primary .entry-content,

.archive .post-author a,.archive a.post-author,.archive span.post-author,
.blog .post-author a,.blog a.post-author,.blog span.post-author,
.header-commission-statement,
.message span,
.sidebar-popular-post .title-rendered,

body,
li,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

#content #primary .columns .header,
#page .entry-header .entry-title a,

.sptagicon-lifesavvy,
.tag #content .archive-description p,

h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

#content #primary #post-networkbox strong,

h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

#page .entry-header .entry-meta,

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

time
 {
	color: #07beff!important;
}

/* Green 
_____________________________________________________________________________________________________*/

.product-price
 {
	color: #03dc03!important;
}

/*
_____________________________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/* Misc 
_____________________________________________________________________________________________________*/

.token.punctuation
 {
    color: #fff!important;
}

.token.selector, .token.attr-name, .token.string, .token.char, .token.builtin, .token.inserted {
    color: #af0!important;
}

.token.atrule, .token.attr-value, .token.keyword
 {
    color: #00b3ff!important;
}

.token.property, .token.tag, .token.boolean, .token.number, .token.constant, .token.symbol, .token.deleted
 {
    color: #ff6cbe!important;
}



}

@-moz-document domain("howtogeek.com") {
/* Background Settings */

#v2campaignpopup_backdrop,

body,
html
{
	background: #000!important;
}

/* Background 
_____________________________________________________________________________________________________*/

.full-screen-menu .full-screen-menu-inner
 {
	background: #060505!important;
	background-color: #060505!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

#content #primary .columns .column.one,
#content #primary .columns .column:not(:last-child):after,
#content #primary .post-navigation ul li.current,
#content,
#page .entry-header,

.splashbox

{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

.load-more
{	
	background: radial-gradient(circle, #1b8bc8a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.load-more::before
{
	background: radial-gradient(circle, #000 0%, #1b8bc8 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.load-more:hover::before
 {
  opacity: 1!important;
}

/* Red 
_____________________________________________________________________________________________________*/

.full-white-popup-style button,
.long_form_roundup_widget .lfroundup_tablewrap td a,
.product-share-links a
{	
	background: radial-gradient(circle, #d90008a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.full-white-popup-style button::before,
.long_form_roundup_widget .lfroundup_tablewrap td a::before,
.product-share-links a::before
{
	background: radial-gradient(circle, #000 0%, #d90008 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.full-white-popup-style button:hover::before,
.long_form_roundup_widget .lfroundup_tablewrap td a:hover::before,
.product-share-links a:hover::before
{
  opacity: 1!important;
}

/* Plain Black 
_____________________________________________________________________________________________________*/

#page .entry-header,
#primary,
#secondary,

.article-quilt
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

#v2campaignpopup,

.entry-footer
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

#page .entry-header:hover,
#primary:hover,
#secondary:hover,
#v2campaignpopup:hover,

.article-quilt:hover,
.entry-footer:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Red Gradient 
_____________________________________________________________________________________________________*/

#primary .wp-caption-text,

.header-commission-statement,
.featured-article-stream article:last-child
{
	background: linear-gradient(to bottom, #800505 0%, #000 100%)!important;
}

/* Blue Gradient 
_____________________________________________________________________________________________________*/

#content #primary article.type-facts,

.featured-article-stream article:first-child,
.long_form_roundup_widget .lfroundup_introus,
.message span
{
	background: linear-gradient(to bottom, #03193c, #000 100%)!important;
}

/* Yellow Gradient 
_____________________________________________________________________________________________________*/

#content #primary #post-recentguidesbox
{
	background: linear-gradient(to bottom, #9d9d5d 0%, #000 100%)!important;
}

/*__ Orange Gradient _______________________________________________________________*/

#content #primary #post-networkbox,
#masthead .subscribe-button
 {
	background: linear-gradient(to bottom, #673900 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	text-shadow: 1px 1px #555!important;
}

/*__ Facebook Gradient _______________________________________________________________*/

.site-footer .facebook
