style.css:
@-moz-document url-prefix(https://www.nicovideo.jp/my) {
  .SlideOut {
      box-shadow:0 6px 3px -4px rgba(0, 0, 10, 0.1);
  }
  .NicorepoItem-item,
  .NicorepoItem-contentDetailTitle {
      font-size:1rem;
  }
  .NicorepoItem-item {
      padding:0.5rem;
  }
  .NicorepoItem-sender {
      align-items:start;
      height:auto;
  }
  .NicorepoItem-senderIcon.Thumbnail {
      position:absolute;
      top:0.5rem;
      left:0.5rem;
      width:2.8rem;
      height:2.8rem;
      border-radius:50%;
  }
  .NicorepoItem-senderIcon.Thumbnail .Thumbnail-image {
      top:-1%;
      left:-1%;
      right:-1%;
      bottom:-1%;
  }
  .NicorepoItem-senderName {
      margin-left:0.5rem;
      padding-left:2.8rem;
      font-weight:bold;
  }
  .NicorepoItem-body {
      margin-left:0px;
      padding-left:0px;
  }
  .NicorepoItem-activity {
      margin:0.4rem 0 0;
      margin-left:0.5rem;
      padding-left:2.8rem;
      color:#555555;
      font-size:0.8rem;
      line-height:1.2em;
  }
  .NicorepoItem-contentThumbnailImage.Thumbnail {
      width:128px;
      height:72px;
  }
  .NicorepoItem-content {
      margin-top:1rem;
  }
}
