style.css:
@-moz-document url-prefix("http://meteofrance.com/previsions-meteo-france/") {
#informations-speciales {
    display: flex;
    flex-wrap: wrap;
}

#informations-speciales ul.list-infos_speciales {
    width: 33%;
    min-width: 33%;
    flex-grow: 1;
    margin-bottom: 0;
    margin-right: 1em;
}

#informations-speciales ul.list-infos_speciales:last-child {
    margin-right: 0;
}

#main_container {
    margin-left: 3em;
    margin-right: 3em;
    max-width: unset;
}

#block-bandeauadministrablepoi {
    display: none;
}

#primary_sidebar {
    display: none;
}

#primary_section .row.has_sidebar #primary_content {
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    align-items: center;
}

#page_content_header {
    flex-grow: 1;
    margin: 0 1em;
}

#block-breadcrumbs-2 {
    margin: 0;
}

#atmogramme_slider .slide-fade-enter-active, #atmogramme_slider .slide-fade-leave-active {
    transition: none;
}

#atmogramme_slider .slider_item.single,
#atmogramme_slider .slider_item.multiple,
#atmogramme_slider {
    min-height: 96px;
}

#atmogramme_slider .slider_item {
    padding: 1em 0;
}

#atmogramme_slider .slider_item.multiple > li {
    width: unset !important;
    flex-grow: 1;
}

#atmogramme_slider .slider_item.multiple > li:first-child {
    margin-bottom: unset !important;
    flex-grow: 0;
}

#atmogramme_slider .slider_item.multiple .weather-item {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-around;
}

#atmogramme_slider .slider_item.multiple .day_date {
    padding-bottom: unset;
}

#atmogramme_slider .slider_item.weekend {
    padding-bottom: 1em;
}

#atmogramme_time_picker .overflow_scroll.weekend {
    padding-top: 16px;
    margin-top: -16px;
}

#atmogramme_time_picker ul li:not(.header).period_item:not(.hour) {
    min-width: unset !important;
    width: auto !important;
    padding: 1em 1ex;
}

#atmogramme_time_picker ul li.header {
    width: 270px;
    font-size: 0.7rem;
    z-index: 23;
}

#atmogramme_time_picker ul li.header.right {
    left: 270px;
}

#atmogramme_slider .slider_item.multiple .weather-item .weather_temp img,
#atmogramme_slider .slider_item.multiple .weather-item .weather_temp svg {
    width: unset;
}

#atmogramme_slider .slider_item.single .weather_icon .svg_container img,
#atmogramme_slider .slider_item.single .weather_icon .svg_container svg {
    width: unset;
    height: unset;
}

#atmogramme_slider .slider_item.single .weather_icon > div {
    display: flex;
    align-items: center;
    justify-content: space-around;
}

#atmogramme_slider .slider_item.single .weather_icon .svg_container {
    height: unset;
    margin: unset;
}

#atmogramme_slider .slider_item.single .weather_temperature {
    display: flex;
    align-items: center;
    justify-content: space-around;
}

#atmogramme_slider .slider_item.single .weather_temperature .temp {
    margin: unset;
}

#atmogramme_time_picker ul li:not(.header):not(.today).period_item .inside .period {
    position: absolute;
    top: 0.5ex;
    text-align: center;
    min-width: 80%;
}

#atmogramme_time_picker li.day ul li:not(.header).period_item .inside .period {
    position: static;
}

#zone-bulletin .zone-bulletin-video {
    width: auto;
}

#primary_section .row #primary_content .region-content .block-mf-hour-rain {
    width: auto;
}

#primary_section .region.region-content {
    display: flex;
    flex-wrap: wrap;
    width: 100%;
}

#primary_section .region.region-content > * {
    order: 100;
}

#primary_section #block-mfsitedetails {
    order: 0;
}

#primary_section #block-mfatmogrammev2 {
    order: 1;
    width: 100%;
}

#primary_section #block-mfweathercaption {
    position: absolute;
    right: 0;
}

#primary_section #block-mfhourrain {
    order: 2;
}

#block-autopromorain {
    display: none !important;
}

#primary_section #block-mfzonebulletin {
    width: auto !important;
    order: 3;
}
}
