style.css:
@-moz-document url("https://client.foldingathome.org/"),
url-prefix("http://127.0.0.1:7396") {
/*
	Dark Folding@Home (2020-04-21)
	Repository: https://github.com/CustomStyles/Dark-FoldingAtHome
				You are free to contribute or report any problems with the style.
	License:	https://creativecommons.org/licenses/by-sa/4.0/
*/
    body {
        min-height: 100vh;
        display: flex;
        flex-direction: column;
    }
    body,
    .header {
        background: #222;
    }
    #overlay {
        display: none;
        opacity: 1;
        background: rgb(34, 34, 34) none repeat scroll 0% 0%;
    }
    #loading {
        color: white;
    }
    #loading #throbber img {
        filter: invert(0.9);
    }
    #upgrade {
        background: #333;
        color: white;
    }

    #cp #menu {
        color: #ddd;
        margin: 0px 40px;
        width: Calc(100% - 80px);
    }
    #menu td.left {
        font-size: 1.25em;
    }
    .footer,
    #menu td.middle {
        display: none;
    }
    a {
        color: #c03;
    }
    .ui-dialog .ui-dialog-buttonpane button,
    a.btn:hover {
        background: #333;
    }
    a.btn {
        padding: 0.25em 1em;
        border-radius: 5px;
        background: #3a3a3a;
    }
    .ui-dialog .ui-dialog-buttonpane button {
        color: white;
        padding: 0.25em 0.5em;
    }
    .button,
    button {
        background: none;
    }
    button {
        padding: 0px 26px 2px;
        min-height: 36px;
        color: #fff !important;
        font-family: Oswald, Arial, Helvetica, sans-serif;
        font-size: 24px;
        border-radius: 0.3em;
        cursor: pointer;
    }
    .ui-dialog.ui-widget-content .ui-widget-header .ui-dialog-titlebar-close {
        min-height: 0;
    }
    .button.red,
    #btn-stop {
        border: none;
        background: #911;
    }
    .button.red:hover,
    #btn-stop:hover {
        background-color: #a11;
        border: none;
    }
    .button.green {
        border: none;
        background-color: #183;
    }
    .button.green:hover {
        border: none;
        background-color: #294;
    }
    .button.grey,
    button {
        border: none;
        background: #444;
    }
    .button.grey:hover,
    button {
        border: none;
        background: #4a4a4a;
    }
    input {
        border: none !important;
        padding: 0.25em 1em !important;
        background: #444 !important;
        color: white !important;
        border-radius: 0.5em;
    }

    #cp.panel {
        border: none;
        box-shadow: none;
        width: auto;
        border-radius: 0;
        padding-bottom: 4px;
        background: #333;
        color: white;
        flex: 1;
        margin: 0;
    }
    #cp .panel-header,
    #cp .panel-top,
    #cp .panel-pu,
    #slots.row.panel-pu li {
        background: #2d2d2d;
    }
    #slots.row.panel-pu ul {
        display: flex;
    }
    #slots.row.panel-pu li {
        margin-left: 8px;
    }
    #slots.row.panel-pu li:last-child {
        margin-right: 8px;
    }
    #cp .panel-header {
        padding: 1em 0;
    }
    .sbOptions,
    .sbSelector {
        background-color: #3a3a3a;
        color: white;
        filter: grayscale(1);
        box-shadow: none;
    }
    .sbOptions a:hover,
    .sbOptions a:focus,
    .sbOptions a.sbFocus {
        background: #444;
    }
    .sbOptions a:link,
    .sbOptions a:visited {
        color: white;
    }

    #slots.row.panel-pu li.active {
        background: #333;
        border-radius: 25px 25px 0 0;
    }
    .ui-progressbar {
        border: none;
        border-radius: 1em;
        background: #444;
    }
    .ui-progressbar .ui-progressbar-value {
        color: white;
        font-size: small;
        line-height: 1.175;
        padding: 0;
        margin: 0;
        text-indent: 5px;
    }
    #cp .hlarge {
        color: #db003e;
    }
    .box-pu li .pu .pu-type {
        color: #ccc;
    }
    .box-pu li .circle {
        border: none;
    }
    .box-pu li .circle.run {
        background: #2b2;
    }
    .box-pu li .circle.finish {
        background: #db0;
    }
    .box-pu li .circle.pause {
        background: #d40;
    }
    .box-pu li .run span,
    .box-pu li .finish span {
        filter: grayscale(1);
    }

    #box-all-slots {
        background: #2b2b2b;
    }
    .radio-selector span {
        background-image: none !important;
        border-radius: 0;
        border: solid 1px #ccc;
    }
    .radio-selector li input[type="radio"]:checked + label span {
        color: #fff;
        text-align: center;
    }
    .radio-selector li input[type="radio"]:checked + label span::after {
        content: "âœ“";
        font-size: 1.25em;
        margin: 0;
    }
    .ui-slider .ui-slider-handle {
        background-color: #999 !important;
        background-image: none !important;
        border-radius: 5px;
        width: 6px;
        margin-left: -4.5px !important;
        height: 25px;
    }
    .ui-slider .ui-slider-handle:hover,
    .ui-slider .ui-slider-handle:focus {
        background-color: #8c8c8c !important;
        background-image: none !important;
        cursor: pointer;
    }
    .ui-widget-header {
        background-color: #28e !important;
    }
    .ui-slider-horizontal {
        background: #d71 !important;
        border-radius: 0;
        border: none;
    }

    .ui-widget-header {
        background-image: none !important;
        border: none;
        border-radius: 0;
    }
    .ui-dialog.ui-widget-overlay {
        background-color: black;
    }
    .ui-dialog.ui-widget-content.ui-draggable .ui-dialog-titlebar {
        cursor: default;
    }
    .ui-dialog.ui-widget-content .ui-widget-header .ui-dialog-titlebar-close {
        background: none;
        border: 0;
    }
    .ui-dialog.ui-widget-content .ui-widget-header .ui-dialog-titlebar-close .ui-icon {
        filter: invert(1) brightness(2);
    }
    .ui-dialog.ui-widget-content {
        width: 100% !important;
        top: 0 !important;
        left: 0 !important;
        height: 100% !important;
        padding: 0;
        position: fixed;
        border: 0;
        background: #222;
    }
    .ui-dialog .ui-dialog-title {
        color: white;
        width: 100%;
    }
    .ui-dialog .ui-widget-content {
        color: white;
        max-height: 100% !important;
    }
    .ui-dialog .ui-widget-content h2 {
        font-size: 1.1em;
        background: #28e;
        padding: 0 1em;
        color: white;
        position: fixed;
        top: 0.4em;
        left: 0;
    }
    .ui-widget-content.ui-dialog-buttonpane {
        background: none;
    }
    .ui-dialog .ui-widget-content .green.button.done {
        display: none;
    }
    .ui-widget-content a {
        color: #5ba0b7;
    }
}

@-moz-document url-prefix("https://stats.foldingathome.org/") {
    html {
        background: #222;
    }
    body {
        color: gray;
    }
    a,
    a:visited {
        color: #39a;
    }
    button,
    input,
    select {
        border: none;
        padding: 0.25em 1em;
        background: #444;
        color: #aaa;
        border-radius: 0.5em;
    }
    input,
    select {
        border-radius: 2px;
    }
    #header a.title span {
        color: #ff7668;
    }

    #menu {
        display: flex;
        padding: 0;
    }
    #menu > li {
        flex: 1;
        background: #3a3a3a;
        border-radius: 0;
        border: none;
    }
    #menu > li:hover,
    #menu > li:focus {
        background: #333;
        opacity: 1;
    }
    #menu > li:hover a,
    #menu > li:focus a {
        color: white;
    }

    #content {
        background-color: #333;
        padding: 0 2em 1em;
    }
    #content h1 {
        color: #ccc;
        margin: 0.5em 0 0;
    }
    #content table {
        background: none !important;
        width: 100% !important;
        color: gray;
    }
    #content table.team-info tr > *,
    #content table.donor-info tr > *,
    #content table.donor-team tr > * {
        width: 50%;
    }
    #content table.results {
        border-spacing: 0;
    }
    #content table.results th,
    #content table.results tr.description td {
        background: #444 !important;
        border: none !important;
    }
    #content table.results tr.description td:empty {
        display: none;
    }
    #content table.results td {
        border: none !important;
    }
    #content table.results :nth-child(2n) > td {
        background-color: #373737 !important;
    }
    #content table.results :nth-child(2n + 1) > td {
        background-color: #3a3a3a !important;
    }
}
