style.css:
@-moz-document url-prefix("https://horriblesubs.info") {
/* Change J-List Ads to whatever you want, some banner albums listed below:
   http://imgur.com/a/ga1nG
   http://imgur.com/a/6YyJA */
img.img-responsive {
    content: url("http://i.imgur.com/U9ThxQs.png");
}
body {
    background-color: #111;
    color: #DDD;
    /* Change floating BG image on right as well as it's size */
    background-image: url("https://i.imgur.com/OP7i1bq.png");
    background-size: 480px;
}
.navbar-brand {
    background-image: none !important;
    color: #cc4040 !important;
}
.navbar-brand:hover {
    color: #DA4453 !important;
}
.navbar.navbar-default {
    background-color: #222;
    box-shadow: 1px 1px 4px #000;
}
.navbar-default .navbar-nav > li > a,
.dropdown-menu > li > a {
    color: #b94242;
}
.navbar-nav > li:hover > .caret {
    color: #f00;
}
.dropdown-menu {
    background-color: #222;
}
.navbar-default .navbar-nav .dropdown-menu > li:hover,
.navbar-default .navbar-nav .dropdown-menu > li:focus,
.dropdown-menu > .active {
    background-color: #484848;
}
.dropdown-menu > .active > a,
.dropdown-menu > .active > a:focus,
.dropdown-menu > .active > a:hover,
.dropdown-menu > .active > .caret,
.dropdown-menu > li > a:focus,
.dropdown-menu > li > a:hover,
.dropdown-menu > li:hover > a,
.dropdown-menu > li:hover > .caret {
    color: #DA4453;
}
a {
    color: #DA4453;
}
.navbar-default .navbar-nav > .active > a,
.navbar-default .navbar-nav > .active > .caret,
.navbar-default .navbar-nav > .active > a:hover,
.navbar-default .navbar-nav > .active > a:focus,
.navbar-default .navbar-nav > li > a:hover,
.navbar-default .navbar-nav > li:hover > a,
.navbar-default .navbar-nav > li > a:focus,
.navbar-default .navbar-nav > .open > a,
.navbar-default .navbar-nav > .open > a:hover,
.navbar-default .navbar-nav > .open > a:focus {
    color: #ca2c3c;
}
.alert-success {
    color: #ca2c3c;
    background-color: #282828;
    border-color: #ca2c3c;
}
.post-inner-content {
    background-color: #1b1b1b;
    box-shadow: 1px 1px 4px #181818;
}
.series-image img {
    box-shadow: 1px 1px 4px #181818;
}
.featured > .row > div .featured-item .featured-image {
    box-shadow: 1px 1px 4px #181818;
}
.well {
    background-color: #1b1b1b;
    box-shadow: 1px 1px 4px #181818;
}
.latest-releases {
    background: #1b1b1b;
}
.latest-releases .active {
    background: #252525;
}
.latest-releases .active a:hover {
    background: #383838 !important;
    color: #ca9999 !important;
}
.latest-releases a:hover,
.latest-show-more a:hover {
    background: #383838 !important;
    color: #ca9999 !important;
}
.latest-releases-res .badge,
.rls-label .badge {
    background-color: #282828a8;
}
.rls-info-container a:focus,
.show-more a:focus,
.rls-label:hover,
.entry-title {
    color: #ca2c3c;
}
.latest-releases-res:hover .badge:hover,
.rls-label:hover .badge:hover {
    background-color: #DA4453;
}
.latest-releases a:hover .badge,
.rls-label:hover .badge {
    background-color: #282828a8;
}
.la-badge {
    background-color: #585858;
}
.table-hover > tbody > tr:hover,
.schedule-table > tbody > tr:hover {
    background-color: #333 !important;
}
.latest-releases a,
.latest-show-more a {
    color: #ca2c3c;
}
#secondary .widget a {
    color: #ca9999;
}
a:hover,
a:focus {
    color: #ca9999;
}
#secondary .widget a:hover{
    color: #ca2c3c;
}
#secondary .widget a:hover {
    background: transparent;
}
.post-navigation a,
.page-numbers,
.pagination .prev,
.pagination .next {
    border: 1px solid #484848;
    background-color: #383838;
}
.post-navigation a:hover,
.pagination .prev:hover .pagination .next:hover {
    color: #FFF;
    background: #484848;
    border: 1px solid #585858;
}
.entry-meta a {
    color: #ffffff;
}
a .li:hover {
    background-color: #00ff00;
}
.widget ul li {
    border-bottom: 1px solid transparent;
    border-bottom-color: transparent;
}
.table > tbody > tr > td {
    border-bottom: 1px solid transparent;
}
.table-hover > tbody > tr:hover {
    background-color: #484848!important;
}
.form-control {
    color: #ffffff;
    background-color: #282828;
    border-color: #181818
}
.hs-shows {
    border-top: 1px solid transparent;
}
table,
tr,
td {
    border: 1px solid transparent;
}
.schedule-widget-time  {
    color: #e63144;
}
.latest-releases ul li {
    border-bottom: 1px solid transparent;
}
#disqus_thread {
    background: #222;
    box-shadow: 1px 1px 4px #181818;
}
.ind-show:hover {
    background-color: #484848!important;
}
.rls-label:hover {
    background: #484848 !important;
}
.rls-info-container {
    border-bottom: 1px solid transparent;
}
.schedule-today {
    background-color: #484848;
}
h4.hrf-title {
    color: #ffffff;
    background-color: #484848;
}
.hrf-content {
    color: #ffffff;
}
h1,
h2,
h3,
h4,
h5,
h6,
.h1,
.h2,
.h3,
.h4,
.h5,
.h6 {
    color: #ca2c3c;
}
.hrf-title.close-faq span {
    background-color: #383838;
}
}

@-moz-document url-prefix("https://disqus.com/embed/comments/?base=default&f=horriblesubs") {
/*-------------Main Text Colour--------------*/
html, body, #container-page, .site-nav--wrapper, #container-main, .more-wrapper, .aside__wrap, .card-wrap, .drawer-footer, .nav-notifs, .nav-tabs, .discussion-page, .textarea-wrapper .textarea, .post-actions, .postbox .media-preview, .textarea-wrapper .placeholder, .card-profile, .text-large, .cover-attribute-details .profile-cover-list li, .card-channel .card-reason, .card-generic .card-reason, .card-profile .card-reason, .cover, .profile-user-info, .text-smaller, .text-gray, .reading-block, .card__additional, .text-title, .text-gray-darker, p, h2, .textarea
{color: #ddd !important;
background: #222 !important;}

/*------------------------------------------------*/

.active .publisher-nav-color::after 
{background: #e63939 !important;}

.post-content.new .indicator,
.post-content.target .indicator 
{background: #e63939;}

.nav__item, .nav--no-aside, .nav__spacing 
{color: white !important;
background: #222 !important;
border: 0 !important;}

.button, .button--recommend, .button-small, .textarea-wrapper .textarea, .home-drawer .cover-profile, .home-drawer .notifs-fixed, .sidebar-scrollable.profile-scrollable.scrollable-measure-selector, .highlighted
{background: #222 !important;}

.input-block-level, .btn
{color: #e63939 !important;
background: #222 !important;
margin: 0 !important;
box-shadow: none !important;}

.card-comment .comment-message, .icon-flag, .collapse span, .expand span, .nav-primary>ul>li.notification-menu a .notification-count, .alert, .alert a:active:hover, .alert a:hover, .alert a:visited:hover
{color: white !important;}

.post-content .realtime .realtime-replies
{color: #e63939 !important;}

a, .publisher-anchor-color a, .nav .dropdown-toggle, .nav-primary > ul > li.active > a, .user, .comments, .time, .label-count, h2 > strong
{color: #e63939 !important;}

.stats, .bio, .content > p
{color: #777 !important;
background-color: #fff !important;}

.post-actions
{background-color: #222 !important;}

.btn-follow
{background-color: #333 !important;}

.home-drawer .cover-profile-name, .truncate-line
{color: azure !important;}

.post-content .post-body spoiler:focus,
.post-content .post-body spoiler:hover 
{background: #252525;
 color: inherit}

/*-------------Mod Badge Colour--------------*/

span.badge.moderator
{background-color: #777 !important;
color: #fff !important;}

/*-------------Comment Footer Colours--------------*/

.post-content footer .voting .vote-down, .voting .vote-up, span.text, .count
{color: #e63939 !important;}

/*-------------Voting Colours--------------*/

.post-content footer .voting .vote-up.upvoted .control, .post-content footer .voting .vote-up.upvoted .count 
{color: #00BF29 !important;}

.post-content footer .voting .vote-down.downvoted .control, .post-content footer .voting .vote-up.downvoted .count 
{color: red !important;}

span.updatable.count.update 
{color:yellow !important;}

/*-------------No Ads--------------*/

#AdSense3
{display: none !important;}
}

@-moz-document url("https://free.timeanddate.com/clock/i4vybhfo/n137/fn17/th1/ta1") {
/* Background */
  #c1 {
	   background: none !important;
  }
  
  /* Text */
  #t1, #t1a {
    color: #FF0000 !important;
  }
}
