}

/* Round configuration (in admin > Brackets) */
[class*="regionWrapper-APP_TOURNAMENT_PAGE-FeatureCanvas-paper"], Div#help-block:hover {
    background-color:var(--bg-dark);
    color: var(--text-white-2);
}
.nav-tabs>li.active>a, .Nav--tabbed>li.active>a, .nav-tabs>li.active>a:hover, .Nav--tabbed>li.active>a:hover, .nav-tabs>li.active>a:focus, .Nav--tabbed>li.active>a:focus {
    color: hsl(208, 100%, 71%);
}
.Nav>li>a:hover {
     color:hsl(208, 100%, 78%);
}

/* Confirm window */
.sggFWUTf.sgg3-sWS {
    background-color: var(--bg-dark);
    color: var(--text-white);
}

/* Text boxes */
.legacyFormElement .input-group-addon, .legacyFormElement .input-group-btn, .legacyFormElement .input-group .form-control {
    background-color: var(--bg-dark-2);
    color: var(--text-white-2);
        border-color: var(--border-color)
}

/* MATCH WINDOW */
.MatchHeader, .report-modal .modal-container {
    background: var(--bg-dark)
}
.report-modal .modal-tabs .tab-pane, .match-display .modal-tabs .tab-pane, .team-roster-cell {
    background-color: var(--bg-dark-2);
    border-color: var(--border-color-1b)
}

/* Active tab text color */
.report-modal .modal-tabs .navbar-nav li.active>a, .match-display .modal-tabs .navbar-nav li.active>a {
    color: var(--text-white)
}

/* Inactive tab hover color */
.nav>li>a:hover, .Nav>li>a:hover, .nav>li>a:focus, .Nav>li>a:focus {
    border-color: var(--bg-dark-3) !important
}

/* Match round (not started) */
.sggjooyc.sgg2jTHe, .sggjooyc.sgg3As8U {
    background-color: hsl(210, 40%, 37%)
}
/* Match round (started) */
.sggjooyc.sgg7a-5i {
    background-color: hsl(125, 40%, 27%)
}

/* Match round header, entrant score */
.gg-color-white, .EntrantScore {
    color: var(--text-white)
}

/* Summary > stage name */
.GameStat__title {
    color: var(--text-white-2)
}

/* Player header before reporting game */
.report-modal .player-report.player-report-columns, .match-display .player-report.player-report-columns {
    background: none;
    border: none;
}

/* Report > winner overlay color */
.gg-list-group-item-success {
    background-color: hsl(125, 17%, 33%)
}

/* "Help with brackets", "Help and support" delet */
.sgg2h1cC.DEPRECATED_bootstrap_container.undefined.top-spacer, .sgg22tpX, .sgguGUip {
    display: none   
}

/* "Homepage" corner thing in events */
.sgg3VBhT .sgg2lWe6 {
    background-color: var(--bg-dark);
    color: var(--text-white);
}
.sgg1ovLa .sggmZGST .sgg2pTAp {
    background: var(--bg-dark-2);

}
.sggmqqYM .sgg3Xff8, .sggmqqYM .sgg3RXp-, Div#custom-intercom-opener {
    display:none
}

/* Messenger button in bottom-right */
.intercom-lightweight-app, .intercom-rkaeus, #intercom-container-body, .intercom-namespace .intercom-app {
    display:none;
}
}
