style.css:
@-moz-document domain("kivinet.de"), domain("www.kivinet.de") {
/* Ich weiß das CSS ist nicht gut gemacht und auch ehrlich gesagt nur schnell hingepfuscht */
html,
body,
.std {
    background-color: #121212;
    color: lightgray;
}
.ilMainHeader {
    background-color: #232323;
    border-bottom: 3px solid #3c3b3b;
}
div.il_Block,
#il_center_col div.il_Block,
.ilCLI,
div.ilBlockInfo,
.ilCalSelAct,
.form-horizontal,
.ilFormHeader,
.ilFormHeader,
.ilFormFooter,
#ilTopBarNav .dropdown-menu,
.popover,
.ilToolbar,
.ilTableSelectAll,
div.ilTableCommandRow,
div.ilTableCommandRowTop,
.table > thead > tr > th,
.table-striped > tbody > tr:nth-of-type(odd),
div.ilNotes,
div.ilc_text_block_Example,
body.ilc_text_block_Example,
.ilInfoScreenSec.form-horizontal .form-group,
div.ilc_text_block_Remark,
body.ilc_text_block_Remark,
div.ilc_text_block_Information,
body.ilc_text_block_Information,
div.ilc_text_block_Confirmation,
body.ilc_text_block_Confirmation,
div.ilc_page_cont_PageContainer,
.ilMainMenu,
.ilOverlay,
.ilFileUploadEntryHeader,
.ilFileUploadEntryOptions,
#ilFileUploadLists .ilFileUploadListTitle,
div.ilFileUploadHeader,
td.ilc_table_cell_brandprimarysecondaryc1,
th.ilc_table_cell_brandprimarysecondaryc1,
.il-item,
.ilTimelinePanel,
div.tblfooter {
    background-color: #232323 !important;
}
.il_InfoScreenPropertyValue {
    background-color: #2f2f2f !important;
}
.table-responsive {
    border: 1px solid lightgray;
}
#il_mhead_t_focus {
    color: #5dadea;
    font-weight: bold;
}
.form-control,
.form-control[disabled],
.form-control[readonly],
fieldset[disabled] .form-control,
div.ilProfile {
    background-color: #242424;
    color: white;
}
.ilCLI:hover {
    background-color: #232323;
}
input:invalid,
div.ilc_text_block_Attention,
body.ilc_text_block_Attention {
    background-color: #281919;
}
.btn-primary {
    background-color: #2d3b4f;
    border-color: #1e2835;
}
#left_nav {
    background-color: #232323;
    border-right: 3px solid #3c3b3b;
}
h3,
.small,
.subtitle,
.xsmall,
#ilTopNav .navbar-nav > li > a,
.form-horizontal label,
#current_perma_link,
li.ilc_list_item_StandardListItem,
div.ilc_text_block_Example,
body.ilc_text_block_Example,
div.ilc_text_block_Remark,
body.ilc_text_block_Remark,
div.ilc_text_block_Information,
body.ilc_text_block_Information,
div.ilc_text_block_Confirmation,
body.ilc_text_block_Confirmation,
h1.ilc_page_title_PageTitle,
div.ilc_text_block_PageTitle,
body.ilc_text_block_PageTitle,
h1.ilc_heading1_Headline1,
div.ilc_text_block_Headline1,
body.ilc_text_block_Headline1,
h2.ilc_heading2_Headline2,
div.ilc_text_block_Headline2,
body.ilc_text_block_Headline2,
div.ilc_text_block_Zwischenueberschrift,
body.ilc_text_block_Zwischenueberschrift,
div.ilc_text_block_Standard,
body.ilc_text_block_Standard,
div.ilc_page_cont_PageContainer,
.Lj9fsd.DU1Mzb {
    color: lightgray;
}

.dropdown-menu li > a:hover,
.dropdown-menu li > a:focus:hover {
    color: black;
}

h3.ilHeader {
    font-weight: bold;
    color: white;
}
h3.ilProfileName {
    font-weight: bold;
    color: white;
}
a,
.btn-link,
a:hover,
.btn-link:hover,
.il_ContainerItemTitle:active,
.il_ContainerItemTitle:visited,
.il_ContainerItemTitle {
    color: #5dadea;
}

a.ilTreeView,
a.ilTreeView > img {
    background-color: #232323;
    border: #232323
}
#ilFooter {
    background-color: #161e28;
}
#ilTopNav .dropdown-menu,
.dropdown-menu {
    background-color: #282828;
}
.dropdown-header {
    background-color: #515050;
    color: #fff;
}
#ilTopNav .dropdown-menu li a {
    color: #c6bfbf;
}
#ilTopNav .navbar-nav > .active > a,
#ilTopNav .navbar-nav > .open > a,
#ilTopNav .navbar-nav > .active > a:hover,
#ilTopNav .navbar-nav > .open > a:hover,
#ilTopNav .navbar-nav > .active > a:active,
#ilTopNav .navbar-nav > .open > a:active,
#ilTopNav .navbar-nav > .active > a:focus,
#ilTopNav .navbar-nav > .open > a:focus {
    background-color: #2d2c2c;
}
.jstree.jstree-default a.disabled {
    color: gray !important;
}
.jstree.jstree-default a {
    color: #5dadea !important;
}
#ilTopNav .navbar-nav > li > a {
    background-color: #515050 !important;
    border-top: 2px solid darkgray;
    color: lightgray;
}
#ilTopNav .navbar-nav > li > a:hover {
    color: white;
    border-top: 2px solid white;
    background-color: #5e5c5c !important;
}
#ilTopNav .navbar-nav > .active > a,
#ilTopNav .navbar-nav > .open > a,
#ilTopNav .navbar-nav > .active > a:hover,
#ilTopNav .navbar-nav > .open > a:hover,
#ilTopNav .navbar-nav > .active > a:active,
#ilTopNav .navbar-nav > .open > a:active,
#ilTopNav .navbar-nav > .active > a:focus,
#ilTopNav .navbar-nav > .open > a:focus {
    background-color: #5e5c5c !important;
    border-top: 2px solid white;
    color: lightgray;
}
.alert-info {
    background-color: #232323;
    border: 1px solid #8e8d8d;
}
div.ilc_text_block_Mnemonic,
body.ilc_text_block_Mnemonic,
a.il_HighlightedNode,
.ilHighlighted {
    background-color: #3c3c23;
}
/* Vertretungsplan */
#nav2,
#nav3,
#reihe1,
#reihe0 {
    background-color: #484545;
}

/* Kalender */
table.calmini td {
    background-color: #121212;
    color: white;
}
table.calmini tr,
table.calmini th {
    background-color: #232323;
}
table.calmini {
    border: 1px solid white !important;
}

.calminitoday {
    background-color: gray !important;
    color: black !important;
}

.xsmall:hover {
    color: black
}

/* Scrollbar Chrome*/
*::-webkit-scrollbar {
    width: 10px !important;
    height: 10px !important;
    background: #1e1e1e !important;
    border: 1px solid #252525 !important;
}
*::-webkit-scrollbar-button {
    display: none !important;
}
*::-webkit-scrollbar-thumb,
*::-webkit-scrollbar-track {
    border: 0 !important;
    box-shadow: none !important;
}
*::-webkit-scrollbar-thumb {
    min-height: 28px !important;
    background: #333 !important;
}
*::-webkit-scrollbar-corner,
*::-webkit-scrollbar-track {
    background: #1e1e1e !important;
}

ilContainerWidth {
    max-width: 1600px;
}

.ilLeftNavSpace {
    margin-left: 415px !important;
}

.ilLeftNav {
    width: 400px;
}

.jstree-default .jstree-node {
    margin-left: 12px;
}

@media only screen and (max-width: 1200px) {
    .ilLeftNav {
        /* at last temporary, since it overlaps content */
        display: block;
    }
}

::selection {
    background: #999;
    color: #fff;
}
::-moz-selection {
    background: #999;
    color: #fff;
}

.il_HAccordionHeadActive,
.il_VAccordionHeadActive {
    background-color: #959594;
}
}
