style.css:
@-moz-document domain(pan.baidu.com) {
	/* 皮肤 */
    a.icon[title="皮肤中心"]{
        display:none
    }

    .g-button[title = "智能设备"]{
        display:none
    }

    /* 动态 */
    .module-header-wrapper[node-type="module-header-wrapper"] [node-type="pan-dynamic"]{
        display:none
    }

    /* 一刻相册 */
    .module-header-wrapper[node-type="module-header-wrapper"] [node-type="photo"]{
        display:none
    }

    /* 找资源 */
    .module-header-wrapper[node-type="module-header-wrapper"] [node-type="pan-mall"]{
        display:none
    }

    /* 超级会员低价限时抢 */
    .module-header-wrapper[node-type="module-header-wrapper"] [node-type="header-union"]{
        visibility:hidden
    }

    /* 卡点视频 */
    .module-header-wrapper[node-type="module-header-wrapper"] [node-type="find-apps"] .director-icon{
        display: none
    }

    /* 通讯录 */
    .module-header-wrapper[node-type="module-header-wrapper"] [node-type="find-apps"] .contacts-icon{
        display: none
    }

    /* 通话记录 */
    .module-header-wrapper[node-type="module-header-wrapper"] [node-type="find-apps"] .callLog-icon{
        display: none
    }

    /* 短信 */
    .module-header-wrapper[node-type="module-header-wrapper"] [node-type="find-apps"] .message-icon{
        display: none
    }

    /* 文章 */
    .module-header-wrapper[node-type="module-header-wrapper"] [node-type="find-apps"] .article-icon{
        display: none
    }

    /* 记事本 */
    .module-header-wrapper[node-type="module-header-wrapper"] [node-type="find-apps"] .note-icon{
        display: none
    }

    .module-header-wrapper[node-type="module-header-wrapper"] [node-type="find-box"] {
        height: 100px
    }
}
