style.css:
@-moz-document url-prefix("https://todoist.com") {
html, body {
    font-family: IRANYekan;
}

.view_content .items {
    direction: rtl;
}

.task_list_item .task_list_item__content .task_content {
    text-align: right;
}

.task_list_item .task_list_item__content {
    margin-right: 10px;
    margin-left: 30px;
}

.task_list_item__actions { 
	right: unset;
    left: 0;
}

.plus_add_button {
	opacity: 0.2;
}


.view_header__content{
    direction: rtl;
}

#editor .arrow {
    margin-right: -27px;
    padding-left: 3px;
    padding-right: 3px;
}

.task_list_item[data-item-indent="2"] {
    margin-left: 0;
    margin-right: 28px;
}

.section_head__title { 
    direction: rtl;
    padding: 6px 10px 5px 30px !important;
}

.section_head__title small {
    padding-right: 10px;
}


.section header button.section_head__title, .section header h2 {
    text-align: right;
    font-family: IRANYekanFN !important;
}

.name {
    font-family: IRANYekanFN ;
}

}
