style.css:
@-moz-document domain(forum.minecorp.ru) {
body {
    background-image: none;
    background-color: #1e1e1e;
}

#brd-navlinks {
    background-color: #242424;
}


#brd-navlinks p, #brd-navlinks ul li a {
    color: #e6e6e6;
}

#brd-navlinks ul li.isactive:hover {
    background-color: #127a00;
}

#brd-navlinks ul #navuserlist a {
    background-image: url(https://i.ibb.co/Wp2sqBJ/users.png)
}

#rules-content {
    color: #e6e6e6;
}

.notification {
    color: #1e1e1e;
}

.main-head, .hn, .main-head, .option {
    margin-top: 0;
}

.main-head, .main-menu {
    background-color: #1e1e1e;
    border: 1px solid #e6e6e6;
}

.profile {
    color: #e6e6e6;
}

#brd-navlinks ul #navsearch a {
    background-image: url(https://i.ibb.co/60RMVrf/search.png);
}

#brd-navlinks ul #navlogout a {
    background-image: url(https://i.ibb.co/M8Qbzpp/exit.png);
}

.main-content .main-item .icon {
    background-image: url(https://i.ibb.co/Lpw2T2y/status.png) !important;
}

.fancy_spoiler_switcher_header {
    color: #127a00;
}

.fancy_spoiler_switcher {
    background: #cfcfcf;
}

.main-subhead {
    background: #1e1e1e;
}

#brd-navlinks li.isactive {
    background-color: #0a4500;
}

#brd-navlinks ul li.isactive a {
    color: #cfcfcf;
}

#brd-navlinks ul li:hover a {
    color: white;
}

#brd-announcement {
    display: none;
}

.logo {
    text-align: center;
    background-image: none;
    background: url(https://i.ibb.co/5Y5sNSV/MINECORP-LOGO.png) center center/cover no-repeat;
    background-size: 180px;
    float: none;
}

a.logo {
    display: inline-block;
}

#brd-head {
    text-align: center;
}

#brd-wrap {
    padding-top: 0;
}

.main-head {
    background: #242424;
    border: 1px solid lightgray;
}

.item-summary {
    background-color: #282828;
    color: #cfcfcf;
}

.main-item.odd {
    background-color: #292929;
    color: #696969;
}

.main-item {
    background-color: #1e1e1e;
    color: #696969;
}

a:link, a:visited, .js_link {
    color: #127a00;
}

li.info-posts strong, li.info-replies strong, li.info-topics strong, li.info-views strong {
    background: #6b6b6b;
    color: white;
}

.main-extensions, #brd-stats, #brd-online, #brd-qpost {
    background-color: #1e1e1e;
    color: #cfcfcf;
}

.even {
    color: #6b6b6b;
}

.posting {
    color: #6b6b6b;
}

ul li strong {
    color: #127a00;
}

.main-item.even {
    background-color: #1e1e1e;
}

.main-foot {
    background-color: #1e1e1e;
    color: #cfcfcf;
}

.main-foot .options a:hover {
    color: #127a00;
}

.main-head .options .sub-option {
    background-color: #292929;
    color: #cfcfcf;
}

.item-status em.closed {
    background-color: #242424;
}

.post, .odd, .firstpost, .topicpost {
    background-color: #1e1e1e;
    color: #cfcfcf;
}

.postbody, .post-options, .posthead {
    background-color: #242424;
}

.brd .entry-content .quotebox {
    background-color: #1e1e1e;
}

.quotebox {
    background-color: #1e1e1e;
    color: #cfcfcf;
}



label, .aslabel, .mf-set legend, .ct-legend, .ct-legend span, .ct-legend cite, .ct-legend strong {
    color: #cfcfcf;
}

.sf-set .text input, .txt-set textarea {
    background: #cfcfcf;
}

#afocus {
    background-color: #1e1e1e;
    color: #cfcfcf;
}

.main-frm {
    background: none;
}

.item-status em.sticky {
    background-color: #242424;
}

.main-content .main-item .hn a .tag {
    background-color: #242424;
    color: #cfcfcf;
}

.notification {
    background-color: #cfcfcf !important;
}

.crumb,.crumblast {
    color: #6b6b6b;
}


}
