style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.roguevampires.net") {

  table[style="border-left: 1px solid rgb(102, 102, 102); border-right: 1px solid rgb(102, 102, 102); margin: 0pt auto; background: transparent url(images/CH_RV_HeaderGraphic.jpg) repeat scroll 0% 0%; -moz-background-clip: -moz-initial; -moz-background-origin: -moz-initial; -moz-background-inline-policy: -moz-initial; width: 864px; height: 123px;"] {
    display: none !important;
  }
  body > p { display: none !important; }
  table[class*="main"] { width: 90% !important; }
  td[class*="menu"] {
    background: #A1A1A1 !important;
    padding-top: 5px !important
  }
  td[class="main"] div { width: 90% !important; }
  td[class="main"] hr { width: 90% !important; }
  td[class="main"] > p { text-align: center }
  p[style="padding-left: 40px;"] {
    padding-left: 0 !important;
    text-align: center !important;
  }
  DIV[style="border: 1px solid rgb(153, 153, 153); margin: 0px 70px; background-color: rgb(238, 238, 238); padding-left: 60px; padding-right: 60px;"] {
    padding-left: 0 !important;
    padding-right: 0 !important;
    margin: auto !important;
  }
}
