style.css:
@-moz-document domain("keylol.com") {
:root {
/*
*文字颜色
*/
  --textlight: #eeeeee;
  --textgray: #9f9e9e;

/*
*主题色
*/
  --grey850: #373737;
  --grey750:#505050;
    
    
    
    
    
    }
}

@-moz-document domain("keylol.com") {
	/*region 导航栏背景*/
	#nav-menu {
	    background: var(--grey800);
	}
	/*region 导航栏下拉菜单*/
	#nav-menu > ul > li:after {
	    background: var(--grey600);
	}
	#nav-menu > ul > li > ul {
	    background: var(--grey800);
	}
		#nav-menu > ul > li.has-sub > ul > li {
	    background: var(--grey800);
	}
	#nav-menu > ul > li.has-sub > ul > li:hover {
	    background: var(--grey600);
	}
	
	#nav-menu ul li ul li a {
	    color: var(--textlight);
	}
	/*region 搜索框*/
	.search-bar {
	    border: 1px solid var(--grey800);
	}
	.search-bar:hover {
	    background: var(--grey700);

	}
	.search-bar input {
	    color: var(--white1);
	}
input:-webkit-autofill {
    transition: background-color 5000s ease-in-out 0s;
}
/* region 头像弹出菜单*/
	#nav-user-action-bar .dropdown-menu.dropdown-menu-right {
	    background: var(--grey800);
	}
	
	#nav-user-action-bar .divider {
	    background: var(--grey800);
	}
#nav-user-action-bar ul li ul li a {
    color: var(--textlight);
	}
		
	.dropdown-menu > li > a:hover,
	.dropdown-menu > li > a:focus {
	    background: var(--grey600);
	}
	#J_ScrollTopBtn {
	    background-color: var(--grey700) !important;
	}
	#nav-user-action-bar .btn.btn-user-action:hover {
	    background-color: var(--grey700);
	}
}

@-moz-document regexp("https?://keylol.com/(forum.php)?") {
/*
	 * 帖子内容
	 * post.css
	 */
	/* region 主题色的背景条*/
	body:before {
	    background: var(--grey850);
	}
	.subject_row_detail_text_down {
	    color: var(--textlight) !important;
	}

	/* 背景色 */
	
	#wp > div:nth-child(even) {
	    background:var(--grey850);
	}
	
	#wp > div:nth-child(odd) {
	    background:var(--grey800);
	}
	
	#wp > div:nth-child(2) {
	    background: var(--grey850);
	}
/* tab */	
	ul.tb.cl > li {
	    background: var(--grey800);
	    color: white;
	}
	
	ul.tb.cl > li:hover {
	    background: var(--grey750);
	}
	
	ul.tb.cl > li a {
	    color: var(--textgray);
	}
	
	.frame-tab .tb li.a a {
	    color: white;
       font-weight: bold
	}
	#diy1 span.title {
	    background: var(--grey850) !important;
	}
	.frame-tab .xl::-webkit-scrollbar-thumb {
	    background-color: var(--grey500);
	}


}

@-moz-document url-prefix("https://keylol.com/t"), url-prefix("https://keylol.com/forum.php?mod=viewthread&t") {
/*
	 * 帖子内容
	 * post.css
	 */
	/* region 主题色的背景条*/
	body:before {
	    background: var(--grey850);
        -webkit-box-shadow: 0 6px 6px -3px rgba(0, 0, 0, .2), 0 10px 14px 1px rgba(0, 0, 0, .14), 0 4px 18px 3px rgba(0, 0, 0, .12);
	    box-shadow: 0 6px 6px -3px rgba(0, 0, 0, .2), 0 10px 14px 1px rgba(0, 0, 0, .14), 0 4px 18px 3px rgba(0, 0, 0, .12);
	}
	/* region 蓝色的横带子*/
	.ad .pls,
	.ad .plc {
	    background: var(--grey700);
	}
.ad .pls, .ad .plc {
    height: 3px;
}
/*页码*/
	.pg strong {
	    color: var(--black1);
	    background-color: var(--gray700);
         font-weight: bold
	}
	.pg a {
	    color: var(--grey500);
	}
		.pg a:active {
	    color: var(--black1);
	    background-color: var(--grey700);
	}
/* region 上一页按钮*/
	.pg a.prev:active {
	    background: var(--grey800);
	}
	
	.pg a.prev:after {
	    border-color: transparent var(--grey800) var(--grey800) transparent;
	}

	
	/* endregion*/
	
	/* region 下一页按钮*/

	.pg a.nxt:active{
	    background: var(--grey800);
	}	
   .pg a.nxt:after  {
	    border-color: transparent var(--grey800) var(--grey800) transparent;
	}
	/* region 回复区域*/
	#f_pst form {
	    border-top: 3px solid var(--grey700);
	}
	/* endregion*/
/* region 评分、举报*/
	
	.pob.cl > p > a:not(:nth-child(1)):active {
	    background: var(--grey500);
	}
	/* region 点评、回复、支持*/
	.pob.cl > em > a:active {
	    background: var(--grey500);
	}
	#fastpostsubmit {
	    color: var(--white1) !important;
	    background: var(--grey800) !important;
	}
/*网页背景*/
body
{
	background: var(--grey800);
}
/*弹出的回复框*/
button.pn, a.pn, .tb .o, .tb .o a{
    background: var(--grey800);
}


}

