#browser .tabbrowser-strip {
   visibility: visible !important;
}

/* Don't display tabs in print preview, it causes nothing but problems... */
#print-preview-toolbar + #navigator-toolbox + #browser .tabbrowser-strip {
   visibility: collapse !important;
}
