style.css:
@-moz-document domain("v3rmillion.net") {
/* Ads System */
ins {
    display: none !important;
}
div#sharingPlace {
    display: none !important;
}

/* Top Image logo */
#logo img {
    max-height: 45px;
    margin-top: -30px;
    visibility: hidden;
}

/* background image & general */
body {
    height: 100%;
    background-image: url(https://i.imgur.com/4my6JmD.jpg) !important;
    background-attachment: fixed !important;
    background-position: center center !important;
    background-repeat: no-repeat !important;
    background-size: cover;
    background-color: #101010;
    overflow-y: scroll;
}

/* Text box on thread */

a:link, a:visited {
    color: #ffffffbd;
    text-decoration: none;
}


#bridge {
    background: #15151538;
    height: 45px;
    border-bottom: 5px solid #dcdcdc99;
}

#wrapper {
    background: #0a0a0a00;
    padding: 20px 0;
}

.navigation {
    border-radius: 14px;
    background: #ffffff3b;
    border: 1px solid transparent;
    margin: auto auto 15px;
}
.navigation .active {
    color: #fff;
}
#container {
    width: 90%;
    text-align: left;
    line-height: 1.4;
    margin: 0 auto;
    margin-left: 30px;
}
#panel {
    float: right;
    margin-right: 75px;
}
.ddm_anchor {
    position: relative;
    float: left;
    height: 45px;
    margin-right: -150px;
}
ul.panel_links {
    list-style: none;
    float: right;
    padding: 0;
    margin: 0;
    margin-left: -200px;
}

#content {
    background: linear-gradient(0deg, rgba(2,0,36,0) 0%, rgba(77,77,77,0.4) 0%, rgba(77,77,77,0.35) 0%, rgba(77,77,77,0.35) 0%, rgba(0,0,0,0.35) 0%, rgba(0,0,0,0.35) 30%, rgba(255,255,255,0.35) 100%, rgba(74,74,74,0.2) 100%, rgba(0,0,0,0.3) 100%);
    border: 0px solid #2a2a2a;
    border-radius: 14px;
    padding: 20px;
    padding-bottom: 0;
    margin-bottom: 20px;
    overflow: hidden;
}

table {
    color: #e4e4e4;
    font-size: 16px;
}

.trow1, .trow2 {
    background: #1f1f1f00;
    border-bottom: 1px solid #b3b3b3;
}

.tborder {
    background: #00000061;
    margin: auto;
}
.tborder tbody tr:last-child>td:first-child {
    -moz-border-radius-bottomleft: 6px;
    -webkit-border-bottom-left-radius: 6px;
    border-bottom-left-radius: 6px;
}

.thead {
    background: linear-gradient(360deg, rgba(2,0,36,0) 0%, rgb(0 0 0 / 0%) 0%, rgba(255,76,0,0.2665441176470589) 0%, rgba(230,132,67,0.28335084033613445) 0%, rgba(0,142,255,0.227328431372549) 20%);
    padding: 10px;
}

.tcat {
    background: #0000003d;
    padding: 6px 10px;
    font-size: 12px;
}

.tfoot {
    padding: 10px;
    background: #40404047;
    color: #e0e0e0;
}

#footer {
    position: relative;
    background: #00000042;
    border-top: 5px solid #dadada8c;
    border-radius: 16px;
    clear: both;
}
td.tfoot.smalltext {
    background-color: #00929200 !important;
    background: linear-gradient(0deg, rgba(2,0,36,0) 0%, rgba(0,0,0,0) 0%, rgba(0,0,0,0.3) 0%, rgba(77,77,77,0.2) 25%, rgba(255,255,255,0.3) 80%);
}

.profile_header {
    background: #1f1f1f52 !important;
}

ul.bottommenu {
    display: inline-block;
    vertical-align: top;
    background: #20202000;
}

.cRem a:visited {
    color: #b7b7b77a !important;
}
.cRem a:link {
    color: #adadad !important;
}
.trow_sep {
    background: linear-gradient(180deg, rgba(2,0,36,0) 0%, rgba(0,0,0,0) 0%, rgba(0,0,0,0.3) 0%, rgba(77,77,77,0.2) 23%, rgba(255,255,255,0.3) 70%);
    color: #e8e8e8;
    padding: 6px;
}

.reputation_positive {
    color: #87e0ef;
}

.reputation_negative {
    color: #ff6464;
}

fieldset {
    padding: 12px;
    border: 0px;
    margin: 0;
}

.panel_buttons {
    z-index: 9999;
    display: none;
    position: absolute;
    top: 45px;
    background: #ffffff3b;
    width: 180px;
    overflow: hidden;
    margin: 0;
    padding: 10px 0;
    border-top: 5px solid #dcdcdc99;
    text-align: left;
}

ul.menu li ul {
    z-index: 9999;
    position: absolute;
    background: #ffffff2e;
    padding: 10px 0;
    border-top: 5px solid #ffffff3b;
}

a.button:link, a.button:visited, a.button:active {
    background: #00ffd030;
    display: inline-block;
    padding: 4px 8px;
    margin: 2px 2px 6px;
    border: 1px solid #cd181800;
}

.ddm_anchor:hover, .ddm_anchor a:hover {
    background: #9696962e;
}

ul.menu li:hover a, ul.menu li a:hover {
    background: #6af9f90f;
}

ul.menu li ul li a:hover {
    background: rgb(106 249 249);
    color: #fff;
}

ul.menu li ul li a:link, ul.menu li ul li a:visited {
    background: #2a2a2a00;
    padding: 10px 15px;
}

.inline_row a {
    color: #ecececd6;
    font-size: 90%;
}
.inline_row a:visited {
    color: #fffffff2;
    font-size: 90%;
}
.forum_on {
    color: #bdbdbd;
}

.sub_control {
    background-color: #20202000;
    border: 1px solid #d3d3d3;
    border-radius: 12px;
    padding: 5px 10px;
}
.sub_control:hover {
    background-color: #00000054;
   -moz-transition: background 1s ease-in;
   -o-transition: background 1s ease-in;
   -webkit-transition: background 0.1s ease-in;
}

a:hover, a:active {
    color: #FFFFFF;
    text-decoration: none;
    -moz-transition: color 1s ease-in;
    -o-transition: color 1s ease-in;
    -webkit-transition: color 0.1s ease-in;
}

.pagination a {
    background: #b5b5b578;
    border: 1px solid #40404000;
    color: #fff;
}
.pagination a:hover {
    background: #ffffff82;
    border-color: #40404000;
    text-decoration: none;
    -moz-transition: background 1s ease-in;
    -o-transition: background 1s ease-in;
    -webkit-transition: background 0.1s ease-in;
}
.pagination .pagination_current {
    background: #00000078;
    color: #fff;
    border: 1px solid #ffffff66;
    border-radius: 6px;
}

button, input.button {
    background: #e6733061;
    border: 1px solid #cd181800;
    padding: 5px 8px;
    color: #fff;
}
input.textbox {
    background: #00000078;
    border: 1px solid #2a2a2a00;
    padding: 5px;
}

select {
    background: #10101052;
    border: 1px solid #2a2a2a00;
    padding: 5px;
    font-size: 14px;
}


/* Reputation & Thread */

._plus {
    background-color: #1a1a1a00;
    color: #5ba8ff;
    border: 1px solid #5ba8ff00;
}
._minus {
    background-color: #1a1a1a00;
    color: #ff5b5b;
    border: 1px solid #5ba8ff00;
}

.thread_head {
    background: #00000061 !important;
    border-radius: 6px;
    border-bottom: 5px solid #ffffff94 !important;
}

.post .post_head {
    font-size: 11px;
    padding: 10px;
    border-top: 15px solid #ffffff00;
}
.post .post_author {
    border-bottom: 3px solid #f5864766;
    background: #58ecf31f;
    padding: 5px;
}
.post_controls {
    margin-top: 3px;
    background: #9e9e9e42;
    border-bottom: 1px solid #ffffffb3;
    padding: 5px;
}
.post .post_author div.author_statistics {
    float: right;
    font-size: 14px;
    background-color: transparent;
    color: #e6e6e6 !important;
    border-radius: 6px !important;
    line-height: 1.3;
}
.postlink {
    background-color: #20202000;
    border: solid thin #cd181800;
}
.postbit_buttons>a:link, .postbit_buttons>a:hover, .postbit_buttons>a:visited, .postbit_buttons>a:active {
    background: #00caff40;
    border: 1px solid #cd181800;
}
form #message {
    width: 500px;
    background-color: #00000045;
}

.buttons {
    background-color: #00ffd030;
    border: 1px solid #da2c2c00;
    border-radius: 3px;
}
.buttons:hover {
    background-color: #4ceaff66;
    border-radius: 6px;
    color: #fff;
}
button, input.button, input.textbox, input.invalid_field, input.valid_field, select, textarea, .editor_control_bar, blockquote, .codeblock, fieldset, .pm_alert, .red_alert, .popup_menu, .postbit_buttons>a, a.button {
    -moz-border-radius: 6px;
    -webkit-border-radius: 6px;
    border-radius: 6px;
    background-color: #00000045;
    border-color: #00000000;
}
blockquote cite span {
    float: right;
    font-weight: 400;
    font-size: 12px;
    /* color: #16beff; */
    color: #ffad16 !important;
}

/* Classic theme edits */
.post.classic .post_author {
    border: 1px solid #ddd0 !important;
    margin: 0 1% 15px 0;
    float: left;
}
.post .post_author div.author_avatar img {
    padding: 5px;
    border: 1px solid #2a2a2a00 !important;
    background: #1a1a1a00 !important;
}

/* Notification box */
.thead {
    background: linear-gradient(0deg, rgba(2,0,36,0) 0%, rgba(0,0,0,0) 0%, rgba(0,0,0,0.3) 0%, rgba(77,77,77,0.2) 25%, rgba(255,255,255,0.3) 80%);
}
td.tfoot.smalltext {
    background-color: #009292ad;
}
tbody.alert {
    background: #000000db;
}
.post .post_head span.post_date {
    color: #ffffff;
}

Style Attribute {
    margin-right: 5px;
    background-color: #20202000 !important;
    display: inline-block;
    min-width: 40px;
    border-radius: 3px;
    text-align: center;
    padding: 1px 5px 1px 5px;
}

/* Other Stuff x2 */

.spoiler_header {
    background: linear-gradient(0deg, rgba(2,0,36,0) 0%, rgba(77,77,77,0.4) 0%, rgba(77,77,77,0.35) 0%, rgba(77,77,77,0.35) 0%, rgba(0,0,0,0.35) 0%, rgba(0,0,0,0.35) 30%, rgba(255,255,255,0.35) 100%, rgba(74,74,74,0.2) 100%, rgba(0,0,0,0.3) 100%);
    border-top: 1px solid #0000003d;
    border-bottom: 1px solid #ffffff26;
}
.spoiler_body {
    padding: 4px;
    border: 1px solid #a0a0a000 !important;
    border-top: 0;
    color: #cccccc;
}

div[style*="background-color: #202020;"] {
    background-color: unset !important;
}
body {
    background-image: unset;
}

/* Like Counter */
.likebar .tooltiptext {
    text-align: center;
    padding: 5px 0;
    width: 120px;
    background-color: #3c120030;
    border-radius: 10px;
    margin-left: 5%;
    position: absolute;
    margin-top: 15px;
    z-index: 10;
}

/* Top alert */

button, input.button, input.textbox, input.invalid_field, input.valid_field, select, textarea, .editor_control_bar, blockquote, .codeblock, fieldset, .pm_alert, .red_alert, .popup_menu, .postbit_buttons>a, a.button {
    -moz-border-radius: 6px;
    -webkit-border-radius: 6px;
    color: #ffffffb8 !important;
    border-radius: 6px;
    background-color: #cecbcb6e !important;
    border-color: #50505000 !important;
}
.red_alert:hover {
    -moz-transition: background-color 0.5s ease-in;
    -o-transition: background-color 0.4s ease-in;
    -webkit-transition: background-color 0.3s ease-in;
    background-color: #73737373 !Important;
}

.red_alert a:link, .red_alert a:visited, .red_alert a:hover, .red_alert a:active {
    color: #000000 !important;
}

Style Attribute {
    color: #71cfe4;
}
span[style*="color: #CD1818;"] {
    color: #d2d2d2d9 !important;
}
.post .post_head span.edited_post a {
    color: #ffffffab;
}

/* Scroll bar */
::-webkit-scrollbar-thumb {
    background-color: #9a9a9a !important;
    border-radius: 10px !important;
    border: none;
}

body {
    background-image: unset;
}
}

@-moz-document url("https://v3rmillion.net/index.php") {
a:hover, a:active {
    color: #FFFFFF !important;
    text-decoration: none;
    -moz-transition: color 1s ease-in;
    -o-transition: color 1s ease-in;
    -webkit-transition: color 0.1s ease-in;
}
}

@-moz-document url-prefix("https://v3rmillion.net/usercp.php?action=") {
button, input.button, input.textbox, input.invalid_field, input.valid_field, select, textarea, .editor_control_bar, blockquote, .codeblock, fieldset, .pm_alert, .red_alert, .popup_menu, .postbit_buttons>a, a.button {
    -moz-border-radius: 6px;
    -webkit-border-radius: 6px;
    color: #ffffff !important;
    border-radius: 6px;
    background-color: #cecbcb42 !important;
    border-color: transparent !important;
}
}

@-moz-document url("https://v3rmillion.net/siterules.php") {
.wrap {
    margin: auto;
        background: linear-gradient(0deg, rgba(2,0,36,0) 0%, rgba(77,77,77,0.4) 0%, rgba(77,77,77,0.35) 0%, rgba(77,77,77,0.35) 0%, rgba(0,0,0,0.35) 0%, rgba(0,0,0,0.35) 30%, rgba(255,255,255,0.35) 100%, rgba(74,74,74,0.2) 100%, rgba(0,0,0,0.3) 100%);
    border: 1px #cd181800 solid;
    border-radius: 10px;
    padding: 40px;
}
.wrap a {
    transition: all .2s ease-in;
    color: #ffffff;
}
.wrap a:hover {
    color: #f19f80;
}

.sectiondiv {
    background-color: #0000004a !important;
    border-radius: 20px !important;
    border-left: none;
    border-left-width: 40px;
    color: #ffffffba !important;
}

.infoHolder {
    background-color: #9c9c9c1a !important;
    margin-bottom: 10px;
    padding-left: 10px;
    border-radius: 4px;
}
.moreinfo {
    margin-left: -10px;
    padding: 10px;
    background-color: #0000003b !important;
    color: #dedede !important;
}
.logo {
    margin: auto;
    max-width: 0%;
}
.footer {
    background-color: #cd181800 !important;
    border-top-left-radius: 10px;
    border-top-right-radius: 10px;
    padding: 5px;
}
}
